  metadata: Record<string, unknown>;
}

export interface EvaluationDetail {
  category: string;
  metric: string;
  value: number;
  weight: number;
  threshold: number;
  passed: boolean;
  details: string;
  evidence: unknown[];
}

export interface EvaluationFramework {
