    return report;
  }

  /**
   * Save validation report
   */
  async saveReport(report: string): Promise<void> {
    const reportFileName = `prisma_validation_${this.timestamp}.txt`;
    const reportPath = path.join(this.backupDir, reportFileName);

    try {
      await fs.writeFile(reportPath, report, 'utf8');
      console.log(`üìÑ Validation report saved: ${reportPath}`);
    } catch (error) {
      console.error(`‚ùå Failed to save report: ${error}`);
    }
  }

  /**
   * Execute complete database validation workflow
   */
  async execute(): Promise<DatabaseValidationResult> {
    console.log('üöÄ Starting Prisma database validation...');

    try {
      // Create backup
      const backupPath = await this.createBackup();

      // Validate database
      const result = await this.validateDatabase();
      result.backupPath = backupPath;

      // Generate and save report
      const report = this.generateReport(result);
      console.log(`\\n${  report}`);
      await this.saveReport(report);

      return result;
    } catch (error) {
      console.error('‚ùå Database validation workflow failed:', error);
      throw error;
    } finally {
      await this.prisma.$disconnect();
    }
  }
}

// Main execution function
async function main() {
  const validator = new PrismaDatabaseValidator();

  try {
    const result = await validator.execute();
    process.exit(result.isValid ? 0 : 1);
  } catch (error) {
    console.error('‚ùå Database validation failed:', error);
    process.exit(1);
  }
}

// Run if this file is executed directly
if (require.main === module) {
  main();
}

