'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';

interface OrchestratorStatus {
  isInitialized: boolean;
  activeOperations: number;
  completedOperations: number;
  config: any;
}

interface SystemHealthStatus {
  overall: string;
  components: any;
  metrics: any;
  insights: string[];
  recommendations: string[];
  lastChecked: Date;
}

interface OrchestratedResult {
  operationId: string;
  success: boolean;
  result?: any;
  insights?: string[];
  recommendations?: string[];
  confidence: number;
  processingTime: number;
  orchestratedBy: string;
  timestamp: Date;
}

export default function GLMOrchestratorDemo() {
  const [status, setStatus] = useState<OrchestratorStatus | null>(null);
  const [health, setHealth] = useState<SystemHealthStatus | null>(null);
  const [results, setResults] = useState<OrchestratedResult[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchStatus = async () => {
    try {
      const response = await fetch('/api/glm-orchestrator?action=status');
      const data = await response.json();
      if (data.success) {
        setStatus(data.data);
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to fetch orchestrator status');
    }
  };

  const fetchHealth = async () => {
    try {
      const response = await fetch('/api/glm-orchestrator?action=health');
      const data = await response.json();
      if (data.success) {
        setHealth(data.data);
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to fetch health status');
    }
  };

  const runAnalysis = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/glm-orchestrator?action=test-analysis');
      const data = await response.json();
      if (data.success) {
        setResults(prev => [...prev, data.data]);
        await fetchStatus();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to run analysis');
    } finally {
      setLoading(false);
    }
  };

  const runOptimization = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/glm-orchestrator?action=test-optimization');
      const data = await response.json();
      if (data.success) {
        setResults(prev => [...prev, data.data]);
        await fetchStatus();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to run optimization');
    } finally {
      setLoading(false);
    }
  };

  const runPrediction = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/glm-orchestrator?action=test-prediction');
      const data = await response.json();
      if (data.success) {
        setResults(prev => [...prev, data.data]);
        await fetchStatus();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to run prediction');
    } finally {
      setLoading(false);
    }
  };

  const runComprehensiveTest = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/glm-orchestrator', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'comprehensive-test',
        }),
      });
      const data = await response.json();
      if (data.success) {
        setResults(prev => [...prev, ...data.data.orchestratedOperations]);
        await fetchStatus();
        await fetchHealth();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to run comprehensive test');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStatus();
    fetchHealth();
  }, []);

  const getHealthColor = (status: string) => {
    switch (status) {
      case 'excellent':
      case 'healthy':
        return 'bg-green-500';
      case 'good':
      case 'degraded':
        return 'bg-yellow-500';
      case 'fair':
        return 'bg-orange-500';
      case 'poor':
      case 'unhealthy':
        return 'bg-red-500';
      case 'critical':
        return 'bg-red-700';
      default:
        return 'bg-gray-500';
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="text-center space-y-2">
        <h1 className="text-4xl font-bold">GLM-4.5 Orchestrator Demo</h1>
        <p className="text-xl text-muted-foreground">
          Demonstrating GLM SDK as the Primary Orchestrator for OptiMind AI Ecosystem
        </p>
      </div>

      {error && (
        <Alert>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <Tabs defaultValue="overview" className="space-y-4">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="health">System Health</TabsTrigger>
          <TabsTrigger value="operations">Operations</TabsTrigger>
