interface ScanResult {
  scanId: string;
  initiatedBy: 'automatic' | 'manual' | 'error-triggered';
  startTime: string;
  endTime?: string;
  status: 'running' | 'completed' | 'failed';
  checkpoints: ScanCheckpoint[];
  issues: ScanIssue[];
  fixesApplied: number;
  score: number;
  grade: 'A+' | 'A' | 'B' | 'C' | 'D' | 'F';
  summary: {
    totalChecks: number;
    passedChecks: number;
    failedChecks: number;
    fixedIssues: number;
    remainingIssues: number;
  };
  recommendations: string[];
}

interface ScanCheckpoint {
  id: string;
  name: string;
  category: 'performance' | 'security' | 'functionality' | 'data' | 'integration';
  status: 'pending' | 'running' | 'passed' | 'failed' | 'fixed';
  priority: 'critical' | 'high' | 'medium' | 'low';
  description: string;
  result?: any;
  error?: string;
  fixApplied?: boolean;
  timestamp: string;
}

interface ScanIssue {
  id: string;
  type: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
  component: string;
  description: string;
  detectedAt: string;
  rootCause?: string;
  suggestedFix?: string;
  autoFixable: boolean;
  fixApplied?: boolean;
  fixTimestamp?: string;
}

interface ScannerStatus {
  activeScans: number;
  scanHistory: number;
  latestScan: ScanResult | null;
  activeScanDetails: ScanResult[];
}

export default function PremiumScannerDemo() {
  const [status, setStatus] = useState<ScannerStatus | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [scanHistory, setScanHistory] = useState<ScanResult[]>([]);

  const fetchStatus = async () => {
    try {
      const response = await fetch('/api/premium-scanner?action=status');
      const data = await response.json();
      if (data.success) {
        setStatus(data.data);
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to fetch scanner status');
    }
  };

  const fetchHistory = async () => {
    try {
      const response = await fetch('/api/premium-scanner?action=history');
      const data = await response.json();
      if (data.success) {
        setScanHistory(data.data.scans);
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to fetch scan history');
    }
  };

  const triggerScan = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/premium-scanner?action=trigger-scan');
      const data = await response.json();
      if (data.success) {
        await fetchStatus();
        await fetchHistory();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to trigger scan');
    } finally {
      setLoading(false);
    }
  };

  const runComprehensiveScan = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/premium-scanner', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'comprehensive-scan',
        }),
      });
      const data = await response.json();
      if (data.success) {
        await fetchStatus();
        await fetchHistory();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to run comprehensive scan');
    } finally {
      setLoading(false);
    }
  };

  const reportIssue = async () => {
    setLoading(true);
    setError(null);
    try {
      const mockIssue = {
        type: 'performance-degradation',
        severity: 'high',
        component: 'system',
        description: 'System performance degradation detected',
        detectedAt: new Date().toISOString(),
        autoFixable: true,
        fixApplied: false,
      };

      const response = await fetch('/api/premium-scanner', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'report-issue',
          issue: mockIssue,
        }),
      });
      const data = await response.json();
      if (data.success) {
        await fetchStatus();
        await fetchHistory();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to report issue');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStatus();
    fetchHistory();

    // Set up periodic status updates
    const interval = setInterval(() => {
      fetchStatus();
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const getGradeColor = (grade: string) => {
    switch (grade) {
      case 'A+':
        return 'bg-green-500 text-white';
      case 'A':
        return 'bg-green-400 text-white';
      case 'B':
        return 'bg-blue-400 text-white';
      case 'C':
        return 'bg-yellow-400 text-white';
      case 'D':
        return 'bg-orange-400 text-white';
      case 'F':
        return 'bg-red-500 text-white';
      default:
        return 'bg-gray-400 text-white';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-500';
      case 'running':
        return 'bg-blue-500';
      case 'failed':
        return 'bg-red-500';
      default:
        return 'bg-gray-500';
    }
  };

  const getCheckpointStatusColor = (status: string) => {
    switch (status) {
      case 'passed':
        return 'bg-green-500';
      case 'failed':
        return 'bg-red-500';
      case 'running':
        return 'bg-blue-500';
      case 'fixed':
        return 'bg-yellow-500';
      default:
        return 'bg-gray-500';
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical':
        return 'bg-red-600 text-white';
      case 'high':
        return 'bg-orange-500 text-white';
      case 'medium':
        return 'bg-yellow-500 text-white';
      case 'low':
        return 'bg-blue-500 text-white';
      default:
        return 'bg-gray-500 text-white';
    }
  };

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="text-center space-y-2">
        <h1 className="text-4xl font-bold">OptiMind AI Premium Diamond-Grade Scanner</h1>
        <p className="text-xl text-muted-foreground">
          Advanced Self-Healing System with Automatic Issue Detection and Resolution
        </p>
      </div>

      {error && (
        <Alert>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <Tabs defaultValue="overview" className="space-y-4">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="scanner">Scanner Status</TabsTrigger>
          <TabsTrigger value="history">Scan History</TabsTrigger>
          <TabsTrigger value="results">Detailed Results</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Active Scans</CardTitle>
                <div className="h-3 w-3 rounded-full bg-blue-500" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{status?.activeScans || 0}</div>
                <p className="text-xs text-muted-foreground">Currently running</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Scan History</CardTitle>
                <div className="h-3 w-3 rounded-full bg-green-500" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{status?.scanHistory || 0}</div>
                <p className="text-xs text-muted-foreground">Total scans completed</p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Latest Grade</CardTitle>
                <div
                  className={`h-3 w-3 rounded-full ${status?.latestScan ? getGradeColor(status.latestScan.grade) : 'bg-gray-500'}`}
                />
              </CardHeader>
              <CardContent>
                <div
                  className={`text-2xl font-bold ${status?.latestScan ? getGradeColor(status.latestScan.grade) : 'text-gray-500'}`}
