                            {scan.summary.remainingIssues}
                          </span>
                        </div>
                      </div>
                    </div>

                    {scan.recommendations.length > 0 && (
                      <div>
                        <h4 className="font-medium mb-2">Recommendations</h4>
                        <ul className="list-disc list-inside text-sm space-y-1">
