'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';

interface HealingReport {
  healingId: string;
  timestamp: string;
  initialIssues: number;
  issuesDetected: number;
  issuesFixed: number;
  issuesRemaining: number;
  scanScore: number;
  scanGrade: string;
  healingTime: number;
  success: boolean;
  summary: string;
}

interface SystemStatus {
  selfHealing: {
    isInitialized: boolean;
    config: any;
  };
  scanner: any;
  errorHandler: any;
  healingHistory: {
    total: number;
    successful: number;
    averageScore: number;
  };
}

export default function SelfHealingDemo() {
  const [systemStatus, setSystemStatus] = useState<SystemStatus | null>(null);
  const [healingHistory, setHealingHistory] = useState<HealingReport[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchStatus = async () => {
    try {
      const response = await fetch('/api/self-healing?action=status');
      const data = await response.json();
      if (data.success) {
        setSystemStatus(data.data);
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to fetch system status');
    }
  };

  const fetchHistory = async () => {
    try {
      const response = await fetch('/api/self-healing?action=history');
      const data = await response.json();
      if (data.success) {
        setHealingHistory(data.data.healingReports);
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to fetch healing history');
    }
  };

  const triggerHealing = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/self-healing', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'trigger-healing',
        }),
      });
      const data = await response.json();
      if (data.success) {
        await fetchStatus();
        await fetchHistory();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to trigger healing');
    } finally {
      setLoading(false);
    }
  };

  const handleSystemError = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/self-healing', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'handle-error',
          error: {
            message: 'Simulated critical system error',
            context: {
              component: 'system-core',
              operation: 'critical-process',
              severity: 'critical',
            },
          },
        }),
      });
      const data = await response.json();
      if (data.success) {
        await fetchStatus();
        await fetchHistory();
      } else {
        setError(data.error);
      }
    } catch (err) {
      setError('Failed to handle system error');
    } finally {
      setLoading(false);
    }
  };

  const monitorHealth = async () => {
    setLoading(true);
