'use client';

import { useState, useEffect } from 'react';
import {
  Brain,
  Network,
  Cpu,
  CheckCircle,
  AlertTriangle,
  RefreshCw,
  Target,
  Zap,
  Shield,
  Crown,
  Lightbulb,
} from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { multiModelAIUtils } from '@/lib/multi-model-ai';

interface ModelStatus {
  id: string;
  name: string;
  version: string;
  provider: string;
  isAvailable: boolean;
  capabilities: string[];
  status: string;
}

interface TestResult {
  modelId: string;
  modelName: string;
  testPrompt: string;
  response: string;
  confidence: number;
  processingTime: number;
  capabilities: string[];
  timestamp: string;
  status: string;
}

export default function TestModelsPage() {
  const [loading, setLoading] = useState(false);
  const [testing, setTesting] = useState(false);
  const [modelStatuses, setModelStatuses] = useState<ModelStatus[]>([]);
  const [testResults, setTestResults] = useState<TestResult[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [availableModels, setAvailableModels] = useState<any[]>([]);

  useEffect(() => {
    const loadModels = async () => {
      try {
        const models = await multiModelAIUtils.getAvailableModels(
          multiModelAIUtils.DEFAULT_OPTIONS
        );
        setAvailableModels(models);
      } catch (err) {
        console.error('Failed to load models:', err);
      }
    };
    loadModels();
  }, []);

  const getModelIcon = (modelId: string) => {
    switch (modelId) {
      case 'glm-45v':
        return <Target className="w-5 h-5" />;
      case 'glm-45-auto-think':
        return <Lightbulb className="w-5 h-5" />;
      case 'glm-45-flagship':
        return <Crown className="w-5 h-5" />;
      case 'air':
        return <Cpu className="w-5 h-5" />;
      case 'glm-45-full-stack':
        return <Network className="w-5 h-5" />;
      default:
        return <Brain className="w-5 h-5" />;
    }
  };

  const getModelColor = (modelId: string) => {
    switch (modelId) {
      case 'glm-45v':
        return 'text-blue-600';
      case 'glm-45-auto-think':
        return 'text-orange-600';
      case 'glm-45-flagship':
        return 'text-yellow-600';
      case 'air':
        return 'text-purple-600';
      case 'glm-45-full-stack':
        return 'text-green-600';
      default:
        return 'text-gray-600';
    }
  };

  const checkModelStatus = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/test-models');
      const data = await response.json();

      if (data.success) {
        setModelStatuses(data.models);
      } else {
        setError(data.message || 'Failed to check model status');
      }
    } catch (error) {
      setError('Network error while checking model status');
      console.error('Model status check failed:', error);
    } finally {
      setLoading(false);
    }
  };

  const testModel = async (modelId: string) => {
    setTesting(true);
    setError(null);

    try {
      const response = await fetch('/api/test-models', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          modelId,
          testPrompt: 'Test advanced AI capabilities for image analysis and security scanning',
        }),
      });

      const data = await response.json();

      if (data.success) {
        setTestResults(prev => {
          const filtered = prev.filter(r => r.modelId !== modelId);
          return [...filtered, data.result];
        });
      } else {
        setError(data.message || 'Failed to test model');
      }
    } catch (error) {
      setError('Network error while testing model');
      console.error('Model test failed:', error);
    } finally {
      setTesting(false);
    }
  };

  const testAllModels = async () => {
    setTesting(true);
    setError(null);

    const promises = availableModels.map(model => testModel(model.id));
    await Promise.all(promises);
    setTesting(false);
  };

  useEffect(() => {
    checkModelStatus();
  }, []);

  return (
    <div className="min-h-screen bg-background p-6">
      <div className="max-w-6xl mx-auto space-y-6">
        {/* Header */}
        <div className="text-center space-y-2">
          <div className="flex items-center justify-center gap-2">
            <Network className="w-8 h-8 text-purple-600" />
            <h1 className="text-3xl font-bold">Multi-Model AI Test Center</h1>
          </div>
          <p className="text-muted-foreground">
            Test and validate GLM-4.5V, GLM-4.5 Auto Think, GLM-4.5 Flagship, AIR, and Base AI
            models
          </p>
        </div>

        {/* Controls */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Shield className="w-5 h-5" />
              System Controls
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex flex-wrap gap-4">
