interface FamilyMember {
  id: string;
  name: string;
  age: number;
  role: 'parent' | 'child' | 'teenager';
  avatar: string;
  isOnline: boolean;
  lastActive: Date;
  safetyScore: number;
  deviceCount: number;
  restrictions: UserRestrictions;
}

interface UserRestrictions {
  contentFiltering: boolean;
  screenTime: {
    enabled: boolean;
    dailyLimit: number; // minutes
    bedtimeStart: string;
    bedtimeEnd: string;
    allowedDays: number[];
  };
  appBlocking: string[];
  webFiltering: {
    enabled: boolean;
    blockedCategories: string[];
    customBlockedSites: string[];
  };
  locationTracking: boolean;
  socialMedia: {
    enabled: boolean;
    timeLimit: number;
    blockedPlatforms: string[];
  };
  messaging: {
    enabled: boolean;
    contactsOnly: boolean;
    blockedContacts: string[];
  };
  purchases: {
    enabled: boolean;
    requireApproval: boolean;
    monthlyLimit: number;
  };
}

interface SafetyAlert {
  id: string;
  type: 'content_warning' | 'screen_time' | 'location' | 'app_usage' | 'web_filter';
  severity: 'low' | 'medium' | 'high' | 'critical';
  title: string;
  description: string;
  userId: string;
  timestamp: Date;
  resolved: boolean;
  actionTaken?: string;
}

interface ActivityReport {
  userId: string;
  period: 'daily' | 'weekly' | 'monthly';
  screenTime: number;
  appsUsed: string[];
  websitesVisited: string[];
  contentFiltered: number;
  alertsTriggered: number;
  safetyScore: number;
  generatedAt: Date;
}

interface FamilySettings {
  familyName: string;
  safetyLevel: 'strict' | 'moderate' | 'relaxed';
  emergencyContacts: string[];
  locationSharing: boolean;
  contentFiltering: boolean;
  screenTimeLimits: boolean;
