'use client';

import { useState } from 'react';
import {
  Search,
  Filter,
  X,
  Calendar,
  Tag,
  User,
  Shield,
  AlertTriangle,
  CheckCircle,
  Clock,
  Download,
  RefreshCw,
} from 'lucide-react';

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from '@/components/ui/accordion';

export interface FilterState {
  search: string;
  status: string[];
  categories: string[];
  dateRange: {
    start: string;
    end: string;
  };
  confidence: {
    min: number;
    max: number;
  };
  models: string[];
  actions: string[];
  reviewers: string[];
}

interface ModerationFilterPanelProps {
  onFilterChange: (filters: FilterState) => void;
  onExport: () => void;
  onReset: () => void;
  totalResults: number;
  filteredResults: number;
}

export function ModerationFilterPanel({
  onFilterChange,
  onExport,
  onReset,
  totalResults,
  filteredResults,
}: ModerationFilterPanelProps) {
  const [filters, setFilters] = useState<FilterState>({
    search: '',
    status: [],
    categories: [],
    dateRange: {
      start: '',
      end: '',
    },
    confidence: {
      min: 0,
      max: 100,
    },
    models: [],
    actions: [],
    reviewers: [],
  });

  const [isExpanded, setIsExpanded] = useState(false);

  const statusOptions = [
    { value: 'safe', label: 'Safe', color: 'bg-green-100 text-green-800' },
    { value: 'flagged', label: 'Flagged', color: 'bg-red-100 text-red-800' },
    {
      value: 'pending',
      label: 'Pending Review',
      color: 'bg-yellow-100 text-yellow-800',
    },
    {
      value: 'quarantined',
      label: 'Quarantined',
      color: 'bg-orange-100 text-orange-800',
    },
  ];

  const categoryOptions = [
    'sexual_nudity',
    'child_exposed',
    'violence',
    'hate_speech',
    'deepfake_suspected',
    'harassment',
    'spam',
    'copyright',
    'misinformation',
    'self_harm',
  ];

  const modelOptions = ['GLM-4.5V', 'GLM-4.5-AIR', 'GLM-4.5', 'Consensus'];

  const actionOptions = ['allow', 'monitor', 'quarantine', 'hold_for_review'];

  const updateFilter = (key: keyof FilterState, value: any) => {
    const newFilters = { ...filters, [key]: value };
    setFilters(newFilters);
    onFilterChange(newFilters);
  };

  const toggleArrayFilter = (key: keyof FilterState, value: string) => {
    const currentArray = filters[key] as string[];
    const newArray = currentArray.includes(value)
      ? currentArray.filter(item => item !== value)
      : [...currentArray, value];
    updateFilter(key, newArray);
  };

  const hasActiveFilters = () => {
    return (
      filters.search !== '' ||
      filters.status.length > 0 ||
      filters.categories.length > 0 ||
      filters.dateRange.start !== '' ||
      filters.dateRange.end !== '' ||
      filters.models.length > 0 ||
      filters.actions.length > 0 ||
      filters.reviewers.length > 0
    );
  };

  const getActiveFiltersCount = () => {
    let count = 0;
    if (filters.search) count++;
    count += filters.status.length;
    count += filters.categories.length;
    if (filters.dateRange.start) count++;
    if (filters.dateRange.end) count++;
    count += filters.models.length;
    count += filters.actions.length;
    count += filters.reviewers.length;
    return count;
  };

  return (
    <div className="space-y-4">
      {/* Quick Search Bar */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center gap-2">
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
              <Input
                placeholder="Search by image ID, filename, or reviewer..."
                value={filters.search}
                onChange={e => updateFilter('search', e.target.value)}
                className="pl-10"
              />
            </div>
            <Button
              variant="outline"
              onClick={() => setIsExpanded(!isExpanded)}
              className="flex items-center gap-2"
            >
              <Filter className="h-4 w-4" />
              Filters
              {getActiveFiltersCount() > 0 && (
                <Badge variant="secondary" className="ml-1">
                  {getActiveFiltersCount()}
                </Badge>
              )}
            </Button>
            <Button variant="outline" onClick={onExport}>
              <Download className="h-4 w-4 mr-2" />
              Export
            </Button>
            <Button variant="outline" onClick={onReset}>
              <RefreshCw className="h-4 w-4 mr-2" />
              Reset
            </Button>
          </div>

          {/* Active Filters Display */}
          {hasActiveFilters() && (
            <div className="mt-3 flex flex-wrap gap-2">
              {filters.search && (
                <Badge variant="outline" className="flex items-center gap-1">
                  Search: {filters.search}
                  <X
                    className="h-3 w-3 cursor-pointer"
                    onClick={() => updateFilter('search', '')}
                  />
                </Badge>
              )}
              {filters.status.map(status => (
                <Badge key={status} variant="outline" className="flex items-center gap-1">
                  Status: {status}
                  <X
                    className="h-3 w-3 cursor-pointer"
                    onClick={() => toggleArrayFilter('status', status)}
                  />
                </Badge>
              ))}
              {filters.categories.map(category => (
                <Badge key={category} variant="outline" className="flex items-center gap-1">
                  Category: {category}
                  <X
                    className="h-3 w-3 cursor-pointer"
                    onClick={() => toggleArrayFilter('categories', category)}
                  />
                </Badge>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Advanced Filters */}
      {isExpanded && (
        <Card>
          <CardHeader>
            <CardTitle>Advanced Filters</CardTitle>
            <CardDescription>Filter moderation results by various criteria</CardDescription>
          </CardHeader>
          <CardContent>
            <Accordion
              type="multiple"
              defaultValue={['status', 'categories']}
              className="space-y-4"
            >
              {/* Status Filter */}
              <AccordionItem value="status">
                <AccordionTrigger className="text-sm font-medium">Status</AccordionTrigger>
                <AccordionContent>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
                    {statusOptions.map(option => (
                      <label
                        key={option.value}
                        className="flex items-center space-x-2 cursor-pointer"
                      >
                        <Checkbox
                          checked={filters.status.includes(option.value)}
                          onCheckedChange={() => toggleArrayFilter('status', option.value)}
                        />
                        <Badge variant="outline" className={option.color}>
                          {option.label}
                        </Badge>
                      </label>
                    ))}
                  </div>
                </AccordionContent>
              </AccordionItem>

              {/* Categories Filter */}
              <AccordionItem value="categories">
                <AccordionTrigger className="text-sm font-medium">
                  Content Categories
                </AccordionTrigger>
                <AccordionContent>
                  <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-3">
                    {categoryOptions.map(category => (
                      <label key={category} className="flex items-center space-x-2 cursor-pointer">
                        <Checkbox
                          checked={filters.categories.includes(category)}
                          onCheckedChange={() => toggleArrayFilter('categories', category)}
                        />
                        <span className="text-sm">{category.replace('_', ' ')}</span>
                      </label>
                    ))}
                  </div>
                </AccordionContent>
              </AccordionItem>

              {/* Date Range Filter */}
              <AccordionItem value="date">
                <AccordionTrigger className="text-sm font-medium">Date Range</AccordionTrigger>
                <AccordionContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="text-sm font-medium">Start Date</label>
                      <Input
                        type="date"
                        value={filters.dateRange.start}
                        onChange={e =>
                          updateFilter('dateRange', {
                            ...filters.dateRange,
                            start: e.target.value,
                          })
                        }
                      />
                    </div>
                    <div>
                      <label className="text-sm font-medium">End Date</label>
                      <Input
                        type="date"
                        value={filters.dateRange.end}
                        onChange={e =>
                          updateFilter('dateRange', {
                            ...filters.dateRange,
                            end: e.target.value,
                          })
                        }
                      />
                    </div>
                  </div>
                </AccordionContent>
              </AccordionItem>

              {/* Models Filter */}
              <AccordionItem value="models">
                <AccordionTrigger className="text-sm font-medium">AI Models</AccordionTrigger>
                <AccordionContent>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
                    {modelOptions.map(model => (
                      <label key={model} className="flex items-center space-x-2 cursor-pointer">
                        <Checkbox
                          checked={filters.models.includes(model)}
                          onCheckedChange={() => toggleArrayFilter('models', model)}
                        />
                        <span className="text-sm">{model}</span>
                      </label>
                    ))}
                  </div>
                </AccordionContent>
              </AccordionItem>

              {/* Actions Filter */}
              <AccordionItem value="actions">
                <AccordionTrigger className="text-sm font-medium">
                  Recommended Actions
                </AccordionTrigger>
                <AccordionContent>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
                    {actionOptions.map(action => (
                      <label key={action} className="flex items-center space-x-2 cursor-pointer">
                        <Checkbox
                          checked={filters.actions.includes(action)}
                          onCheckedChange={() => toggleArrayFilter('actions', action)}
                        />
                        <span className="text-sm capitalize">{action.replace('_', ' ')}</span>
                      </label>
                    ))}
                  </div>
                </AccordionContent>
              </AccordionItem>
            </Accordion>
          </CardContent>
        </Card>
      )}

      {/* Results Summary */}
      <Card>
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div>
                <span className="text-sm text-gray-600">Showing </span>
                <span className="font-semibold">{filteredResults.toLocaleString()}</span>
                <span className="text-sm text-gray-600"> of </span>
                <span className="font-semibold">{totalResults.toLocaleString()}</span>
                <span className="text-sm text-gray-600"> results</span>
              </div>
              {hasActiveFilters() && (
                <Badge variant="outline">
                  {getActiveFiltersCount()} active filter
                  {getActiveFiltersCount() !== 1 ? 's' : ''}
                </Badge>
              )}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useState, useMemo } from 'react';
import {
  Eye,
  Download,
  MoreHorizontal,
  Shield,
  AlertTriangle,
  CheckCircle,
  Clock,
  User,
  Calendar,
  BarChart3,
  Filter,
  ChevronLeft,
  ChevronRight,
} from 'lucide-react';

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Progress } from '@/components/ui/progress';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';

interface ModerationResult {
  id: string;
  imageId: string;
  filename: string;
  status: 'safe' | 'flagged' | 'pending' | 'quarantined';
  category: string;
  confidence: number;
  recommendedAction: string;
  modelsUsed: string[];
  reviewer?: string;
  reviewDate?: string;
  createdAt: string;
  processingTime: number;
  thumbnail?: string;
  reasons: string[];
}

interface ModerationResultsTableProps {
  results: ModerationResult[];
  onViewDetails: (result: ModerationResult) => void;
  onExport: (results: ModerationResult[]) => void;
  loading?: boolean;
}

export function ModerationResultsTable({
  results,
  onViewDetails,
  onExport,
  loading = false,
}: ModerationResultsTableProps) {
  const [currentPage, setCurrentPage] = useState(1);
  const [sortField, setSortField] = useState<keyof ModerationResult>('createdAt');
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('desc');
  const [selectedResults, setSelectedResults] = useState<Set<string>>(new Set());

  const itemsPerPage = 20;

  // Sort and paginate results
  const sortedAndPaginatedResults = useMemo(() => {
    const sorted = [...results].sort((a, b) => {
      let aValue = a[sortField];
      let bValue = b[sortField];

      if (sortField === 'createdAt') {
        aValue = new Date(aValue as string).getTime();
        bValue = new Date(bValue as string).getTime();
      }

      if (sortDirection === 'asc') {
        return aValue > bValue ? 1 : -1;
      } else {
        return aValue < bValue ? 1 : -1;
      }
    });

    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;

    return sorted.slice(startIndex, endIndex);
  }, [results, sortField, sortDirection, currentPage]);

  const totalPages = Math.ceil(results.length / itemsPerPage);

  const handleSort = (field: keyof ModerationResult) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('desc');
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'safe':
        return (
          <Badge className="bg-green-100 text-green-800">
            <CheckCircle className="h-3 w-3 mr-1" />
            Safe
          </Badge>
        );
      case 'flagged':
        return (
          <Badge className="bg-red-100 text-red-800">
            <AlertTriangle className="h-3 w-3 mr-1" />
            Flagged
          </Badge>
        );
      case 'pending':
        return (
          <Badge className="bg-yellow-100 text-yellow-800">
            <Clock className="h-3 w-3 mr-1" />
            Pending
          </Badge>
        );
      case 'quarantined':
        return (
          <Badge className="bg-orange-100 text-orange-800">
            <Shield className="h-3 w-3 mr-1" />
            Quarantined
          </Badge>
        );
      default:
        return <Badge>{status}</Badge>;
    }
  };

  const getActionBadge = (action: string) => {
    const colors: Record<string, string> = {
      allow: 'bg-green-100 text-green-800',
      monitor: 'bg-blue-100 text-blue-800',
      quarantine: 'bg-red-100 text-red-800',
      hold_for_review: 'bg-yellow-100 text-yellow-800',
    };

    return (
      <Badge className={colors[action] || 'bg-gray-100 text-gray-800'}>
        {action.replace('_', ' ')}
      </Badge>
    );
  };

  const toggleResultSelection = (id: string) => {
    const newSelected = new Set(selectedResults);
    if (newSelected.has(id)) {
      newSelected.delete(id);
    } else {
      newSelected.add(id);
    }
    setSelectedResults(newSelected);
  };

  const exportSelected = () => {
    const selectedData = results.filter(r => selectedResults.has(r.id));
    onExport(selectedData);
  };

  if (loading) {
    return (
      <Card>
        <CardContent className="p-8">
          <div className="space-y-4">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="animate-pulse">
                <div className="h-4 bg-gray-200 rounded w-full mb-2"></div>
                <div className="h-4 bg-gray-200 rounded w-3/4"></div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      {/* Results Header */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Moderation Results</CardTitle>
              <CardDescription>{results.length} total results found</CardDescription>
            </div>
            <div className="flex items-center gap-2">
              {selectedResults.size > 0 && (
                <Button variant="outline" onClick={exportSelected}>
                  <Download className="h-4 w-4 mr-2" />
                  Export Selected ({selectedResults.size})
                </Button>
              )}
              <Button variant="outline" onClick={() => onExport(results)}>
                <Download className="h-4 w-4 mr-2" />
                Export All
              </Button>
            </div>
          </div>
        </CardHeader>
      </Card>

      {/* Results Table */}
      <Card>
        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead className="w-12">
                    <input
                      type="checkbox"
                      checked={
                        selectedResults.size === sortedAndPaginatedResults.length &&
                        sortedAndPaginatedResults.length > 0
                      }
                      onChange={e => {
                        if (e.target.checked) {
                          setSelectedResults(new Set(sortedAndPaginatedResults.map(r => r.id)));
                        } else {
                          setSelectedResults(new Set());
                        }
                      }}
                    />
                  </TableHead>
                  <TableHead
                    className="cursor-pointer hover:bg-gray-50"
                    onClick={() => handleSort('imageId')}
                  >
                    <div className="flex items-center gap-1">
                      Image ID
                      {sortField === 'imageId' && (
                        <span>{sortDirection === 'asc' ? '‚Üë' : '‚Üì'}</span>
                      )}
                    </div>
                  </TableHead>
                  <TableHead>Filename</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Category</TableHead>
                  <TableHead
                    className="cursor-pointer hover:bg-gray-50"
                    onClick={() => handleSort('confidence')}
                  >
                    <div className="flex items-center gap-1">
                      Confidence
                      {sortField === 'confidence' && (
                        <span>{sortDirection === 'asc' ? '‚Üë' : '‚Üì'}</span>
                      )}
                    </div>
                  </TableHead>
                  <TableHead>Action</TableHead>
                  <TableHead>Models</TableHead>
                  <TableHead
                    className="cursor-pointer hover:bg-gray-50"
                    onClick={() => handleSort('createdAt')}
                  >
                    <div className="flex items-center gap-1">
                      Date
                      {sortField === 'createdAt' && (
                        <span>{sortDirection === 'asc' ? '‚Üë' : '‚Üì'}</span>
                      )}
                    </div>
                  </TableHead>
                  <TableHead className="w-12"></TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {sortedAndPaginatedResults.map(result => (
                  <TableRow key={result.id} className="hover:bg-gray-50">
                    <TableCell>
                      <input
                        type="checkbox"
                        checked={selectedResults.has(result.id)}
                        onChange={() => toggleResultSelection(result.id)}
                      />
                    </TableCell>
                    <TableCell className="font-mono text-sm">{result.imageId}</TableCell>
                    <TableCell className="max-w-xs truncate">{result.filename}</TableCell>
                    <TableCell>{getStatusBadge(result.status)}</TableCell>
                    <TableCell>
                      <Badge variant="outline">{result.category.replace('_', ' ')}</Badge>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-2">
                        <Progress value={result.confidence * 100} className="w-16" />
                        <span className="text-sm">{(result.confidence * 100).toFixed(1)}%</span>
                      </div>
                    </TableCell>
                    <TableCell>{getActionBadge(result.recommendedAction)}</TableCell>
                    <TableCell>
                      <div className="flex flex-wrap gap-1">
                        {result.modelsUsed.slice(0, 2).map((model, index) => (
                          <Badge key={index} variant="outline" className="text-xs">
                            {model}
                          </Badge>
                        ))}
                        {result.modelsUsed.length > 2 && (
                          <Badge variant="outline" className="text-xs">
                            +{result.modelsUsed.length - 2}
                          </Badge>
                        )}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="text-sm">
                        <div>{new Date(result.createdAt).toLocaleDateString()}</div>
                        <div className="text-gray-500">
                          {new Date(result.createdAt).toLocaleTimeString()}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <Button variant="ghost" size="sm" onClick={() => onViewDetails(result)}>
                        <Eye className="h-4 w-4" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      {/* Pagination */}
      {totalPages > 1 && (
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div className="text-sm text-gray-600">
                Showing {(currentPage - 1) * itemsPerPage + 1} to{' '}
                {Math.min(currentPage * itemsPerPage, results.length)} of {results.length} results
              </div>
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                  disabled={currentPage === 1}
                >
                  <ChevronLeft className="h-4 w-4" />
                </Button>
                <div className="flex items-center gap-1">
                  {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                    let pageNum;
                    if (totalPages <= 5) {
                      pageNum = i + 1;
                    } else if (currentPage <= 3) {
                      pageNum = i + 1;
                    } else if (currentPage >= totalPages - 2) {
                      pageNum = totalPages - 4 + i;
                    } else {
                      pageNum = currentPage - 2 + i;
                    }

                    return (
                      <Button
                        key={pageNum}
                        variant={currentPage === pageNum ? 'default' : 'outline'}
                        size="sm"
                        onClick={() => setCurrentPage(pageNum)}
                        className="w-8 h-8"
                      >
                        {pageNum}
                      </Button>
                    );
                  })}
                </div>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setCurrentPage(prev => Math.min(totalPages, prev + 1))}
                  disabled={currentPage === totalPages}
                >
                  <ChevronRight className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

