                        </Select>
                      </div>

                      <div className="space-y-2">
                        <Label className="text-sm font-medium">Auto Process</Label>
                        <div className="flex items-center space-x-2">
                          <Switch
                            id="auto-process"
                            checked={autoProcess}
                            onCheckedChange={setAutoProcess}
                          />
                          <Label htmlFor="auto-process" className="text-xs">
                            Process new uploads automatically
                          </Label>
                        </div>
                      </div>

                      <div className="space-y-2">
                        <Label className="text-sm font-medium">Estimated Cost</Label>
                        <div className="text-lg font-semibold text-purple-600">
                          {totalCost} credits
                        </div>
                      </div>
                    </div>

                    {/* Progress */}
                    {(isProcessing || isServiceProcessing) && (
                      <div className="space-y-2">
                        <div className="flex items-center justify-between text-sm">
                          <span>Processing...</span>
                          <span>{Math.round(currentProgress)}%</span>
                        </div>
                        <Progress value={currentProgress} className="h-2" />
                      </div>
                    )}

                    {/* Action Buttons */}
                    <div className="flex gap-3">
                      <Button
                        onClick={
                          processingMode === 'single' ? handleProcessSingle : handleProcessBatch
                        }
                        disabled={
                          selectedPhotos.length === 0 || isProcessing || isServiceProcessing
                        }
                        className="flex-1"
                      >
                        {isProcessing || isServiceProcessing ? (
                          <>
                            <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                            Processing...
                          </>
                        ) : (
                          <>
                            <Play className="w-4 h-4 mr-2" />
                            {processingMode === 'single'
                              ? 'Process Photo'
                              : `Process ${selectedPhotos.length} Photos`}
                          </>
                        )}
                      </Button>

                      <Button
                        variant="outline"
                        onClick={() => setShowResults(!showResults)}
                        disabled={results.size === 0}
                      >
                        <BarChart3 className="w-4 h-4 mr-2" />
                        {showResults ? 'Hide Results' : 'Show Results'}
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Results Display */}
            {showResults && results.size > 0 && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <CheckCircle className="w-5 h-5 text-green-600" />
                    Processing Results
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {Array.from(results.entries()).map(([photoId, result]) => {
                      const photo = photos.find(p => p.id === photoId);
                      return (
                        <div
                          key={photoId}
                          className="flex items-start gap-4 p-4 bg-muted/30 rounded-lg"
                        >
                          {photo && (
                            <img
                              src={photo.url}
                              alt={photo.file.name}
                              className="w-16 h-16 object-cover rounded-lg"
                            />
                          )}
                          <div className="flex-1">
                            <div className="flex items-center gap-2 mb-2">
                              <h4 className="font-medium">{photo?.file.name || 'Unknown'}</h4>
                              <Badge variant={result.success ? 'default' : 'destructive'}>
                                {result.success ? 'Success' : 'Failed'}
                              </Badge>
                            </div>
                            <div className="text-sm text-muted-foreground space-y-1">
                              <div>Processing time: {result.processingTime}ms</div>
                              <div>Credits used: {result.creditsUsed}</div>
                              {result.error && (
                                <div className="text-red-600">Error: {result.error}</div>
                              )}
                            </div>
                            {result.result && (
                              <details className="mt-2">
                                <summary className="text-sm cursor-pointer hover:text-muted-foreground">
                                  View detailed results
                                </summary>
                                <pre className="text-xs bg-background p-2 rounded mt-2 overflow-x-auto">
                                  {JSON.stringify(result.result, null, 2)}
                                </pre>
                              </details>
                            )}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Error Display */}
            {error && (
              <Alert className="border-red-200 bg-red-50">
                <AlertTriangle className="w-4 h-4 text-red-600" />
                <AlertDescription className="text-red-800">{error}</AlertDescription>
              </Alert>
            )}
          </TabsContent>
        </Tabs>
      </CardContent>
    </Card>
  );
}

'use client';

import { useState } from 'react';
import {
  Search,
  Filter,
  X,
  Calendar,
  TrendingUp,
  Users,
  Target,
  Brain,
  FileText,
  Image as ImageIcon,
  Mic,
  Video,
  Code,
  MessageSquare,
  Palette,
} from 'lucide-react';

import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';

interface FilterOptions {
  status: string[];
  type: string[];
  dateRange: string;
  sortBy: string;
  sortOrder: 'asc' | 'desc';
}

interface SearchAndFilterProps {
  onSearch: (query: string) => void;
  onFilter: (filters: FilterOptions) => void;
  onClearFilters: () => void;
  totalResults: number;
}

export default function SearchAndFilter({
  onSearch,
  onFilter,
  onClearFilters,
  totalResults,
}: SearchAndFilterProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState<FilterOptions>({
    status: [],
    type: [],
    dateRange: 'all',
    sortBy: 'timestamp',
    sortOrder: 'desc',
  });

  const statusOptions = [
    {
      value: 'success',
      label: 'Success',
      color: 'bg-green-100 text-green-800',
    },
    {
      value: 'in_progress',
      label: 'In Progress',
      color: 'bg-blue-100 text-blue-800',
    },
    { value: 'error', label: 'Error', color: 'bg-red-100 text-red-800' },
    { value: 'info', label: 'Info', color: 'bg-gray-100 text-gray-800' },
  ];

  const typeOptions = [
    { value: 'content', label: 'Content', icon: FileText },
    { value: 'optimization', label: 'Optimization', icon: Target },
    { value: 'research', label: 'Research', icon: Brain },
    { value: 'image', label: 'Image', icon: ImageIcon },
    { value: 'voice', label: 'Voice', icon: Mic },
    { value: 'video', label: 'Video', icon: Video },
    { value: 'code', label: 'Code', icon: Code },
    { value: 'chat', label: 'Chat', icon: MessageSquare },
    { value: 'design', label: 'Design', icon: Palette },
  ];

  const dateRangeOptions = [
    { value: 'all', label: 'All Time' },
    { value: 'today', label: 'Today' },
    { value: 'week', label: 'This Week' },
    { value: 'month', label: 'This Month' },
    { value: 'year', label: 'This Year' },
  ];

  const sortOptions = [
    { value: 'timestamp', label: 'Date' },
    { value: 'title', label: 'Title' },
    { value: 'type', label: 'Type' },
    { value: 'status', label: 'Status' },
  ];

  const handleSearch = (value: string) => {
    setSearchQuery(value);
    onSearch(value);
  };

  const handleStatusToggle = (status: string) => {
    const newStatuses = filters.status.includes(status)
      ? filters.status.filter(s => s !== status)
      : [...filters.status, status];

    const newFilters = { ...filters, status: newStatuses };
    setFilters(newFilters);
    onFilter(newFilters);
  };

  const handleTypeToggle = (type: string) => {
    const newTypes = filters.type.includes(type)
      ? filters.type.filter(t => t !== type)
      : [...filters.type, type];

    const newFilters = { ...filters, type: newTypes };
    setFilters(newFilters);
    onFilter(newFilters);
  };

  const handleDateRangeChange = (dateRange: string) => {
    const newFilters = { ...filters, dateRange };
    setFilters(newFilters);
    onFilter(newFilters);
  };

  const handleSortChange = (sortBy: string) => {
    const newFilters = {
      ...filters,
      sortBy,
      sortOrder: filters.sortBy === sortBy && filters.sortOrder === 'desc' ? 'asc' : 'desc',
    };
    setFilters(newFilters);
    onFilter(newFilters);
  };

  const handleClearFilters = () => {
    const resetFilters: FilterOptions = {
      status: [],
      type: [],
      dateRange: 'all',
      sortBy: 'timestamp',
      sortOrder: 'desc',
    };
    setFilters(resetFilters);
    setSearchQuery('');
    onClearFilters();
  };

  const hasActiveFilters =
    filters.status.length > 0 ||
    filters.type.length > 0 ||
    filters.dateRange !== 'all' ||
    searchQuery;

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Search className="w-5 h-5" />
            <span>Search & Filter</span>
            {hasActiveFilters && (
              <Badge variant="secondary" className="ml-2">
                Active
              </Badge>
            )}
          </div>
          <div className="flex items-center space-x-2">
            <span className="text-sm text-muted-foreground">{totalResults} results</span>
            <Button variant="outline" size="sm" onClick={() => setShowFilters(!showFilters)}>
              <Filter className="w-4 h-4 mr-2" />
              {showFilters ? 'Hide' : 'Show'} Filters
            </Button>
            {hasActiveFilters && (
              <Button variant="outline" size="sm" onClick={handleClearFilters}>
                <X className="w-4 h-4 mr-2" />
                Clear
              </Button>
            )}
          </div>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Search Input */}
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
          <Input
            placeholder="Search activities, alerts, or metrics..."
            value={searchQuery}
            onChange={e => handleSearch(e.target.value)}
            className="pl-10"
          />
        </div>

        {/* Filters Panel */}
        {showFilters && (
          <div className="space-y-6 border-t pt-4">
            {/* Status Filters */}
            <div>
              <h4 className="text-sm font-medium mb-3">Status</h4>
              <div className="flex flex-wrap gap-2">
                {statusOptions.map(option => (
                  <Badge
                    key={option.value}
                    variant={filters.status.includes(option.value) ? 'default' : 'outline'}
                    className={`cursor-pointer ${
                      filters.status.includes(option.value) ? option.color : ''
                    }`}
                    onClick={() => handleStatusToggle(option.value)}
                  >
                    {option.label}
                  </Badge>
                ))}
              </div>
            </div>

            {/* Type Filters */}
            <div>
              <h4 className="text-sm font-medium mb-3">Type</h4>
              <div className="grid grid-cols-3 md:grid-cols-5 gap-2">
                {typeOptions.map(option => {
                  const Icon = option.icon;
                  return (
                    <Badge
                      key={option.value}
                      variant={filters.type.includes(option.value) ? 'default' : 'outline'}
                      className="cursor-pointer justify-center p-2"
                      onClick={() => handleTypeToggle(option.value)}
                    >
                      <Icon className="w-3 h-3 mr-1" />
                      {option.label}
                    </Badge>
                  );
                })}
              </div>
            </div>

            {/* Date Range and Sort */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <h4 className="text-sm font-medium mb-3">Date Range</h4>
                <div className="flex flex-wrap gap-2">
                  {dateRangeOptions.map(option => (
                    <Badge
                      key={option.value}
                      variant={filters.dateRange === option.value ? 'default' : 'outline'}
                      className="cursor-pointer"
                      onClick={() => handleDateRangeChange(option.value)}
                    >
                      <Calendar className="w-3 h-3 mr-1" />
                      {option.label}
                    </Badge>
                  ))}
                </div>
              </div>

              <div>
                <h4 className="text-sm font-medium mb-3">Sort By</h4>
                <div className="flex flex-wrap gap-2">
                  {sortOptions.map(option => (
                    <Badge
                      key={option.value}
                      variant={filters.sortBy === option.value ? 'default' : 'outline'}
                      className="cursor-pointer"
                      onClick={() => handleSortChange(option.value)}
                    >
                      {option.label}
                      {filters.sortBy === option.value && (
                        <span className="ml-1">{filters.sortOrder === 'asc' ? '‚Üë' : '‚Üì'}</span>
                      )}
                    </Badge>
                  ))}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Active Filters Display */}
        {hasActiveFilters && (
          <div className="flex flex-wrap gap-2 pt-2 border-t">
            {searchQuery && (
              <Badge variant="secondary" className="flex items-center">
                Search: "{searchQuery}"
                <X className="w-3 h-3 ml-1 cursor-pointer" onClick={() => handleSearch('')} />
              </Badge>
            )}
            {filters.status.map(status => {
              const option = statusOptions.find(opt => opt.value === status);
              return (
                <Badge key={status} variant="secondary" className="flex items-center">
                  Status: {option?.label}
                  <X
                    className="w-3 h-3 ml-1 cursor-pointer"
                    onClick={() => handleStatusToggle(status)}
                  />
                </Badge>
              );
            })}
            {filters.type.map(type => {
              const option = typeOptions.find(opt => opt.value === type);
              return (
                <Badge key={type} variant="secondary" className="flex items-center">
                  Type: {option?.label}
                  <X
                    className="w-3 h-3 ml-1 cursor-pointer"
                    onClick={() => handleTypeToggle(type)}
                  />
                </Badge>
              );
            })}
            {filters.dateRange !== 'all' && (
              <Badge variant="secondary" className="flex items-center">
                Date: {dateRangeOptions.find(opt => opt.value === filters.dateRange)?.label}
                <X
                  className="w-3 h-3 ml-1 cursor-pointer"
                  onClick={() => handleDateRangeChange('all')}
                />
              </Badge>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

'use client';

import { useState, useCallback, useEffect } from 'react';
import {
  Shield,
  AlertTriangle,
  CheckCircle,
  Clock,
  Activity,
  BarChart3,
  Download,
  RefreshCw,
  Eye,
  Lock,
  Database,
  Network,
  UserCheck,
  FileText,
  Trash2,
  Settings,
  Zap,
  TrendingUp,
  TrendingDown,
  AlertCircle,
  Info,
  XCircle,
  Filter,
} from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { ScrollArea } from '@/components/ui/scroll-area';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { useSecurityMonitor, SecurityEvent, SecurityAlert } from '@/lib/security-monitor';
import { useSecureSubscription } from '@/lib/secure-subscription-manager';
import { useToast } from '@/hooks/use-toast';

export function SecurityDashboard() {
  const { isPremium } = useSecureSubscription();
  const {
    events,
    alerts,
    metrics,
    assessment,
    isLoading,
    resolveEvent,
    resolveAlert,
    performAssessment,
    exportData,
    clearOldData,
  } = useSecurityMonitor();

  const [selectedEvent, setSelectedEvent] = useState<SecurityEvent | null>(null);
  const [selectedAlert, setSelectedAlert] = useState<SecurityAlert | null>(null);
  const [eventFilter, setEventFilter] = useState<string>('all');
  const [alertFilter, setAlertFilter] = useState<string>('all');
  const { toast } = useToast();

  const filteredEvents = events.filter(event => {
    if (eventFilter === 'all') return true;
    return event.severity === eventFilter;
  });

  const filteredAlerts = alerts.filter(alert => {
    if (alertFilter === 'all') return true;
    return alert.severity === alertFilter;
  });

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical':
        return 'text-red-600 bg-red-100 border-red-200';
      case 'high':
        return 'text-orange-600 bg-orange-100 border-orange-200';
      case 'medium':
        return 'text-yellow-600 bg-yellow-100 border-yellow-200';
      case 'low':
        return 'text-green-600 bg-green-100 border-green-200';
      default:
        return 'text-gray-600 bg-gray-100 border-gray-200';
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case 'critical':
        return <XCircle className="w-4 h-4" />;
      case 'high':
        return <AlertTriangle className="w-4 h-4" />;
      case 'medium':
        return <AlertCircle className="w-4 h-4" />;
      case 'low':
        return <Info className="w-4 h-4" />;
      default:
        return <Shield className="w-4 h-4" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
        return 'text-red-600 bg-red-100 border-red-200';
      case 'investigating':
        return 'text-yellow-600 bg-yellow-100 border-yellow-200';
      case 'resolved':
        return 'text-green-600 bg-green-100 border-green-200';
      case 'false_positive':
        return 'text-blue-600 bg-blue-100 border-blue-200';
      default:
        return 'text-gray-600 bg-gray-100 border-gray-200';
    }
  };

  const getEventTypeIcon = (type: string) => {
    switch (type) {
      case 'auth':
        return <UserCheck className="w-4 h-4" />;
      case 'access':
        return <Eye className="w-4 h-4" />;
      case 'scan':
        return <Activity className="w-4 h-4" />;
      case 'upload':
        return <Database className="w-4 h-4" />;
      case 'download':
        return <Download className="w-4 h-4" />;
      case 'delete':
        return <Trash2 className="w-4 h-4" />;
      case 'share':
        return <Network className="w-4 h-4" />;
      case 'system':
        return <Settings className="w-4 h-4" />;
      default:
        return <Shield className="w-4 h-4" />;
    }
  };

  const handleResolveEvent = useCallback(
    async (eventId: string) => {
      const action = prompt('Enter action taken to resolve this event:');
      if (action) {
        const success = resolveEvent(eventId, action);
        if (success) {
          toast({
            title: 'Event Resolved',
            description: 'Security event has been marked as resolved',
          });
        }
      }
    },
    [resolveEvent, toast]
  );

  const handleResolveAlert = useCallback(
    async (alertId: string, status: 'resolved' | 'false_positive') => {
      const success = resolveAlert(alertId, status);
      if (success) {
        toast({
          title: 'Alert Resolved',
          description: `Security alert marked as ${status}`,
        });
      }
    },
    [resolveAlert, toast]
  );

  const handlePerformAssessment = useCallback(async () => {
    await performAssessment();
    toast({
      title: 'Assessment Complete',
      description: 'Security assessment has been performed',
    });
  }, [performAssessment, toast]);

  const handleExportData = useCallback(() => {
    const data = exportData();
    const blob = new Blob([data], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `security-data-${new Date().toISOString().split('T')[0]}.json`;
    a.click();
    URL.revokeObjectURL(url);

    toast({
      title: 'Data Exported',
      description: 'Security data has been exported successfully',
    });
  }, [exportData, toast]);

  const getThreatLevelColor = (score: number) => {
    if (score >= 80) return 'text-red-600';
    if (score >= 60) return 'text-orange-600';
    if (score >= 40) return 'text-yellow-600';
    return 'text-green-600';
  };

  const getThreatLevelText = (score: number) => {
    if (score >= 80) return 'Critical';
    if (score >= 60) return 'High';
    if (score >= 40) return 'Medium';
    return 'Low';
  };

  if (!isPremium) {
    return (
      <Card>
        <CardContent className="p-6">
          <div className="text-center">
            <Shield className="w-12 h-12 mx-auto text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold mb-2">Security Dashboard</h3>
            <p className="text-sm text-muted-foreground mb-4">
              Advanced security monitoring and threat detection
            </p>
            <Button className="w-full">
              <Zap className="w-4 h-4 mr-2" />
              Upgrade to Premium
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold flex items-center gap-2">
            <Shield className="w-6 h-6 text-blue-600" />
            Security Dashboard
          </h2>
          <p className="text-sm text-muted-foreground">
            Real-time security monitoring and threat detection
          </p>
        </div>
        <div className="flex gap-2">
          <Button
            variant="outline"
            size="sm"
            onClick={handlePerformAssessment}
            disabled={isLoading}
          >
            <RefreshCw className={`w-4 h-4 mr-2 ${isLoading ? 'animate-spin' : ''}`} />
            Assess
          </Button>
          <Button variant="outline" size="sm" onClick={handleExportData}>
            <Download className="w-4 h-4 mr-2" />
            Export
          </Button>
        </div>
      </div>

      {/* Security Metrics */}
      {metrics && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Threat Level</p>
                  <p className={`text-2xl font-bold ${getThreatLevelColor(metrics.threatScore)}`}>
                    {getThreatLevelText(metrics.threatScore)}
                  </p>
                  <p className="text-xs text-muted-foreground">
                    {metrics.threatScore.toFixed(1)}/100
                  </p>
                </div>
                <div
                  className={`p-2 rounded-lg ${
                    metrics.threatScore >= 80
                      ? 'bg-red-100'
                      : metrics.threatScore >= 60
                        ? 'bg-orange-100'
                        : metrics.threatScore >= 40
                          ? 'bg-yellow-100'
                          : 'bg-green-100'
                  }`}
                >
                  <Shield className="w-6 h-6" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Active Alerts</p>
                  <p className="text-2xl font-bold text-red-600">
                    {alerts.filter(a => a.status === 'active').length}
                  </p>
                  <p className="text-xs text-muted-foreground">{alerts.length} total alerts</p>
                </div>
                <div className="p-2 rounded-lg bg-red-100">
                  <AlertTriangle className="w-6 h-6 text-red-600" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">Security Events</p>
                  <p className="text-2xl font-bold text-blue-600">{events.length}</p>
                  <p className="text-xs text-muted-foreground">{metrics.resolvedEvents} resolved</p>
                </div>
                <div className="p-2 rounded-lg bg-blue-100">
                  <Activity className="w-6 h-6 text-blue-600" />
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
