'use client';

import { useState, useCallback } from 'react';
import {
  Settings,
  Shield,
  Brain,
  Monitor,
  Smartphone,
  Database,
  Wifi,
  WifiOff,
  Bell,
  Lock,
  Eye,
  Download,
  Trash2,
  RefreshCw,
  Save,
  X,
} from 'lucide-react';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import { useTheme } from '@/components/theme-provider';

interface SettingsModalProps {
  isOpen: boolean;
  onClose: () => void;
}

interface Settings {
  autoScan: boolean;
  notifications: boolean;
  darkMode: boolean;
  highContrast: boolean;
  autoBackup: boolean;
  offlineMode: boolean;
  aiModel: string;
  scanSensitivity: 'low' | 'medium' | 'high';
  storageLocation: string;
  maxFileSize: number;
  language: string;
}

export function SettingsModal({ isOpen, onClose }: SettingsModalProps) {
  const { toast } = useToast();
  const { theme, setTheme, isHighContrast, toggleHighContrast } = useTheme();
  const [settings, setSettings] = useState<Settings>({
    autoScan: true,
    notifications: true,
    darkMode: theme === 'dark',
    highContrast: isHighContrast,
    autoBackup: false,
    offlineMode: false,
    aiModel: 'glm-45v',
    scanSensitivity: 'medium',
    storageLocation: 'local',
    maxFileSize: 10,
    language: 'en',
  });
  const [isSaving, setIsSaving] = useState(false);

  const handleSave = useCallback(async () => {
    setIsSaving(true);
    try {
      // Apply theme settings
      if (settings.darkMode !== (theme === 'dark')) {
        setTheme(settings.darkMode ? 'dark' : 'light');
      }

      if (settings.highContrast !== isHighContrast) {
        toggleHighContrast();
      }

      // Save other settings to localStorage
      localStorage.setItem('app-settings', JSON.stringify(settings));

      toast({
        title: 'Settings Saved',
        description: 'Your preferences have been saved successfully.',
      });

      onClose();
    } catch {
      toast({
        title: 'Save Failed',
        description: 'Unable to save settings. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setIsSaving(false);
    }
  }, [settings, theme, setTheme, isHighContrast, toggleHighContrast, toast, onClose]);

  const handleReset = useCallback(() => {
    setSettings({
      autoScan: true,
      notifications: true,
      darkMode: theme === 'dark',
      highContrast: isHighContrast,
      autoBackup: false,
      offlineMode: false,
      aiModel: 'glm-45v',
      scanSensitivity: 'medium',
      storageLocation: 'local',
      maxFileSize: 10,
      language: 'en',
    });

    toast({
      title: 'Settings Reset',
      description: 'Settings have been reset to defaults.',
    });
  }, [theme, isHighContrast, toast]);

  const handleClearCache = useCallback(() => {
    if (confirm('Are you sure you want to clear all cached data? This action cannot be undone.')) {
      // Clear localStorage
      localStorage.clear();

      // Clear sessionStorage
      sessionStorage.clear();

      // Clear service worker cache if available
      if ('caches' in window) {
        caches.keys().then(cacheNames => {
          return Promise.all(cacheNames.map(cacheName => caches.delete(cacheName)));
        });
      }

      toast({
        title: 'Cache Cleared',
        description: 'All cached data has been cleared.',
      });
    }
  }, []);

  const exportData = useCallback(() => {
    const data = {
      settings: localStorage.getItem('app-settings'),
      photos: localStorage.getItem('photo-data'),
      preferences: localStorage.getItem('user-preferences'),
      exportDate: new Date().toISOString(),
    };

    const blob = new Blob([JSON.stringify(data, null, 2)], {
      type: 'application/json',
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `photo-guardian-export-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    toast({
      title: 'Data Exported',
      description: 'Your data has been exported successfully.',
    });
  }, []);

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Settings className="w-5 h-5" />
            Settings
          </DialogTitle>
        </DialogHeader>

        <Tabs defaultValue="general" className="w-full">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="general">General</TabsTrigger>
            <TabsTrigger value="privacy">Privacy</TabsTrigger>
            <TabsTrigger value="ai">AI Settings</TabsTrigger>
            <TabsTrigger value="advanced">Advanced</TabsTrigger>
          </TabsList>

          <TabsContent value="general" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Monitor className="w-4 h-4" />
                  Appearance
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Dark Mode</Label>
                    <p className="text-sm text-muted-foreground">Enable dark theme</p>
                  </div>
                  <Switch
                    checked={settings.darkMode}
                    onCheckedChange={checked =>
                      setSettings(prev => ({ ...prev, darkMode: checked }))
                    }
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>High Contrast</Label>
                    <p className="text-sm text-muted-foreground">Improve visibility</p>
                  </div>
                  <Switch
                    checked={settings.highContrast}
                    onCheckedChange={checked =>
                      setSettings(prev => ({
                        ...prev,
                        highContrast: checked,
                      }))
                    }
                  />
                </div>

                <div className="space-y-2">
                  <Label>Language</Label>
                  <Select
                    value={settings.language}
                    onValueChange={value => setSettings(prev => ({ ...prev, language: value }))}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="en">English</SelectItem>
                      <SelectItem value="es">Espa√±ol</SelectItem>
                      <SelectItem value="fr">Fran√ßais</SelectItem>
                      <SelectItem value="de">Deutsch</SelectItem>
                      <SelectItem value="zh">‰∏≠Êñá</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Bell className="w-4 h-4" />
                  Notifications
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Push Notifications</Label>
                    <p className="text-sm text-muted-foreground">Receive app notifications</p>
                  </div>
                  <Switch
                    checked={settings.notifications}
                    onCheckedChange={checked =>
                      setSettings(prev => ({
                        ...prev,
                        notifications: checked,
                      }))
                    }
                  />
                </div>

                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Auto Scan</Label>
                    <p className="text-sm text-muted-foreground">Automatically scan new photos</p>
                  </div>
                  <Switch
                    checked={settings.autoScan}
                    onCheckedChange={checked =>
                      setSettings(prev => ({ ...prev, autoScan: checked }))
                    }
                  />
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="privacy" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Shield className="w-4 h-4" />
                  Privacy Settings
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center justify-between">
                  <div className="space-y-0.5">
                    <Label>Auto Backup</Label>
                    <p className="text-sm text-muted-foreground">Backup photos to cloud</p>
                  </div>
                  <Switch
                    checked={settings.autoBackup}
