  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Skeleton } from '@/components/ui/skeleton';
import { AlertTriangle, Key, Shield, Activity, Users, TrendingUp } from 'lucide-react';

interface DeveloperAccessKey {
  id: string;
  keyId: string;
  userId: string;
  keyType: 'EXCLUSIVE' | 'STANDARD' | 'TEMPORARY';
  accessLevel: 'PUBLIC' | 'INTERNAL' | 'RESTRICTED' | 'CONFIDENTIAL' | 'SECRET' | 'TOP_SECRET';
  permissions: string[];
  allowedEndpoints: string[];
  rateLimit: {
    requestsPerMinute: number;
    requestsPerHour: number;
    requestsPerDay: number;
  };
  expiresAt: string;
  isActive: boolean;
  metadata: {
    purpose?: string;
    environment?: 'development' | 'staging' | 'production';
    createdBy?: string;
  };
  createdAt: string;
  updatedAt: string;
}

interface DeveloperAccessEvent {
  id: string;
  keyId: string;
  userId: string;
  eventType:
    | 'KEY_GENERATED'
    | 'KEY_USED'
    | 'KEY_REVOKED'
    | 'KEY_EXPIRED'
    | 'ACCESS_GRANTED'
    | 'ACCESS_DENIED'
    | 'QUOTA_EXCEEDED';
  endpoint?: string;
  method?: string;
  ipAddress: string;
  userAgent: string;
  details: Record<string, any>;
  timestamp: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
}

interface DeveloperAccessMetrics {
  totalKeys: number;
  activeKeys: number;
  expiredKeys: number;
  revokedKeys: number;
  totalEvents: number;
  eventsToday: number;
  eventsThisWeek: number;
  eventsThisMonth: number;
  averageUsagePerKey: number;
  topUsers: Array<{
    userId: string;
    keyCount: number;
    eventCount: number;
  }>;
  securityAlerts: number;
  quotaViolations: number;
}

export function DeveloperAccessDashboard() {
  const [metrics, setMetrics] = useState<DeveloperAccessMetrics | null>(null);
  const [events, setEvents] = useState<DeveloperAccessEvent[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Load metrics
      const metricsResponse = await fetch('/api/developer-access/metrics');
      const metricsData = await metricsResponse.json();

      if (!metricsData.success) {
        throw new Error(metricsData.error || 'Failed to load metrics');
      }

      setMetrics(metricsData.metrics);

      // Load recent events
      const eventsResponse = await fetch('/api/developer-access/events?limit=50');
      const eventsData = await eventsResponse.json();

      if (!eventsData.success) {
        throw new Error(eventsData.error || 'Failed to load events');
      }

      setEvents(eventsData.events || []);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error occurred');
    } finally {
      setLoading(false);
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'low':
        return 'bg-green-100 text-green-800';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800';
      case 'high':
        return 'bg-orange-100 text-orange-800';
      case 'critical':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getKeyTypeColor = (keyType: string) => {
    switch (keyType) {
      case 'EXCLUSIVE':
        return 'bg-purple-100 text-purple-800';
      case 'STANDARD':
        return 'bg-blue-100 text-blue-800';
      case 'TEMPORARY':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getAccessLevelColor = (accessLevel: string) => {
    switch (accessLevel) {
      case 'PUBLIC':
        return 'bg-green-100 text-green-800';
      case 'INTERNAL':
        return 'bg-blue-100 text-blue-800';
      case 'RESTRICTED':
        return 'bg-yellow-100 text-yellow-800';
      case 'CONFIDENTIAL':
        return 'bg-orange-100 text-orange-800';
      case 'SECRET':
        return 'bg-red-100 text-red-800';
      case 'TOP_SECRET':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDateTime = (dateString: string) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
