interface CoverageData {
  overall: number;
  byType: {
    unit: number;
    integration: number;
    e2e: number;
    security: number;
    performance: number;
  };
  byModule: {
    name: string;
    coverage: number;
    lines: number;
    functions: number;
    branches: number;
    statements: number;
  }[];
  trends: {
    date: string;
    overall: number;
    unit: number;
    integration: number;
    e2e: number;
    security: number;
    performance: number;
  }[];
  fileLevel: {
    path: string;
    coverage: number;
    lines: number;
    uncovered: number;
    risk: 'low' | 'medium' | 'high';
  }[];
}

interface CoverageVisualizationProps {
  data?: CoverageData;
  onRefresh?: () => void;
  isRealTime?: boolean;
}

const CoverageVisualization: React.FC<CoverageVisualizationProps> = ({
  data: initialData,
  onRefresh,
  isRealTime = false,
