'use client';

import { useState, useEffect, useRef } from 'react';
import {
  Send,
  Loader2,
  Bot,
  User,
  Settings,
  Copy,
  Download,
  Trash2,
  Zap,
  Brain,
  Code,
  MessageSquare,
} from 'lucide-react';

import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Slider } from '@/components/ui/slider';

interface Message {
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
  model?: string;
  cost?: number;
  tokens?: number;
}

interface ChatSession {
  id: string;
  title: string;
  messages: Message[];
  createdAt: Date;
  model: string;
  totalCost: number;
}

interface AIChatProps {
  className?: string;
}

interface AIModel {
  name: string;
  provider: string;
  cost: number;
  category: string;
}

interface AvailableModels {
  [key: string]: AIModel;
}

export function AIChat({ className }: AIChatProps) {
  const [sessions, setSessions] = useState<ChatSession[]>([]);
  const [currentSession, setCurrentSession] = useState<ChatSession | null>(null);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedModel, setSelectedModel] = useState('openai/gpt-4-turbo');
  const [temperature, setTemperature] = useState([0.7]);
  const [maxTokens, setMaxTokens] = useState([1000]);
  const [availableModels, setAvailableModels] = useState<AvailableModels>({});
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Load available models on component mount
  useEffect(() => {
    loadModels();
    loadSessions();
  }, []);

  // Auto-scroll to bottom of messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [currentSession?.messages]);

  const loadModels = async () => {
    try {
      const response = await fetch('/api/chat');
      const data = await response.json();
      setAvailableModels(data.models);
      setSelectedModel(data.defaultModel);
    } catch (error) {
      console.error('Error loading models:', error);
    }
  };

  const loadSessions = () => {
    // In a real app, this would load from localStorage or a database
    const savedSessions = localStorage.getItem('ai-chat-sessions');
    if (savedSessions) {
      const parsed = JSON.parse(savedSessions);
      setSessions(parsed);
      if (parsed.length > 0) {
        setCurrentSession(parsed[0]);
      }
    }
  };

  const saveSessions = (updatedSessions: ChatSession[]) => {
    localStorage.setItem('ai-chat-sessions', JSON.stringify(updatedSessions));
    setSessions(updatedSessions);
  };

  const createNewSession = () => {
    const newSession: ChatSession = {
      id: Date.now().toString(),
      title: 'New Chat',
      messages: [],
      createdAt: new Date(),
      model: selectedModel,
      totalCost: 0,
    };

    const updatedSessions = [newSession, ...sessions];
    saveSessions(updatedSessions);
    setCurrentSession(newSession);
  };

  const deleteSession = (sessionId: string) => {
    const updatedSessions = sessions.filter(s => s.id !== sessionId);
    saveSessions(updatedSessions);

    if (currentSession?.id === sessionId) {
      setCurrentSession(updatedSessions[0] || null);
    }
  };

  const sendMessage = async () => {
    if (!input.trim() || !currentSession || isLoading) return;

    const userMessage: Message = {
      role: 'user',
      content: input,
      timestamp: new Date(),
    };

    // Update current session with user message
    const updatedSession = {
      ...currentSession,
      messages: [...currentSession.messages, userMessage],
      title:
        currentSession.messages.length === 0 ? `${input.slice(0, 50)}...` : currentSession.title,
    };

    setCurrentSession(updatedSession);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          messages: updatedSession.messages.map(msg => ({
            role: msg.role,
            content: msg.content,
          })),
          model: selectedModel,
          temperature: temperature[0],
          maxTokens: maxTokens[0],
        }),
      });

      const data = await response.json();

      if (data.success) {
        const assistantMessage: Message = {
          role: 'assistant',
          content: data.response,
          timestamp: new Date(),
          model: data.model,
          cost: data.cost,
          tokens: data.usage.totalTokens,
        };

        const finalSession = {
          ...updatedSession,
          messages: [...updatedSession.messages, assistantMessage],
          totalCost: updatedSession.totalCost + data.cost,
        };

        setCurrentSession(finalSession);

        // Update sessions list
        const updatedSessions = sessions.map(s => (s.id === finalSession.id ? finalSession : s));
        saveSessions(updatedSessions);
      } else {
        alert(`Failed to get response: ${data.error}`);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      alert('Error sending message');
    } finally {
      setIsLoading(false);
    }
  };

  const copyMessage = (content: string) => {
    navigator.clipboard.writeText(content);
  };

  const exportSession = () => {
    if (!currentSession) return;

    const exportData = {
      title: currentSession.title,
      model: currentSession.model,
      createdAt: currentSession.createdAt,
      messages: currentSession.messages,
      totalCost: currentSession.totalCost,
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], {
      type: 'application/json',
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `chat-session-${currentSession.id}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const clearSession = () => {
    if (!currentSession) return;

    const clearedSession = {
      ...currentSession,
      messages: [],
      title: 'New Chat',
      totalCost: 0,
    };

    setCurrentSession(clearedSession);

    const updatedSessions = sessions.map(s => (s.id === clearedSession.id ? clearedSession : s));
    saveSessions(updatedSessions);
  };

  const getModelIcon = (model: string) => {
    if (model.includes('gpt')) return <Bot className="h-4 w-4" />;
    if (model.includes('claude')) return <Brain className="h-4 w-4" />;
    if (model.includes('gemini')) return <Zap className="h-4 w-4" />;
    if (model.includes('llama') || model.includes('mistral')) return <Code className="h-4 w-4" />;
    return <MessageSquare className="h-4 w-4" />;
  };

  const getModelCategory = (model: string) => {
    const modelInfo = availableModels[model];
    return modelInfo?.category || 'unknown';
  };

  return (
    <div className={`space-y-6 ${className}`}>
      <Card className="h-[700px]">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <CardTitle className="flex items-center gap-2">
              <MessageSquare className="h-5 w-5" />
              AI Assistant Chat
            </CardTitle>
            <div className="flex gap-2">
              <Button variant="outline" size="sm" onClick={createNewSession}>
                New Chat
              </Button>
              {currentSession && (
                <>
                  <Button variant="outline" size="sm" onClick={exportSession}>
                    <Download className="h-4 w-4" />
                  </Button>
                  <Button variant="outline" size="sm" onClick={clearSession}>
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </>
              )}
            </div>
          </div>
        </CardHeader>
        <CardContent className="flex h-[580px]">
          {/* Sidebar */}
          <div className="w-64 border-r pr-4 mr-4">
            <div className="space-y-4">
              <div>
                <h3 className="text-sm font-medium mb-2">Chat Sessions</h3>
                <ScrollArea className="h-[200px]">
                  <div className="space-y-2">
                    {sessions.map(session => (
                      <div
                        key={session.id}
                        className={`p-2 rounded cursor-pointer text-sm ${
                          currentSession?.id === session.id
                            ? 'bg-primary text-primary-foreground'
                            : 'hover:bg-muted'
                        }`}
                        onClick={() => setCurrentSession(session)}
                      >
                        <div className="flex items-center justify-between">
                          <span className="truncate">{session.title}</span>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={e => {
                              e.stopPropagation();
                              deleteSession(session.id);
                            }}
                          >
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        </div>
                        <div className="text-xs opacity-70">
                          {session.messages.length} messages ‚Ä¢ ${session.totalCost.toFixed(4)}
                        </div>
                      </div>
                    ))}
                  </div>
                </ScrollArea>
              </div>

              <div>
                <h3 className="text-sm font-medium mb-2">Model Settings</h3>
                <div className="space-y-3">
                  <div>
                    <label className="text-xs text-muted-foreground">AI Model</label>
                    <Select value={selectedModel} onValueChange={setSelectedModel}>
                      <SelectTrigger className="h-8 text-xs">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {Object.entries(availableModels).map(([key, model]: [string, AIModel]) => (
                          <SelectItem key={key} value={key}>
                            <div className="flex items-center gap-2">
                              {getModelIcon(key)}
                              <div>
                                <div className="font-medium">{model.name}</div>
                                <div className="text-xs text-muted-foreground">
                                  {model.provider} ‚Ä¢ ${model.cost}/1K tokens
                                </div>
                              </div>
                            </div>
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div>
                    <label className="text-xs text-muted-foreground">
                      Temperature: {temperature[0]}
                    </label>
                    <Slider
                      value={temperature}
                      onValueChange={setTemperature}
                      max={2}
                      min={0}
                      step={0.1}
                      className="w-full"
                    />
                  </div>

                  <div>
                    <label className="text-xs text-muted-foreground">
                      Max Tokens: {maxTokens[0]}
                    </label>
                    <Slider
                      value={maxTokens}
                      onValueChange={setMaxTokens}
                      max={4000}
                      min={100}
                      step={100}
                      className="w-full"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Main Chat Area */}
          <div className="flex-1 flex flex-col">
            {currentSession ? (
              <>
                <ScrollArea className="flex-1 mb-4 p-4 border rounded-lg">
                  <div className="space-y-4">
                    {currentSession.messages.length === 0 ? (
                      <div className="text-center py-8 text-muted-foreground">
                        <MessageSquare className="h-12 w-12 mx-auto mb-4 opacity-50" />
                        <p>Start a conversation with AI</p>
                        <p className="text-sm">Ask questions, get help, or just chat!</p>
                      </div>
                    ) : (
                      currentSession.messages.map((message, index) => (
                        <div
                          key={index}
                          className={`flex gap-3 ${
                            message.role === 'user' ? 'justify-end' : 'justify-start'
                          }`}
                        >
                          <div
                            className={`flex gap-2 max-w-[80%] ${
                              message.role === 'user' ? 'flex-row-reverse' : 'flex-row'
                            }`}
                          >
                            <div
                              className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center ${
                                message.role === 'user'
                                  ? 'bg-primary text-primary-foreground'
                                  : 'bg-muted'
                              }`}
                            >
                              {message.role === 'user' ? (
                                <User className="h-4 w-4" />
                              ) : (
                                getModelIcon(message.model || selectedModel)
                              )}
                            </div>
                            <div className="space-y-1">
                              <div
                                className={`p-3 rounded-lg ${
                                  message.role === 'user'
                                    ? 'bg-primary text-primary-foreground'
                                    : 'bg-muted'
                                }`}
                              >
                                <p className="whitespace-pre-wrap">{message.content}</p>
                              </div>
                              <div className="flex items-center gap-2 text-xs text-muted-foreground">
                                <span>{message.timestamp.toLocaleTimeString()}</span>
                                {message.model && (
                                  <Badge variant="outline" className="text-xs">
                                    {availableModels[message.model]?.name || message.model}
                                  </Badge>
                                )}
                                {message.cost && <span>${message.cost.toFixed(4)}</span>}
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  onClick={() => copyMessage(message.content)}
                                  className="h-4 w-4 p-0"
                                >
                                  <Copy className="h-3 w-3" />
                                </Button>
                              </div>
                            </div>
                          </div>
                        </div>
                      ))
                    )}
                    {isLoading && (
                      <div className="flex gap-3 justify-start">
                        <div className="flex gap-2 max-w-[80%]">
                          <div className="flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center bg-muted">
                            {getModelIcon(selectedModel)}
                          </div>
                          <div className="space-y-1">
                            <div className="p-3 rounded-lg bg-muted">
                              <div className="flex items-center gap-2">
                                <Loader2 className="h-4 w-4 animate-spin" />
                                <span>AI is thinking...</span>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    )}
                    <div ref={messagesEndRef} />
                  </div>
                </ScrollArea>

                <div className="flex gap-2">
                  <Input
                    placeholder="Type your message..."
                    value={input}
                    onChange={e => setInput(e.target.value)}
                    onKeyPress={e => e.key === 'Enter' && sendMessage()}
                    disabled={isLoading}
                    className="flex-1"
                  />
                  <Button onClick={sendMessage} disabled={isLoading || !input.trim()}>
                    {isLoading ? (
                      <Loader2 className="h-4 w-4 animate-spin" />
                    ) : (
                      <Send className="h-4 w-4" />
                    )}
                  </Button>
                </div>

                <div className="mt-2 flex items-center justify-between text-xs text-muted-foreground">
                  <div className="flex items-center gap-2">
                    <Badge variant="outline" className="text-xs">
                      {availableModels[selectedModel]?.name || selectedModel}
                    </Badge>
                    <Badge variant="secondary" className="text-xs">
                      {getModelCategory(selectedModel)}
                    </Badge>
                  </div>
                  <div>Session Cost: ${currentSession.totalCost.toFixed(4)}</div>
                </div>
              </>
            ) : (
              <div className="flex-1 flex items-center justify-center text-muted-foreground">
                <div className="text-center">
                  <MessageSquare className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No chat session selected</p>
                  <p className="text-sm">Create a new chat to get started</p>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useState } from 'react';
import {
  Loader2,
  Code,
  FileText,
  Search,
  Bug,
  Zap,
  Copy,
  Download,
  CheckCircle,
  AlertTriangle,
  Lightbulb,
} from 'lucide-react';

import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { ScrollArea } from '@/components/ui/scroll-area';

interface CodeAssistantProps {
  className?: string;
}

interface CodeResult {
  content: string;
  model: string;
  cost: number;
  usage: {
    promptTokens: number;
    completionTokens: number;
    totalTokens: number;
  };
}

export function CodeAssistant({ className }: CodeAssistantProps) {
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [task, setTask] = useState('review');
  const [context, setContext] = useState('');
  const [result, setResult] = useState<CodeResult | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [copied, setCopied] = useState(false);

  const languages = [
    { value: 'javascript', label: 'JavaScript' },
    { value: 'typescript', label: 'TypeScript' },
    { value: 'python', label: 'Python' },
    { value: 'java', label: 'Java' },
    { value: 'cpp', label: 'C++' },
    { value: 'csharp', label: 'C#' },
    { value: 'go', label: 'Go' },
    { value: 'rust', label: 'Rust' },
    { value: 'php', label: 'PHP' },
    { value: 'ruby', label: 'Ruby' },
    { value: 'swift', label: 'Swift' },
    { value: 'kotlin', label: 'Kotlin' },
  ];

  const tasks = [
    {
      value: 'review',
      label: 'Code Review',
      icon: <Search className="h-4 w-4" />,
      description: 'Review code for bugs, performance issues, and best practices',
    },
    {
      value: 'optimize',
      label: 'Optimize',
      icon: <Zap className="h-4 w-4" />,
      description: 'Optimize code for better performance and readability',
    },
    {
      value: 'document',
      label: 'Document',
      icon: <FileText className="h-4 w-4" />,
      description: 'Generate comprehensive documentation and comments',
    },
    {
      value: 'debug',
      label: 'Debug',
      icon: <Bug className="h-4 w-4" />,
      description: 'Identify and fix bugs in the code',
    },
    {
      value: 'generate-tests',
      label: 'Generate Tests',
      icon: <CheckCircle className="h-4 w-4" />,
      description: 'Create unit tests and test cases',
    },
    {
      value: 'refactor',
      label: 'Refactor',
      icon: <Code className="h-4 w-4" />,
      description: 'Refactor code for better structure and maintainability',
    },
  ];

  const processCode = async () => {
    if (!code.trim() || !language || !task) {
      alert('Please fill in all required fields');
      return;
    }

    setIsProcessing(true);
    try {
      const response = await fetch('/api/code-assist', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          code,
          language,
          task,
          context,
        }),
      });

      const data = await response.json();

      if (data.success) {
        setResult({
          content: data.result,
          model: data.model,
          cost: data.cost,
          usage: data.usage,
        });
      } else {
        alert(`Failed to process code: ${data.error}`);
      }
    } catch (error) {
      console.error('Error processing code:', error);
      alert('Error processing code');
    } finally {
      setIsProcessing(false);
    }
  };

  const copyToClipboard = async () => {
    if (!result) return;

    try {
      await navigator.clipboard.writeText(result.content);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy to clipboard:', error);
    }
  };

  const downloadResult = () => {
    if (!result) return;

    const taskInfo = tasks.find(t => t.value === task);
    const filename = `${taskInfo?.label.toLowerCase().replace(/\s+/g, '_')}_${language}.${language === 'javascript' ? 'js' : language}`;

    const blob = new Blob([result.content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const getTaskIcon = (taskValue: string) => {
    const taskInfo = tasks.find(t => t.value === taskValue);
    return taskInfo?.icon || <Code className="h-4 w-4" />;
  };

  const getTaskDescription = (taskValue: string) => {
    const taskInfo = tasks.find(t => t.value === taskValue);
    return taskInfo?.description || '';
  };

  const codeExamples = {
    javascript: `// Example JavaScript function
function calculateTotal(items) {
  let total = 0;
  for (let i = 0; i < items.length; i++) {
    total += items[i].price * items[i].quantity;
  }
  return total;
}`,
    python: `# Example Python function
def calculate_total(items):
    total = 0
    for item in items:
        total += item['price'] * item['quantity']
    return total`,
    java: `// Example Java method
public double calculateTotal(List<Item> items) {
    double total = 0.0;
    for (Item item : items) {
        total += item.getPrice() * item.getQuantity();
    }
    return total;
}`,
  };

  const loadExample = () => {
    const example = codeExamples[language as keyof typeof codeExamples];
    if (example) {
      setCode(example);
    }
  };

  return (
    <div className={`space-y-6 ${className}`}>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Code className="h-5 w-5" />
            AI Code Assistant
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Programming Language</label>
              <Select value={language} onValueChange={setLanguage}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {languages.map(lang => (
                    <SelectItem key={lang.value} value={lang.value}>
                      {lang.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium">Task</label>
              <Select value={task} onValueChange={setTask}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {tasks.map(taskOption => (
                    <SelectItem key={taskOption.value} value={taskOption.value}>
                      <div className="flex items-center gap-2">
                        {taskOption.icon}
                        {taskOption.label}
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <label className="text-sm font-medium">Code *</label>
              <Button variant="outline" size="sm" onClick={loadExample}>
                Load Example
              </Button>
            </div>
            <Textarea
              placeholder={`Enter your ${language} code here...`}
              value={code}
              onChange={e => setCode(e.target.value)}
              rows={8}
              className="font-mono text-sm"
            />
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">Context (Optional)</label>
            <Textarea
              placeholder="Provide additional context about the code, project, or specific requirements..."
              value={context}
              onChange={e => setContext(e.target.value)}
              rows={3}
            />
          </div>

          <div className="bg-muted p-3 rounded-lg">
            <div className="flex items-center gap-2 mb-2">
              {getTaskIcon(task)}
              <span className="font-medium">{tasks.find(t => t.value === task)?.label}</span>
            </div>
            <p className="text-sm text-muted-foreground">{getTaskDescription(task)}</p>
          </div>

          <Button onClick={processCode} disabled={isProcessing || !code.trim()} className="w-full">
            {isProcessing ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Processing...
              </>
            ) : (
              <>
                {getTaskIcon(task)}
                <span className="ml-2">{tasks.find(t => t.value === task)?.label} Code</span>
              </>
            )}
          </Button>
        </CardContent>
      </Card>

      {result && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                {getTaskIcon(task)}
                {tasks.find(t => t.value === task)?.label} Result
              </div>
              <div className="flex gap-2">
                <Button variant="outline" size="sm" onClick={copyToClipboard}>
                  {copied ? <CheckCircle className="h-4 w-4" /> : <Copy className="h-4 w-4" />}
                </Button>
                <Button variant="outline" size="sm" onClick={downloadResult}>
                  <Download className="h-4 w-4" />
                </Button>
              </div>
            </CardTitle>
            <div className="flex flex-wrap gap-2">
              <Badge variant="secondary">Model: {result.model}</Badge>
              <Badge variant="secondary">Cost: ${result.cost.toFixed(4)}</Badge>
              <Badge variant="secondary">Tokens: {result.usage.totalTokens}</Badge>
            </div>
          </CardHeader>
          <CardContent>
            <ScrollArea className="h-[400px] w-full">
              <pre className="whitespace-pre-wrap text-sm bg-muted p-4 rounded-md">
                <code>{result.content}</code>
              </pre>
            </ScrollArea>
          </CardContent>
        </Card>
      )}

      {/* Quick Actions Section */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Lightbulb className="h-5 w-5" />
            Quick Actions
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {tasks.map(taskOption => (
              <Button
                key={taskOption.value}
                variant="outline"
                className="h-auto p-4 flex flex-col items-start gap-2"
                onClick={() => {
                  setTask(taskOption.value);
                  if (!code) loadExample();
                }}
              >
                <div className="flex items-center gap-2 w-full">
                  {taskOption.icon}
                  <span className="font-medium">{taskOption.label}</span>
                </div>
                <p className="text-xs text-muted-foreground text-left">{taskOption.description}</p>
              </Button>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Loader2, Wand2, Copy, Download } from 'lucide-react';

import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';

interface ContentGeneratorProps {
  className?: string;
}

export function ContentGenerator({ className }: ContentGeneratorProps) {
  const [topic, setTopic] = useState('');
  const [contentType, setContentType] = useState('');
  const [tone, setTone] = useState('professional');
  const [length, setLength] = useState('medium');
  const [keywords, setKeywords] = useState('');
  const [targetAudience, setTargetAudience] = useState('');
  const [generatedContent, setGeneratedContent] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [generationInfo, setGenerationInfo] = useState<{
    model: string;
    cost: number;
    usage: {
      promptTokens: number;
      completionTokens: number;
      totalTokens: number;
    };
  } | null>(null);

  const contentTypes = [
    { value: 'blog', label: 'Blog Post' },
    { value: 'article', label: 'Article' },
    { value: 'product-description', label: 'Product Description' },
    { value: 'social-media', label: 'Social Media Post' },
    { value: 'email', label: 'Email' },
    { value: 'documentation', label: 'Documentation' },
  ];

  const tones = [
    { value: 'professional', label: 'Professional' },
    { value: 'casual', label: 'Casual' },
    { value: 'friendly', label: 'Friendly' },
    { value: 'formal', label: 'Formal' },
    { value: 'enthusiastic', label: 'Enthusiastic' },
  ];

  const lengths = [
    { value: 'short', label: 'Short (200-500 words)' },
    { value: 'medium', label: 'Medium (500-1000 words)' },
    { value: 'long', label: 'Long (1000+ words)' },
  ];

  const generateContent = async () => {
    if (!topic || !contentType) {
      alert('Please fill in the topic and content type');
      return;
    }

    setIsGenerating(true);
    try {
      const response = await fetch('/api/content/generate', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          topic,
          contentType,
          tone,
          length,
          keywords: keywords
            .split(',')
            .map(k => k.trim())
            .filter(k => k),
          targetAudience,
        }),
      });

      const data = await response.json();

      if (data.success) {
        setGeneratedContent(data.content);
        setGenerationInfo({
          model: data.model,
          cost: data.cost,
          usage: data.usage,
        });
      } else {
        alert(`Failed to generate content: ${data.error}`);
      }
    } catch (error) {
      console.error('Error generating content:', error);
      alert('Error generating content');
    } finally {
      setIsGenerating(false);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(generatedContent);
  };

  const downloadContent = () => {
    const blob = new Blob([generatedContent], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${topic.replace(/\s+/g, '_')}_${contentType}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  return (
    <div className={`space-y-6 ${className}`}>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Wand2 className="h-5 w-5" />
            AI Content Generator
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Topic *</label>
              <Input
                placeholder="Enter your topic..."
                value={topic}
                onChange={e => setTopic(e.target.value)}
              />
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium">Content Type *</label>
              <Select value={contentType} onValueChange={setContentType}>
                <SelectTrigger>
                  <SelectValue placeholder="Select content type" />
                </SelectTrigger>
                <SelectContent>
                  {contentTypes.map(type => (
                    <SelectItem key={type.value} value={type.value}>
                      {type.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="space-y-2">
              <label className="text-sm font-medium">Tone</label>
              <Select value={tone} onValueChange={setTone}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {tones.map(toneOption => (
                    <SelectItem key={toneOption.value} value={toneOption.value}>
                      {toneOption.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium">Length</label>
              <Select value={length} onValueChange={setLength}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {lengths.map(lengthOption => (
                    <SelectItem key={lengthOption.value} value={lengthOption.value}>
                      {lengthOption.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <label className="text-sm font-medium">Target Audience</label>
              <Input
                placeholder="e.g., developers, marketers"
                value={targetAudience}
                onChange={e => setTargetAudience(e.target.value)}
              />
            </div>
          </div>

          <div className="space-y-2">
            <label className="text-sm font-medium">Keywords (comma-separated)</label>
            <Input
              placeholder="e.g., AI, technology, innovation"
              value={keywords}
              onChange={e => setKeywords(e.target.value)}
            />
          </div>

          <Button
            onClick={generateContent}
            disabled={isGenerating || !topic || !contentType}
            className="w-full"
          >
            {isGenerating ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Generating...
              </>
            ) : (
              <>
                <Wand2 className="mr-2 h-4 w-4" />
                Generate Content
              </>
            )}
          </Button>
        </CardContent>
      </Card>

      {generatedContent && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              Generated Content
              <div className="flex gap-2">
                <Button variant="outline" size="sm" onClick={copyToClipboard}>
                  <Copy className="h-4 w-4 mr-2" />
                  Copy
                </Button>
                <Button variant="outline" size="sm" onClick={downloadContent}>
                  <Download className="h-4 w-4 mr-2" />
                  Download
                </Button>
              </div>
            </CardTitle>
            {generationInfo && (
              <div className="flex flex-wrap gap-2">
                <Badge variant="secondary">Model: {generationInfo.model}</Badge>
                <Badge variant="secondary">Cost: ${generationInfo.cost.toFixed(4)}</Badge>
                <Badge variant="secondary">Tokens: {generationInfo.usage.totalTokens}</Badge>
              </div>
            )}
          </CardHeader>
          <CardContent>
            <ScrollArea className="h-[400px] w-full border rounded-md p-4">
              <div className="whitespace-pre-wrap">{generatedContent}</div>
            </ScrollArea>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

