  private generateCategories(analysis: AIAnalysisResult): string[] {
    const categories: string[] = [];

    // Content-based categories
    if (analysis.faces > 0) categories.push('People', 'Portraits');
    if (analysis.objects.includes('car') || analysis.objects.includes('vehicle'))
      categories.push('Transportation');
    if (analysis.objects.includes('building') || analysis.objects.includes('house'))
      categories.push('Architecture');
    if (
      analysis.objects.includes('tree') ||
      analysis.objects.includes('flower') ||
      analysis.objects.includes('plant')
    )
      categories.push('Nature');
    if (analysis.objects.includes('food') || analysis.objects.includes('drink'))
      categories.push('Food & Drink');

    // Emotion-based categories
    if (analysis.emotions.includes('happy') || analysis.emotions.includes('joyful'))
      categories.push('Happy Moments');
    if (analysis.emotions.includes('sad') || analysis.emotions.includes('melancholic'))
      categories.push('Emotional');
    if (analysis.emotions.includes('calm') || analysis.emotions.includes('peaceful'))
      categories.push('Peaceful');

    // Quality-based categories
    if (analysis.quality > 0.8) categories.push('High Quality');
    if (analysis.aestheticScore > 0.8) categories.push('Aesthetic');

    // Safety-based categories
    if (analysis.safetyScore > 0.9) categories.push('Safe Content');
    if (analysis.isNsfw) categories.push('Sensitive Content');

    // Text-based categories
    if (analysis.text.length > 0) categories.push('Contains Text');

    return categories;
  }

