    period: { startDate: Date; endDate: Date },
    options?: {
      includeEvidence?: boolean;
      includeRecommendations?: boolean;
      includeTrends?: boolean;
      customSections?: ReportSection[];
    }
  ): Promise<ComplianceReport> {
    if (!this.isInitialized) {
