}

export interface FinancialImpact {
  direct: number;
  indirect: number;
  currency: string;
  estimated: boolean;
  timeframe: string;
}

export interface ReputationalImpact {
  stakeholders: string[];
  perception: 'positive' | 'neutral' | 'negative';
  media: boolean;
  customer: boolean;
  partner: boolean;
  regulatory: boolean;
}

export interface LegalImpact {
