// API key types
export enum APIKeyType {
  PRODUCTION = 'PRODUCTION',
  DEVELOPMENT = 'DEVELOPMENT',
  TESTING = 'TESTING',
  PARTNER = 'PARTNER',
  INTERNAL = 'INTERNAL',
}

// API key status
export enum APIKeyStatus {
  ACTIVE = 'ACTIVE',
  INACTIVE = 'INACTIVE',
  EXPIRED = 'EXPIRED',
  REVOKED = 'REVOKED',
  PENDING_ROTATION = 'PENDING_ROTATION',
}

// Rate limiting strategies
export enum RateLimitStrategy {
  FIXED_WINDOW = 'FIXED_WINDOW',
  SLIDING_WINDOW = 'SLIDING_WINDOW',
  TOKEN_BUCKET = 'TOKEN_BUCKET',
  LEAKY_BUCKET = 'LEAKY_BUCKET',
}

// API request methods
export enum APIMethod {
  GET = 'GET',
  POST = 'POST',
  PUT = 'PUT',
  DELETE = 'DELETE',
  PATCH = 'PATCH',
  HEAD = 'HEAD',
  OPTIONS = 'OPTIONS',
}

// API key interface
export interface APIKey {
  id: string;
  name: string;
  key: string;
  secret: string;
  type: APIKeyType;
  status: APIKeyStatus;
  permissions: string[];
  rateLimits: RateLimitConfig[];
  allowedIPs: string[];
  allowedOrigins: string[];
  createdAt: number;
  expiresAt?: number;
  lastUsed?: number;
  usageCount: number;
  metadata: Record<string, any>;
  rotationConfig?: {
    enabled: boolean;
    interval: number;
    nextRotation?: number;
  };
}

// Rate limit configuration
export interface RateLimitConfig {
  strategy: RateLimitStrategy;
  window: number; // Time window in milliseconds
  limit: number; // Maximum requests per window
  burst?: number; // Burst capacity for token bucket
}

// Rate limit state
export interface RateLimitState {
  requests: number;
  windowStart: number;
  tokens?: number; // For token bucket
  lastRefill?: number; // For token bucket
}

// API request context
export interface APIRequestContext {
  id: string;
  method: APIMethod;
  path: string;
  headers: Record<string, string>;
  query: Record<string, string>;
  body?: any;
  timestamp: number;
  clientIP: string;
  userAgent: string;
  apiKey?: APIKey;
  userId?: string;
  sessionId?: string;
}

// API response context
export interface APIResponseContext {
  id: string;
  status: number;
  headers: Record<string, string>;
  body?: any;
  timestamp: number;
  duration: number;
  size: number;
  cached: boolean;
}

// API analytics event
export interface APIAnalyticsEvent {
  id: string;
  requestId: string;
  timestamp: number;
  eventType: 'request' | 'response' | 'error' | 'security';
  method: APIMethod;
  path: string;
  status?: number;
  duration?: number;
  error?: string;
  securityEvent?: {
    type: 'suspicious_activity' | 'rate_limit_exceeded' | 'invalid_key' | 'unauthorized';
    details: Record<string, any>;
  };
  metadata: Record<string, any>;
}

// API route definition
export interface APIRoute {
  id: string;
  path: string;
  method: APIMethod;
  handler: (request: APIRequestContext) => Promise<APIResponseContext>;
  authRequired: boolean;
  permissions: string[];
  rateLimitOverride?: RateLimitConfig;
  cacheConfig?: {
    enabled: boolean;
    ttl: number;
    keyGenerator?: (request: APIRequestContext) => string;
  };
  transform?: {
    request?: (request: APIRequestContext) => APIRequestContext;
    response?: (response: APIResponseContext) => APIResponseContext;
  };
  metadata: Record<string, any>;
}

// API gateway configuration
export interface APIGatewayConfig {
  enabled: boolean;
  port: number;
  host: string;
  ssl: {
    enabled: boolean;
    certPath?: string;
    keyPath?: string;
  };
  cors: {
    enabled: boolean;
    allowedOrigins: string[];
    allowedMethods: string[];
    allowedHeaders: string[];
    exposedHeaders: string[];
    credentials: boolean;
    maxAge: number;
  };
  compression: {
    enabled: boolean;
    level: number;
  };
  cache: {
    enabled: boolean;
    driver: 'memory' | 'redis';
    config: Record<string, any>;
  };
}

// Security monitoring configuration
export interface SecurityMonitoringConfig {
  enabled: boolean;
  suspiciousIPThreshold: number;
  rateLimitThreshold: number;
  anomalyDetection: boolean;
  threatIntelligence: boolean;
  blockMaliciousIPs: boolean;
  logSecurityEvents: boolean;
}

// Analytics configuration
export interface AnalyticsConfig {
  enabled: boolean;
  retention: number;
  aggregation: {
    enabled: boolean;
    interval: number;
  };
  export: {
    enabled: boolean;
    format: 'json' | 'csv' | 'parquet';
    destination: string;
    interval: number;
  };
  dashboard: {
    enabled: boolean;
    refreshInterval: number;
  };
}

// Enterprise API manager configuration
export interface APIManagerConfig {
  keyRotation: {
    enabled: boolean;
    defaultInterval: number; // 30 days in milliseconds
    warningPeriod: number; // 7 days in milliseconds
    autoRotate: boolean;
  };
  rateLimiting: {
    defaultStrategy: RateLimitStrategy;
    defaultWindow: number; // 15 minutes
    defaultLimit: number; // 1000 requests
    enableBurst: boolean;
    burstMultiplier: number;
  };
  gateway: APIGatewayConfig;
  security: SecurityMonitoringConfig;
  analytics: AnalyticsConfig;
}

// Enterprise API manager
export class EnterpriseAPIManager extends EventEmitter {
  private config: APIManagerConfig;
  private environment: EnterpriseEnvironmentConfig;
  private healthMonitor?: EnterpriseHealthMonitor;
  private apiKeys: Map<string, APIKey> = new Map();
  private rateLimitStates: Map<string, RateLimitState> = new Map();
  private routes: Map<string, APIRoute> = new Map();
  private analyticsEvents: APIAnalyticsEvent[] = [];
  private suspiciousIPs: Set<string> = new Set();
  private blockedIPs: Set<string> = new Set();
  private cache: Map<string, { data: any; expires: number }> = new Map();
  private isRunning = false;
  private rotationTimer?: NodeJS.Timeout;
  private cleanupTimer?: NodeJS.Timeout;
  private analyticsTimer?: NodeJS.Timeout;

  constructor(environment: EnterpriseEnvironmentConfig, config: Partial<APIManagerConfig> = {}) {
    super();
    this.environment = environment;
    this.config = {
      keyRotation: {
        enabled: true,
        defaultInterval: 2592000000, // 30 days
        warningPeriod: 604800000, // 7 days
        autoRotate: true,
      },
      rateLimiting: {
        defaultStrategy: RateLimitStrategy.SLIDING_WINDOW,
        defaultWindow: 900000, // 15 minutes
        defaultLimit: 1000,
        enableBurst: true,
        burstMultiplier: 2,
      },
      gateway: {
        enabled: true,
        port: 8080,
        host: '0.0.0.0',
        ssl: {
          enabled: false,
        },
        cors: {
          enabled: true,
          allowedOrigins: ['*'],
          allowedMethods: ['GET', 'POST', 'PUT', 'DELETE', 'PATCH', 'OPTIONS'],
          allowedHeaders: ['Content-Type', 'Authorization', 'X-API-Key'],
          exposedHeaders: ['X-RateLimit-Limit', 'X-RateLimit-Remaining', 'X-RateLimit-Reset'],
          credentials: true,
          maxAge: 86400,
        },
        compression: {
          enabled: true,
          level: 6,
        },
        cache: {
          enabled: true,
          driver: 'memory',
          config: {},
        },
      },
      security: {
        enabled: true,
        suspiciousIPThreshold: 100,
        rateLimitThreshold: 10,
        anomalyDetection: true,
        threatIntelligence: true,
        blockMaliciousIPs: true,
        logSecurityEvents: true,
      },
      analytics: {
        enabled: true,
        retention: 2592000000, // 30 days
        aggregation: {
          enabled: true,
          interval: 300000, // 5 minutes
        },
        export: {
          enabled: false,
          format: 'json',
          destination: '',
          interval: 3600000, // 1 hour
        },
        dashboard: {
          enabled: true,
          refreshInterval: 30000, // 30 seconds
        },
      },
      ...config,
    };

    this.setupDefaultRoutes();
    this.setupEventHandlers();
  }

  setHealthMonitor(monitor: EnterpriseHealthMonitor): void {
    this.healthMonitor = monitor;
  }

  private setupEventHandlers(): void {
    this.on('api:request', (request: APIRequestContext) => {
      this.recordAnalyticsEvent({
        id: this.generateId(),
        requestId: request.id,
        timestamp: request.timestamp,
        eventType: 'request',
        method: request.method,
        path: request.path,
        metadata: {
          clientIP: request.clientIP,
          userAgent: request.userAgent,
          apiKeyId: request.apiKey?.id,
        },
      });
    });

    this.on('api:response', (response: APIResponseContext, request: APIRequestContext) => {
      this.recordAnalyticsEvent({
        id: this.generateId(),
        requestId: request.id,
        timestamp: response.timestamp,
        eventType: 'response',
        method: request.method,
        path: request.path,
        status: response.status,
        duration: response.duration,
        metadata: {
          cached: response.cached,
          responseSize: response.size,
        },
      });
    });

    this.on('api:error', (error: Error, request: APIRequestContext) => {
      this.recordAnalyticsEvent({
        id: this.generateId(),
        requestId: request.id,
        timestamp: Date.now(),
        eventType: 'error',
        method: request.method,
        path: request.path,
        error: error.message,
        metadata: {
          clientIP: request.clientIP,
          userAgent: request.userAgent,
        },
      });
    });

    this.on('security:event', (event: any) => {
      if (this.config.security.logSecurityEvents) {
        this.recordAnalyticsEvent({
          id: this.generateId(),
          requestId: event.requestId || '',
          timestamp: Date.now(),
          eventType: 'security',
          method: event.method || APIMethod.GET,
          path: event.path || '/',
          securityEvent: event,
          metadata: event.details || {},
        });
      }
    });
  }

  private setupDefaultRoutes(): void {
    // Health check route
    this.registerRoute({
      id: 'health',
      path: '/health',
      method: APIMethod.GET,
      authRequired: false,
      permissions: [],
      handler: async request => {
        return {
          id: this.generateId(),
          status: 200,
          headers: { 'Content-Type': 'application/json' },
          body: {
            status: 'healthy',
            timestamp: Date.now(),
            version: '2.0.0',
          },
          timestamp: Date.now(),
          duration: 0,
          size: 0,
          cached: false,
        };
      },
      metadata: {
        description: 'Health check endpoint',
        category: 'system',
      },
    });

    // API keys management route (protected)
    this.registerRoute({
      id: 'api-keys',
      path: '/api/keys',
      method: APIMethod.GET,
      authRequired: true,
      permissions: ['api_keys:read'],
      handler: async request => {
        const keys = Array.from(this.apiKeys.values()).map(key => ({
          id: key.id,
          name: key.name,
          type: key.type,
          status: key.status,
          createdAt: key.createdAt,
          expiresAt: key.expiresAt,
          lastUsed: key.lastUsed,
          usageCount: key.usageCount,
        }));

        return {
          id: this.generateId(),
          status: 200,
          headers: { 'Content-Type': 'application/json' },
          body: { keys },
          timestamp: Date.now(),
          duration: 0,
          size: 0,
          cached: false,
        };
      },
      metadata: {
        description: 'List API keys',
        category: 'management',
      },
    });

    // Analytics route (protected)
    this.registerRoute({
      id: 'analytics',
      path: '/api/analytics',
      method: APIMethod.GET,
      authRequired: true,
      permissions: ['analytics:read'],
      handler: async request => {
        const analytics = this.getAnalyticsSummary();

        return {
          id: this.generateId(),
          status: 200,
          headers: { 'Content-Type': 'application/json' },
          body: analytics,
          timestamp: Date.now(),
          duration: 0,
          size: 0,
          cached: false,
        };
      },
      metadata: {
        description: 'Get API analytics summary',
        category: 'analytics',
      },
    });
  }

  async start(): Promise<void> {
    if (this.isRunning) return;

    this.isRunning = true;
    this.emit('starting');

    // Start key rotation
    if (this.config.keyRotation.enabled) {
      this.startKeyRotation();
    }

    // Start cleanup tasks
    this.startCleanupTasks();

    // Start analytics aggregation
    if (this.config.analytics.aggregation.enabled) {
      this.startAnalyticsAggregation();
    }

    // Start API gateway if enabled
    if (this.config.gateway.enabled) {
      await this.startAPIGateway();
    }

    this.emit('started');
  }

  async stop(): Promise<void> {
    if (!this.isRunning) return;

    this.isRunning = false;
    this.emit('stopping');

    // Stop timers
    if (this.rotationTimer) {
      clearInterval(this.rotationTimer);
    }
    if (this.cleanupTimer) {
      clearInterval(this.cleanupTimer);
    }
    if (this.analyticsTimer) {
      clearInterval(this.analyticsTimer);
    }

    this.emit('stopped');
  }

  private startKeyRotation(): void {
    this.rotationTimer = setInterval(async () => {
      await this.rotateKeys();
    }, this.config.keyRotation.defaultInterval);
  }

  private async rotateKeys(): Promise<void> {
    const now = Date.now();
    const keysToRotate = Array.from(this.apiKeys.values()).filter(
      key =>
        key.rotationConfig?.enabled &&
        key.rotationConfig.nextRotation &&
        key.rotationConfig.nextRotation <= now
    );

    for (const key of keysToRotate) {
      try {
        await this.rotateKey(key.id);
      } catch (error) {
        console.error(`Failed to rotate key ${key.id}:`, error);
      }
    }
  }

  private async rotateKey(keyId: string): Promise<void> {
    const key = this.apiKeys.get(keyId);
    if (!key) {
      throw new Error(`Key ${keyId} not found`);
    }

    // Generate new key and secret
    const newKey = this.generateAPIKey();
    const newSecret = this.generateAPISecret();

    // Update key
    key.key = newKey;
    key.secret = newSecret;
    key.lastUsed = Date.now();
    key.rotationConfig = {
      ...key.rotationConfig!,
      nextRotation: Date.now() + this.config.keyRotation.defaultInterval,
    };

    this.apiKeys.set(keyId, key);
    this.emit('key:rotated', key);
  }

  private startCleanupTasks(): void {
    this.cleanupTimer = setInterval(() => {
      this.cleanupExpiredKeys();
      this.cleanupAnalyticsEvents();
      this.cleanupRateLimitStates();
      this.cleanupCache();
    }, 3600000); // Clean up every hour
  }

  private cleanupExpiredKeys(): void {
    const now = Date.now();
    for (const [keyId, key] of this.apiKeys.entries()) {
      if (key.expiresAt && key.expiresAt <= now) {
        key.status = APIKeyStatus.EXPIRED;
        this.apiKeys.set(keyId, key);
        this.emit('key:expired', key);
      }
    }
  }

  private cleanupAnalyticsEvents(): void {
    const cutoff = Date.now() - this.config.analytics.retention;
    this.analyticsEvents = this.analyticsEvents.filter(event => event.timestamp > cutoff);
  }

  private cleanupRateLimitStates(): void {
    const now = Date.now();
    for (const [key, state] of this.rateLimitStates.entries()) {
      if (now - state.windowStart > this.config.rateLimiting.defaultWindow * 2) {
        this.rateLimitStates.delete(key);
      }
    }
  }

  private cleanupCache(): void {
    const now = Date.now();
    for (const [key, value] of this.cache.entries()) {
      if (value.expires <= now) {
        this.cache.delete(key);
      }
    }
  }

  private startAnalyticsAggregation(): void {
    this.analyticsTimer = setInterval(() => {
      this.aggregateAnalytics();
    }, this.config.analytics.aggregation.interval);
  }

  private aggregateAnalytics(): void {
    const now = Date.now();
    const windowStart = now - this.config.analytics.aggregation.interval;

    const windowEvents = this.analyticsEvents.filter(
      event => event.timestamp >= windowStart && event.timestamp <= now
    );

    const summary = {
      window: { start: windowStart, end: now },
      totalRequests: windowEvents.filter(e => e.eventType === 'request').length,
      totalResponses: windowEvents.filter(e => e.eventType === 'response').length,
      totalErrors: windowEvents.filter(e => e.eventType === 'error').length,
      totalSecurityEvents: windowEvents.filter(e => e.eventType === 'security').length,
      averageResponseTime: this.calculateAverageResponseTime(windowEvents),
      topEndpoints: this.getTopEndpoints(windowEvents),
      errorRate: this.calculateErrorRate(windowEvents),
      statusCodes: this.getStatusCodeDistribution(windowEvents),
    };

    this.emit('analytics:aggregated', summary);
  }

  private calculateAverageResponseTime(events: APIAnalyticsEvent[]): number {
    const responseEvents = events.filter(e => e.eventType === 'response' && e.duration);
    if (responseEvents.length === 0) return 0;

    const totalDuration = responseEvents.reduce((sum, e) => sum + (e.duration || 0), 0);
    return totalDuration / responseEvents.length;
  }

  private getTopEndpoints(events: APIAnalyticsEvent[]): Array<{ path: string; count: number }> {
    const endpointCounts = new Map<string, number>();

    events.forEach(event => {
      const count = endpointCounts.get(event.path) || 0;
      endpointCounts.set(event.path, count + 1);
    });

    return Array.from(endpointCounts.entries())
      .map(([path, count]) => ({ path, count }))
      .sort((a, b) => b.count - a.count)
      .slice(0, 10);
  }

  private calculateErrorRate(events: APIAnalyticsEvent[]): number {
    const totalRequests = events.filter(e => e.eventType === 'request').length;
    const totalErrors = events.filter(e => e.eventType === 'error').length;

    if (totalRequests === 0) return 0;
    return (totalErrors / totalRequests) * 100;
  }

  private getStatusCodeDistribution(events: APIAnalyticsEvent[]): Record<number, number> {
    const distribution: Record<number, number> = {};

    events
      .filter(e => e.eventType === 'response' && e.status)
      .forEach(event => {
        const status = event.status!;
        distribution[status] = (distribution[status] || 0) + 1;
      });

    return distribution;
  }

  private async startAPIGateway(): Promise<void> {
    // Implementation would start HTTP server
    // This is a simplified version
    console.log(`API Gateway starting on ${this.config.gateway.host}:${this.config.gateway.port}`);
  }

  // API Key Management
  createAPIKey(options: {
    name: string;
    type: APIKeyType;
    permissions: string[];
    expiresAt?: number;
    allowedIPs?: string[];
    allowedOrigins?: string[];
    metadata?: Record<string, any>;
  }): APIKey {
    const key: APIKey = {
      id: this.generateId(),
      name: options.name,
      key: this.generateAPIKey(),
      secret: this.generateAPISecret(),
      type: options.type,
      status: APIKeyStatus.ACTIVE,
      permissions: options.permissions,
      rateLimits: [
        {
          strategy: this.config.rateLimiting.defaultStrategy,
          window: this.config.rateLimiting.defaultWindow,
          limit: this.config.rateLimiting.defaultLimit,
        },
      ],
      allowedIPs: options.allowedIPs || [],
      allowedOrigins: options.allowedOrigins || [],
      createdAt: Date.now(),
      expiresAt: options.expiresAt,
      usageCount: 0,
      metadata: options.metadata || {},
      rotationConfig: {
        enabled: this.config.keyRotation.enabled,
        interval: this.config.keyRotation.defaultInterval,
        nextRotation: Date.now() + this.config.keyRotation.defaultInterval,
      },
    };

    this.apiKeys.set(key.id, key);
    this.emit('key:created', key);
    return key;
  }

  revokeAPIKey(keyId: string): void {
    const key = this.apiKeys.get(keyId);
    if (!key) {
      throw new Error(`Key ${keyId} not found`);
    }

    key.status = APIKeyStatus.REVOKED;
    this.apiKeys.set(keyId, key);
    this.emit('key:revoked', key);
  }

  getAPIKey(keyId: string): APIKey | undefined {
    return this.apiKeys.get(keyId);
  }

  getAPIKeys(): APIKey[] {
    return Array.from(this.apiKeys.values());
  }

  // Route Management
  registerRoute(route: APIRoute): void {
    const routeKey = `${route.method}:${route.path}`;
    this.routes.set(routeKey, route);
    this.emit('route:registered', route);
  }

  getRoute(method: APIMethod, path: string): APIRoute | undefined {
    const routeKey = `${method}:${path}`;
    return this.routes.get(routeKey);
  }

  getRoutes(): APIRoute[] {
    return Array.from(this.routes.values());
  }

  // Request Processing
  async processRequest(request: APIRequestContext): Promise<APIResponseContext> {
    const startTime = Date.now();

    try {
      this.emit('api:request', request);

      // Authenticate request
      if (!(await this.authenticateRequest(request))) {
        return this.createErrorResponse(401, 'Unauthorized', request);
      }

      // Authorize request
      if (!(await this.authorizeRequest(request))) {
        return this.createErrorResponse(403, 'Forbidden', request);
      }

      // Check rate limits
      if (!(await this.checkRateLimits(request))) {
        return this.createErrorResponse(429, 'Too Many Requests', request);
      }

      // Check security
      if (!(await this.checkSecurity(request))) {
        return this.createErrorResponse(403, 'Security Check Failed', request);
      }

      // Get route
      const route = this.getRoute(request.method, request.path);
      if (!route) {
        return this.createErrorResponse(404, 'Not Found', request);
      }

      // Apply request transformation
      let transformedRequest = request;
      if (route.transform?.request) {
        transformedRequest = route.transform.request(request);
      }

      // Check cache
      if (route.cacheConfig?.enabled) {
        const cachedResponse = this.getFromCache(transformedRequest, route);
        if (cachedResponse) {
          const response = {
            ...cachedResponse,
            cached: true,
          };
          this.emit('api:response', response, transformedRequest);
          return response;
        }
      }

      // Process request
      let response = await route.handler(transformedRequest);

      // Apply response transformation
      if (route.transform?.response) {
        response = route.transform.response(response);
      }

      // Update response metadata
      response.duration = Date.now() - startTime;
      response.size = JSON.stringify(response.body).length;

      // Cache response if enabled
      if (route.cacheConfig?.enabled && response.status === 200) {
        this.setToCache(transformedRequest, response, route);
      }

      // Update API key usage
      if (request.apiKey) {
        request.apiKey.lastUsed = Date.now();
        request.apiKey.usageCount++;
        this.apiKeys.set(request.apiKey.id, request.apiKey);
      }

      this.emit('api:response', response, transformedRequest);
      return response;
    } catch (error) {
      const errorResponse = this.createErrorResponse(500, 'Internal Server Error', request);
      this.emit('api:error', error as Error, request);
      return errorResponse;
    }
  }

  private async authenticateRequest(request: APIRequestContext): Promise<boolean> {
    if (!request.headers['authorization'] && !request.headers['x-api-key']) {
      return false;
    }

    const apiKey =
      request.headers['x-api-key'] || request.headers['authorization']?.replace('Bearer ', '');
    if (!apiKey) {
      return false;
    }

    const key = Array.from(this.apiKeys.values()).find(k => k.key === apiKey);
    if (!key || key.status !== APIKeyStatus.ACTIVE) {
      this.emit('security:event', {
        type: 'invalid_key',
        requestId: request.id,
        method: request.method,
        path: request.path,
        details: { apiKey, clientIP: request.clientIP },
      });
      return false;
    }

    // Check expiration
    if (key.expiresAt && key.expiresAt <= Date.now()) {
      key.status = APIKeyStatus.EXPIRED;
      this.apiKeys.set(key.id, key);
      return false;
    }

    // Check allowed IPs
    if (key.allowedIPs.length > 0 && !key.allowedIPs.includes(request.clientIP)) {
      this.emit('security:event', {
        type: 'unauthorized',
        requestId: request.id,
        method: request.method,
        path: request.path,
        details: { clientIP: request.clientIP, allowedIPs: key.allowedIPs },
      });
      return false;
    }

    request.apiKey = key;
    return true;
  }

  private async authorizeRequest(request: APIRequestContext): Promise<boolean> {
    if (!request.apiKey) return false;

    const route = this.getRoute(request.method, request.path);
    if (!route) return false;

    if (!route.authRequired) return true;

    // Check permissions
    for (const permission of route.permissions) {
      if (!request.apiKey.permissions.includes(permission)) {
        return false;
      }
    }

    return true;
  }

  private async checkRateLimits(request: APIRequestContext): Promise<boolean> {
    if (!request.apiKey) return false;

    const rateLimitKey = `${request.apiKey.id}:${request.clientIP}`;
    let state = this.rateLimitStates.get(rateLimitKey);

    if (!state) {
      state = {
        requests: 0,
        windowStart: Date.now(),
      };
      this.rateLimitStates.set(rateLimitKey, state);
    }

    // Reset window if expired
    const now = Date.now();
    if (now - state.windowStart > this.config.rateLimiting.defaultWindow) {
      state.requests = 0;
      state.windowStart = now;
    }

    // Check limit
    if (state.requests >= this.config.rateLimiting.defaultLimit) {
      this.emit('security:event', {
        type: 'rate_limit_exceeded',
        requestId: request.id,
        method: request.method,
        path: request.path,
        details: {
          clientIP: request.clientIP,
          apiKeyId: request.apiKey.id,
          requests: state.requests,
          limit: this.config.rateLimiting.defaultLimit,
        },
      });
      return false;
    }

    state.requests++;
    return true;
  }

  private async checkSecurity(request: APIRequestContext): Promise<boolean> {
    if (!this.config.security.enabled) return true;

    // Check blocked IPs
    if (this.blockedIPs.has(request.clientIP)) {
      return false;
    }

    // Check suspicious activity
    if (this.suspiciousIPs.has(request.clientIP)) {
      const recentEvents = this.analyticsEvents.filter(
        event =>
          event.metadata?.clientIP === request.clientIP && event.timestamp > Date.now() - 3600000 // Last hour
      ).length;

      if (recentEvents > this.config.security.suspiciousIPThreshold) {
        if (this.config.security.blockMaliciousIPs) {
          this.blockedIPs.add(request.clientIP);
        }
        return false;
      }
    }

    return true;
  }

  private getFromCache(request: APIRequestContext, route: APIRoute): APIResponseContext | null {
    if (!route.cacheConfig?.enabled) return null;

    const cacheKey = route.cacheConfig.keyGenerator
      ? route.cacheConfig.keyGenerator(request)
      : `${request.method}:${request.path}:${JSON.stringify(request.query)}`;

    const cached = this.cache.get(cacheKey);
    if (cached && cached.expires > Date.now()) {
      return cached.data;
    }

    this.cache.delete(cacheKey);
    return null;
  }

  private setToCache(
    request: APIRequestContext,
    response: APIResponseContext,
    route: APIRoute
  ): void {
    if (!route.cacheConfig?.enabled) return;

    const cacheKey = route.cacheConfig.keyGenerator
      ? route.cacheConfig.keyGenerator(request)
      : `${request.method}:${request.path}:${JSON.stringify(request.query)}`;

    const expires = Date.now() + route.cacheConfig.ttl;
    this.cache.set(cacheKey, { data: response, expires });
  }

  private createErrorResponse(
    status: number,
    message: string,
    request: APIRequestContext
  ): APIResponseContext {
    return {
      id: this.generateId(),
      status,
      headers: { 'Content-Type': 'application/json' },
      body: { error: message, timestamp: Date.now() },
      timestamp: Date.now(),
      duration: 0,
      size: 0,
      cached: false,
    };
  }

  private recordAnalyticsEvent(event: APIAnalyticsEvent): void {
    this.analyticsEvents.push(event);

    // Keep only recent events
    const cutoff = Date.now() - this.config.analytics.retention;
    this.analyticsEvents = this.analyticsEvents.filter(e => e.timestamp > cutoff);
  }

  private generateId(): string {
    return Math.random().toString(36).substr(2, 9) + Date.now().toString(36);
  }

  private generateAPIKey(): string {
    return `ak_${Math.random().toString(36).substr(2, 32)}`;
  }

  private generateAPISecret(): string {
    return `as_${Math.random().toString(36).substr(2, 64)}`;
  }

  // Public API methods
  getAnalyticsSummary(): any {
    const now = Date.now();
    const last24Hours = this.analyticsEvents.filter(e => e.timestamp > now - 86400000);

    return {
      totalRequests: last24Hours.filter(e => e.eventType === 'request').length,
      totalResponses: last24Hours.filter(e => e.eventType === 'response').length,
      totalErrors: last24Hours.filter(e => e.eventType === 'error').length,
      averageResponseTime: this.calculateAverageResponseTime(last24Hours),
      errorRate: this.calculateErrorRate(last24Hours),
      activeKeys: Array.from(this.apiKeys.values()).filter(k => k.status === APIKeyStatus.ACTIVE)
        .length,
      totalKeys: this.apiKeys.size,
      blockedIPs: this.blockedIPs.size,
      suspiciousIPs: this.suspiciousIPs.size,
    };
  }

  getAnalyticsEvents(eventType?: string, limit?: number): APIAnalyticsEvent[] {
    let events = this.analyticsEvents;

    if (eventType) {
      events = events.filter(e => e.eventType === eventType);
    }

    events = events.sort((a, b) => b.timestamp - a.timestamp);

    if (limit) {
      events = events.slice(0, limit);
    }

    return events;
  }

  getRateLimitStates(): Map<string, RateLimitState> {
    return new Map(this.rateLimitStates);
  }

  getCacheStats(): { size: number; hitRate: number } {
    const totalRequests = this.analyticsEvents.filter(e => e.eventType === 'response').length;
    const cachedRequests = this.analyticsEvents.filter(
      e => e.eventType === 'response' && e.metadata?.cached
    ).length;

    return {
      size: this.cache.size,
      hitRate: totalRequests > 0 ? (cachedRequests / totalRequests) * 100 : 0,
    };
  }
}

// Factory function to create enterprise API manager
export function createEnterpriseAPIManager(
  environment: EnterpriseEnvironmentConfig,
  config?: Partial<APIManagerConfig>
): EnterpriseAPIManager {
  return new EnterpriseAPIManager(environment, config);
}

/**
 * Premium Diamond-Grade Professional Enterprise Service Container
 *
 * This module implements a comprehensive service container with dependency injection,
 * lifecycle management, and enterprise-grade features for robust service management.
 *
 * Features:
 * - Dependency injection with automatic resolution
 * - Service lifecycle management (singleton, transient, scoped)
 * - Circular dependency detection and prevention
 * - Service health monitoring and metrics
 * - Graceful shutdown and error recovery
 * - Service discovery and registration
 * - Event-driven architecture for service communication
 * - Performance monitoring and optimization
 * - Thread-safe service access
 * - Configuration-driven service initialization
 *
 * @author: Enterprise Architecture Team
 * @version: 2.0.0
 * @compliance: Enterprise Service Standards
 */

import { EventEmitter } from 'events';
import { EnterpriseEnvironmentConfig } from '../environment/EnterpriseEnvironmentConfig';

// Service lifecycle states
export enum ServiceLifecycle {
  REGISTERED = 'REGISTERED',
  INITIALIZING = 'INITIALIZING',
  INITIALIZED = 'INITIALIZED',
  STARTING = 'STARTING',
  RUNNING = 'RUNNING',
  STOPPING = 'STOPPING',
  STOPPED = 'STOPPED',
  DISPOSING = 'DISPOSING',
  DISPOSED = 'DISPOSED',
  ERROR = 'ERROR',
}

// Service scope types
export enum ServiceScope {
  SINGLETON = 'SINGLETON', // Single instance for entire application
  TRANSIENT = 'TRANSIENT', // New instance for each request
  SCOPED = 'SCOPED', // Single instance per scope
}

// Service metadata
export interface ServiceMetadata {
  name: string;
  version: string;
  description?: string;
  author?: string;
  dependencies: string[];
  tags: string[];
  scope: ServiceScope;
  priority: number;
  timeout: number;
  retryCount: number;
  healthCheckEnabled: boolean;
  metricsEnabled: boolean;
}

// Service health status
export interface ServiceHealth {
  status: 'HEALTHY' | 'DEGRADED' | 'UNHEALTHY' | 'UNKNOWN';
  timestamp: number;
  uptime: number;
  memoryUsage: number;
  cpuUsage?: number;
  lastError?: Error;
  metrics: Record<string, number>;
  checks: {
    name: string;
    status: 'PASS' | 'FAIL' | 'WARN';
    message?: string;
    timestamp: number;
  }[];
}

// Service interface
export interface IService {
  readonly name: string;
  readonly metadata: ServiceMetadata;
  initialize?(): Promise<void>;
  start?(): Promise<void>;
  stop?(): Promise<void>;
  dispose?(): Promise<void>;
  healthCheck?(): Promise<ServiceHealth>;
  getMetrics?(): Promise<Record<string, number>>;
}

// Service factory function
export type ServiceFactory<T extends IService = IService> = (
  container: EnterpriseServiceContainer
) => Promise<T> | T;

// Service descriptor
export interface ServiceDescriptor<T extends IService = IService> {
  name: string;
  factory: ServiceFactory<T>;
  metadata: Partial<ServiceMetadata>;
  dependencies?: string[];
  config?: Record<string, any>;
}

// Service instance wrapper
interface ServiceInstance<T extends IService = IService> {
  service: T;
  metadata: ServiceMetadata;
  lifecycle: ServiceLifecycle;
  createdAt: number;
  startedAt?: number;
  stoppedAt?: number;
  lastHealthCheck?: number;
  health?: ServiceHealth;
  metrics: Record<string, number>;
  errorCount: number;
  lastError?: Error;
}

// Scope context
export interface ScopeContext {
  id: string;
  parentId?: string;
  createdAt: number;
  services: Map<string, IService>;
  metadata: Record<string, any>;
}

// Container configuration
export interface ContainerConfig {
  enableCircularDependencyCheck: boolean;
  enableHealthMonitoring: boolean;
  enableMetrics: boolean;
  enableGracefulShutdown: boolean;
  shutdownTimeout: number;
  healthCheckInterval: number;
  metricsInterval: number;
  maxServiceInstances: number;
  enableServiceDiscovery: boolean;
  enableEventBus: boolean;
}

// Enterprise service container
export class EnterpriseServiceContainer extends EventEmitter {
  private services: Map<string, ServiceDescriptor> = new Map();
  private instances: Map<string, ServiceInstance> = new Map();
  private scopes: Map<string, ScopeContext> = new Map();
  private config: ContainerConfig;
  private environment: EnterpriseEnvironmentConfig;
  private healthCheckTimer?: NodeJS.Timeout;
  private metricsTimer?: NodeJS.Timeout;
  private isInitialized = false;
  private isShuttingDown = false;
  private initializationPromises: Map<string, Promise<any>> = new Map();
  private dependencyGraph: Map<string, Set<string>> = new Map();
  private eventBus: EventEmitter;

  constructor(environment: EnterpriseEnvironmentConfig, config: Partial<ContainerConfig> = {}) {
    super();
    this.environment = environment;
    this.config = {
      enableCircularDependencyCheck: true,
      enableHealthMonitoring: true,
      enableMetrics: true,
      enableGracefulShutdown: true,
      shutdownTimeout: 30000,
      healthCheckInterval: 60000,
      metricsInterval: 30000,
      maxServiceInstances: 1000,
      enableServiceDiscovery: true,
      enableEventBus: true,
      ...config,
    };
    this.eventBus = new EventEmitter();
    this.setupEventHandlers();
    this.setupGracefulShutdown();
  }

  private setupEventHandlers(): void {
    this.eventBus.on('service:registered', (descriptor: ServiceDescriptor) => {
      this.emit('service:registered', descriptor);
    });

    this.eventBus.on('service:initialized', (instance: ServiceInstance) => {
      this.emit('service:initialized', instance);
    });

    this.eventBus.on('service:started', (instance: ServiceInstance) => {
      this.emit('service:started', instance);
    });

    this.eventBus.on('service:stopped', (instance: ServiceInstance) => {
      this.emit('service:stopped', instance);
    });

    this.eventBus.on('service:error', (instance: ServiceInstance, error: Error) => {
      this.emit('service:error', instance, error);
    });

    this.eventBus.on('service:health:changed', (instance: ServiceInstance) => {
      this.emit('service:health:changed', instance);
    });
  }

  private setupGracefulShutdown(): void {
    if (!this.config.enableGracefulShutdown) return;

    const shutdown = async (signal: string) => {
      console.log(`Received ${signal}, initiating graceful shutdown...`);
      await this.shutdown();
      process.exit(0);
    };

    process.on('SIGTERM', () => shutdown('SIGTERM'));
    process.on('SIGINT', () => shutdown('SIGINT'));
    process.on('SIGUSR2', () => shutdown('SIGUSR2')); // For nodemon
  }

  async initialize(): Promise<void> {
    if (this.isInitialized) return;

    try {
      this.emit('initializing');

      // Initialize all registered services
      const initializationPromises = Array.from(this.services.values()).map(async descriptor => {
        try {
          await this.initializeService(descriptor.name);
        } catch (error) {
          console.error(`Failed to initialize service ${descriptor.name}:`, error);
          this.eventBus.emit('service:error', this.instances.get(descriptor.name), error as Error);
        }
      });

      await Promise.all(initializationPromises);

      // Start health monitoring
      if (this.config.enableHealthMonitoring) {
        this.startHealthMonitoring();
      }

      // Start metrics collection
      if (this.config.enableMetrics) {
        this.startMetricsCollection();
      }

      this.isInitialized = true;
      this.emit('initialized');
    } catch (error) {
      this.emit('error', error);
      throw error;
    }
  }

  register<T extends IService>(descriptor: ServiceDescriptor<T>): void {
    if (this.services.has(descriptor.name)) {
      throw new Error(`Service ${descriptor.name} is already registered`);
    }

    // Complete metadata with defaults
    const metadata: ServiceMetadata = {
      name: descriptor.name,
      version: '1.0.0',
      description: '',
      author: '',
      dependencies: descriptor.dependencies || [],
      tags: [],
      scope: ServiceScope.SINGLETON,
      priority: 0,
      timeout: 30000,
      retryCount: 3,
      healthCheckEnabled: true,
      metricsEnabled: true,
      ...descriptor.metadata,
    };

    const completeDescriptor: ServiceDescriptor<T> = {
      ...descriptor,
      metadata,
    };

    this.services.set(descriptor.name, completeDescriptor);

    // Update dependency graph
    this.updateDependencyGraph(descriptor.name, metadata.dependencies);

    // Check for circular dependencies
    if (this.config.enableCircularDependencyCheck) {
      this.checkCircularDependencies(descriptor.name);
    }

    this.eventBus.emit('service:registered', completeDescriptor);
  }

  private updateDependencyGraph(serviceName: string, dependencies: string[]): void {
    this.dependencyGraph.set(serviceName, new Set(dependencies));
  }

  private checkCircularDependencies(serviceName: string): void {
    const visited = new Set<string>();
    const recursionStack = new Set<string>();

    const hasCycle = (node: string): boolean => {
      if (recursionStack.has(node)) return true;
      if (visited.has(node)) return false;

      visited.add(node);
      recursionStack.add(node);

      const dependencies = this.dependencyGraph.get(node) || new Set();
      for (const dep of dependencies) {
        if (hasCycle(dep)) return true;
      }

      recursionStack.delete(node);
      return false;
    };

    if (hasCycle(serviceName)) {
      throw new Error(`Circular dependency detected involving service: ${serviceName}`);
    }
  }

  async initializeService(serviceName: string): Promise<void> {
    if (this.initializationPromises.has(serviceName)) {
      return this.initializationPromises.get(serviceName);
    }

    const descriptor = this.services.get(serviceName);
    if (!descriptor) {
      throw new Error(`Service ${serviceName} not found`);
    }

    const initPromise = this.doInitializeService(descriptor);
    this.initializationPromises.set(serviceName, initPromise);

    try {
      await initPromise;
    } finally {
      this.initializationPromises.delete(serviceName);
    }
  }

  private async doInitializeService(descriptor: ServiceDescriptor): Promise<void> {
    const instance = this.instances.get(descriptor.name);
    if (instance && instance.lifecycle === ServiceLifecycle.INITIALIZED) {
      return;
    }

    try {
      // Create instance wrapper
      const wrapper: ServiceInstance = {
        service: null as any,
        metadata: descriptor.metadata,
        lifecycle: ServiceLifecycle.INITIALIZING,
        createdAt: Date.now(),
        metrics: {},
        errorCount: 0,
      };

      this.instances.set(descriptor.name, wrapper);
      this.eventBus.emit('service:initializing', wrapper);

      // Initialize dependencies first
      for (const depName of descriptor.metadata.dependencies) {
        await this.initializeService(depName);
      }

      // Create service instance
      wrapper.service = await descriptor.factory(this);
      wrapper.lifecycle = ServiceLifecycle.INITIALIZED;

      // Call initialize method if it exists
      if (wrapper.service.initialize) {
        await wrapper.service.initialize();
      }

      this.eventBus.emit('service:initialized', wrapper);

      // Start the service if auto-start is enabled
      if (this.isInitialized) {
        await this.startService(descriptor.name);
      }
    } catch (error) {
      const instance = this.instances.get(descriptor.name);
      if (instance) {
        instance.lifecycle = ServiceLifecycle.ERROR;
        instance.lastError = error as Error;
        instance.errorCount++;
      }
      throw error;
    }
  }

  async startService(serviceName: string): Promise<void> {
    const instance = this.instances.get(serviceName);
    if (!instance) {
      throw new Error(`Service ${serviceName} not found`);
    }

    if (instance.lifecycle === ServiceLifecycle.RUNNING) {
      return;
    }

    try {
      instance.lifecycle = ServiceLifecycle.STARTING;
      this.eventBus.emit('service:starting', instance);

      if (instance.service.start) {
        await instance.service.start();
      }

      instance.lifecycle = ServiceLifecycle.RUNNING;
      instance.startedAt = Date.now();
      this.eventBus.emit('service:started', instance);
    } catch (error) {
      instance.lifecycle = ServiceLifecycle.ERROR;
      instance.lastError = error as Error;
      instance.errorCount++;
      throw error;
    }
  }

  async stopService(serviceName: string): Promise<void> {
    const instance = this.instances.get(serviceName);
    if (!instance) {
      throw new Error(`Service ${serviceName} not found`);
    }

    if (instance.lifecycle === ServiceLifecycle.STOPPED) {
      return;
    }

    try {
      instance.lifecycle = ServiceLifecycle.STOPPING;
      this.eventBus.emit('service:stopping', instance);

      if (instance.service.stop) {
        await instance.service.stop();
      }

      instance.lifecycle = ServiceLifecycle.STOPPED;
      instance.stoppedAt = Date.now();
      this.eventBus.emit('service:stopped', instance);
    } catch (error) {
      instance.lifecycle = ServiceLifecycle.ERROR;
      instance.lastError = error as Error;
      instance.errorCount++;
      throw error;
    }
  }

  async getService<T extends IService>(serviceName: string, scopeId?: string): Promise<T> {
    const descriptor = this.services.get(serviceName);
    if (!descriptor) {
      throw new Error(`Service ${serviceName} not found`);
    }

    // Handle scoped services
    if (descriptor.metadata.scope === ServiceScope.SCOPED && scopeId) {
      return this.getScopedService<T>(serviceName, scopeId);
    }

    // Handle transient services
    if (descriptor.metadata.scope === ServiceScope.TRANSIENT) {
      return this.createTransientService<T>(descriptor);
    }

    // Handle singleton services
    const instance = this.instances.get(serviceName);
    if (!instance || instance.lifecycle === ServiceLifecycle.ERROR) {
      await this.initializeService(serviceName);
      return this.getService<T>(serviceName, scopeId);
    }

    return instance.service as T;
  }

  private getScopedService<T extends IService>(serviceName: string, scopeId: string): T {
    let scope = this.scopes.get(scopeId);
    if (!scope) {
      scope = this.createScope(scopeId);
      this.scopes.set(scopeId, scope);
    }

    if (scope.services.has(serviceName)) {
      return scope.services.get(serviceName) as T;
    }

    // Create new instance for this scope
    const descriptor = this.services.get(serviceName)!;
    const service = descriptor.factory(this);
    scope.services.set(serviceName, service);

    return service as T;
  }

  private createTransientService<T extends IService>(descriptor: ServiceDescriptor<T>): T {
    return descriptor.factory(this);
  }

  createScope(scopeId?: string): ScopeContext {
    const id = scopeId || this.generateScopeId();
    const scope: ScopeContext = {
      id,
      createdAt: Date.now(),
      services: new Map(),
      metadata: {},
    };

    this.scopes.set(id, scope);
    return scope;
  }

  destroyScope(scopeId: string): void {
    const scope = this.scopes.get(scopeId);
    if (scope) {
      // Dispose all scoped services
      for (const [name, service] of scope.services) {
        if (service.dispose) {
          service.dispose();
        }
      }
      this.scopes.delete(scopeId);
    }
  }

  private generateScopeId(): string {
    return `scope_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  async getServiceHealth(serviceName: string): Promise<ServiceHealth> {
    const instance = this.instances.get(serviceName);
    if (!instance) {
      throw new Error(`Service ${serviceName} not found`);
    }

    if (instance.service.healthCheck) {
      try {
        const health = await instance.service.healthCheck();
        instance.health = health;
        instance.lastHealthCheck = Date.now();
        return health;
      } catch (error) {
        return {
          status: 'UNHEALTHY',
          timestamp: Date.now(),
          uptime: Date.now() - instance.createdAt,
          memoryUsage: process.memoryUsage().heapUsed,
          lastError: error as Error,
          metrics: instance.metrics,
          checks: [
            {
              name: 'health_check',
              status: 'FAIL',
              message: (error as Error).message,
              timestamp: Date.now(),
            },
          ],
        };
      }
    }

    // Default health check
    const uptime = Date.now() - instance.createdAt;
    return {
      status: instance.lifecycle === ServiceLifecycle.RUNNING ? 'HEALTHY' : 'UNKNOWN',
      timestamp: Date.now(),
      uptime,
      memoryUsage: process.memoryUsage().heapUsed,
      metrics: instance.metrics,
      checks: [
        {
          name: 'lifecycle',
          status: instance.lifecycle === ServiceLifecycle.RUNNING ? 'PASS' : 'FAIL',
          message: `Service is ${instance.lifecycle}`,
          timestamp: Date.now(),
        },
      ],
    };
  }

  async getAllServicesHealth(): Promise<Record<string, ServiceHealth>> {
    const health: Record<string, ServiceHealth> = {};

    for (const serviceName of this.services.keys()) {
      try {
        health[serviceName] = await this.getServiceHealth(serviceName);
      } catch (error) {
        health[serviceName] = {
          status: 'UNHEALTHY',
          timestamp: Date.now(),
          uptime: 0,
          memoryUsage: 0,
          lastError: error as Error,
          metrics: {},
          checks: [
            {
              name: 'health_check',
              status: 'FAIL',
              message: (error as Error).message,
              timestamp: Date.now(),
            },
          ],
        };
      }
    }

    return health;
  }

  private startHealthMonitoring(): void {
    this.healthCheckTimer = setInterval(async () => {
      try {
        const healthStatus = await this.getAllServicesHealth();
        this.emit('health:check', healthStatus);

        // Check for unhealthy services
        for (const [serviceName, health] of Object.entries(healthStatus)) {
          const instance = this.instances.get(serviceName);
          if (instance && instance.health?.status !== health.status) {
            instance.health = health;
            this.eventBus.emit('service:health:changed', instance);
          }
        }
      } catch (error) {
        console.error('Health check failed:', error);
      }
    }, this.config.healthCheckInterval);
  }

  private startMetricsCollection(): void {
    this.metricsTimer = setInterval(async () => {
      try {
        const metrics: Record<string, Record<string, number>> = {};

        for (const [serviceName, instance] of this.instances) {
          if (instance.service.getMetrics) {
            try {
              const serviceMetrics = await instance.service.getMetrics();
              metrics[serviceName] = { ...instance.metrics, ...serviceMetrics };
              instance.metrics = metrics[serviceName];
            } catch (error) {
              console.error(`Failed to get metrics for service ${serviceName}:`, error);
            }
          }
        }

        this.emit('metrics:collected', metrics);
      } catch (error) {
        console.error('Metrics collection failed:', error);
      }
    }, this.config.metricsInterval);
  }

  async shutdown(): Promise<void> {
    if (this.isShuttingDown) return;
    this.isShuttingDown = true;

    try {
      this.emit('shutting_down');

      // Stop health monitoring and metrics collection
      if (this.healthCheckTimer) {
        clearInterval(this.healthCheckTimer);
      }
      if (this.metricsTimer) {
        clearInterval(this.metricsTimer);
      }

      // Stop all services in reverse priority order
      const services = Array.from(this.instances.values()).sort(
        (a, b) => b.metadata.priority - a.metadata.priority
      );

      const stopPromises = services.map(async instance => {
        try {
          await this.stopService(instance.service.name);
        } catch (error) {
          console.error(`Failed to stop service ${instance.service.name}:`, error);
        }
      });

      await Promise.all(stopPromises);

      // Dispose all services
      const disposePromises = services.map(async instance => {
        try {
          if (instance.service.dispose) {
            await instance.service.dispose();
          }
          instance.lifecycle = ServiceLifecycle.DISPOSED;
        } catch (error) {
          console.error(`Failed to dispose service ${instance.service.name}:`, error);
        }
      });

      await Promise.all(disposePromises);

      // Destroy all scopes
      for (const scopeId of this.scopes.keys()) {
        this.destroyScope(scopeId);
      }

      this.emit('shutdown_complete');
    } catch (error) {
      this.emit('shutdown_error', error);
      throw error;
    }
  }

  getRegisteredServices(): string[] {
    return Array.from(this.services.keys());
  }

  getRunningServices(): string[] {
    return Array.from(this.instances.values())
      .filter(instance => instance.lifecycle === ServiceLifecycle.RUNNING)
      .map(instance => instance.service.name);
  }

  getServiceMetadata(serviceName: string): ServiceMetadata | undefined {
    return this.services.get(serviceName)?.metadata;
  }

  getServiceInstance(serviceName: string): ServiceInstance | undefined {
    return this.instances.get(serviceName);
  }

  emit(eventName: string, ...args: any[]): boolean {
    if (this.config.enableEventBus) {
      this.eventBus.emit(eventName, ...args);
    }
    return super.emit(eventName, ...args);
  }

  on(eventName: string, listener: (...args: any[]) => void): this {
    if (this.config.enableEventBus) {
      this.eventBus.on(eventName, listener);
    }
    return super.on(eventName, listener);
  }

  off(eventName: string, listener: (...args: any[]) => void): this {
    if (this.config.enableEventBus) {
      this.eventBus.off(eventName, listener);
    }
    return super.off(eventName, listener);
  }
}

// Factory function to create enterprise service container
export function createEnterpriseServiceContainer(
  environment: EnterpriseEnvironmentConfig,
  config?: Partial<ContainerConfig>
): EnterpriseServiceContainer {
  return new EnterpriseServiceContainer(environment, config);
}

// Global container instance
let globalContainer: EnterpriseServiceContainer;

export function getGlobalContainer(): EnterpriseServiceContainer {
  if (!globalContainer) {
    throw new Error('Global service container not initialized');
  }
  return globalContainer;
}

export function setGlobalContainer(container: EnterpriseServiceContainer): void {
  globalContainer = container;
}

// Decorator for service registration
export function Service(options: Partial<ServiceMetadata> = {}) {
  return function <T extends { new (...args: any[]): IService }>(constructor: T) {
    const serviceName = options.name || constructor.name.toLowerCase();

    return class extends constructor {
      static readonly serviceName = serviceName;
      static readonly metadata = {
        name: serviceName,
        version: '1.0.0',
        ...options,
      } as ServiceMetadata;
    };
  };
}

// Decorator for dependency injection
export function Inject(serviceName: string) {
  return function (target: any, propertyKey: string, descriptor: PropertyDescriptor) {
    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      const container = getGlobalContainer();
      const service = await container.getService(serviceName);
      return originalMethod.apply(this, [service, ...args]);
    };

    return descriptor;
  };
}

