import { EventEmitter } from 'events';
import { EnterpriseEnvironmentConfig } from '../environment/EnterpriseEnvironmentConfig';
import { EnterpriseServiceContainer } from '../container/EnterpriseServiceContainer';

// Health status types
export enum HealthStatus {
  HEALTHY = 'HEALTHY',
  DEGRADED = 'DEGRADED',
  UNHEALTHY = 'UNHEALTHY',
  UNKNOWN = 'UNKNOWN',
  MAINTENANCE = 'MAINTENANCE',
}

// Alert severity levels
export enum AlertSeverity {
  INFO = 'INFO',
  WARNING = 'WARNING',
  ERROR = 'ERROR',
  CRITICAL = 'CRITICAL',
}

// Alert types
export enum AlertType {
  THRESHOLD_EXCEEDED = 'THRESHOLD_EXCEEDED',
  SERVICE_DOWN = 'SERVICE_DOWN',
  PERFORMANCE_DEGRADED = 'PERFORMANCE_DEGRADED',
  SECURITY_BREACH = 'SECURITY_BREACH',
  RESOURCE_EXHAUSTED = 'RESOURCE_EXHAUSTED',
  CUSTOM = 'CUSTOM',
}

// Metric types
export enum MetricType {
  COUNTER = 'COUNTER',
  GAUGE = 'GAUGE',
  HISTOGRAM = 'HISTOGRAM',
  SUMMARY = 'SUMMARY',
}

// SLA compliance status
export enum SLAStatus {
  COMPLIANT = 'COMPLIANT',
  WARNING = 'WARNING',
  VIOLATION = 'VIOLATION',
  UNKNOWN = 'UNKNOWN',
}

// Health check interface
export interface HealthCheck {
  id: string;
  name: string;
  description: string;
  component: string;
  interval: number;
  timeout: number;
  enabled: boolean;
  threshold?: {
    warning?: number;
    critical?: number;
  };
  checkFunction: () => Promise<HealthCheckResult>;
}

// Health check result
export interface HealthCheckResult {
  id: string;
  status: HealthStatus;
  timestamp: number;
  duration: number;
  message?: string;
  details?: Record<string, any>;
  metrics?: Record<string, number>;
  error?: Error;
}

// Metric definition
export interface MetricDefinition {
  name: string;
  type: MetricType;
  description: string;
  labels?: Record<string, string>;
  buckets?: number[]; // For histogram metrics
}

// Metric value
export interface MetricValue {
  name: string;
  type: MetricType;
  value: number;
  timestamp: number;
  labels?: Record<string, string>;
}

// Alert definition
export interface AlertDefinition {
  id: string;
  name: string;
  description: string;
  severity: AlertSeverity;
  type: AlertType;
  enabled: boolean;
  condition: {
    metric: string;
    operator: 'gt' | 'lt' | 'eq' | 'ne' | 'gte' | 'lte';
    threshold: number;
    duration?: number; // Duration threshold must be exceeded
  };
  actions: AlertAction[];
  cooldown: number; // Minimum time between alerts
}

// Alert action
export interface AlertAction {
  type: 'email' | 'slack' | 'webhook' | 'pagerduty' | 'sms';
  config: Record<string, any>;
}

// Alert instance
export interface Alert {
  id: string;
  definitionId: string;
  severity: AlertSeverity;
  type: AlertType;
  title: string;
  message: string;
  timestamp: number;
  resolved: boolean;
  resolvedAt?: number;
  metrics: Record<string, number>;
  labels: Record<string, string>;
}

// SLA definition
export interface SLADefinition {
  id: string;
  name: string;
  description: string;
  target: number; // Target percentage (e.g., 99.9)
  window: number; // Time window in milliseconds
  metrics: {
    availability: number;
    performance: number;
    errorRate: number;
  };
  penalties: {
    warningThreshold: number;
    violationThreshold: number;
  };
}

// SLA status report
export interface SLAReport {
  id: string;
  name: string;
  status: SLAStatus;
  compliance: number; // Current compliance percentage
  window: {
    start: number;
    end: number;
  };
  metrics: {
    availability: number;
    performance: number;
    errorRate: number;
  };
  violations: SLAViolation[];
}

// SLA violation
export interface SLAViolation {
  timestamp: number;
  type: 'availability' | 'performance' | 'errorRate';
  actual: number;
  expected: number;
  duration: number;
}

// Health monitoring configuration
