  }

  getAvailableWorkflows(): MCAgenticWorkflow[] {
    return Array.from(this.workflows.values());
  }

  // Tool Execution
  async executeTool(request: MCPToolRequest): Promise<MCPToolResponse> {
    const tool = this.tools.get(request.toolId);
    if (!tool) {
      throw new Error(`Tool ${request.toolId} not found`);
    }

    const startTime = Date.now();
    const requestId =
      request.requestId || `req-${Date.now()}-${Math.random().toString(36).slice(2, 11)}`;

    try {
      // Validate input schema
      this.validateInput(tool.inputSchema, request.parameters);

      // Execute tool based on category
      const result = await this.executeToolByCategory(tool, request.parameters, request.context);

      const processingTime = Date.now() - startTime;

      const response: MCPToolResponse = {
        toolId: request.toolId,
        success: true,
        result,
        processingTime,
        timestamp: new Date(),
        metadata: {
          tokensUsed: this.estimateTokensUsed(request.parameters, result),
          cost: tool.cost ? this.calculateCost(tool, request.parameters, result) : undefined,
        },
      };

      // Store in execution history
      this.storeExecutionHistory(requestId, response);

      return response;
    } catch (error: any) {
      const processingTime = Date.now() - startTime;

      const response: MCPToolResponse = {
        toolId: request.toolId,
        success: false,
        result: null,
        error: error.message,
        processingTime,
        timestamp: new Date(),
      };

      this.storeExecutionHistory(requestId, response);
      return response;
    }
  }

  private async executeToolByCategory(tool: MCPTool, parameters: any, context?: any): Promise<any> {
    switch (tool.category) {
      case 'search':
        return await this.executeSearchTool(tool, parameters, context);
      case 'system':
        return await this.executeSystemTool(tool, parameters, context);
      case 'analysis':
        return await this.executeAnalysisTool(tool, parameters, context);
      case 'compliance':
        return await this.executeComplianceTool(tool, parameters, context);
      case 'domain':
        return await this.executeDomainTool(tool, parameters, context);
      default:
        throw new Error(`Unknown tool category: ${tool.category}`);
    }
  }

  private async executeSearchTool(tool: MCPTool, parameters: any, context?: any): Promise<any> {
    switch (tool.id) {
      case 'knowledge-base-search':
        return await this.searchKnowledgeBase(
          parameters.query,
          parameters.domain,
          parameters.filters,
          parameters.limit
        );
      case 'web-search':
        return await this.searchWeb(parameters.query, parameters.numResults, parameters.safeSearch);
      default:
        throw new Error(`Unknown search tool: ${tool.id}`);
    }
  }

  private async executeSystemTool(tool: MCPTool, parameters: any, context?: any): Promise<any> {
    switch (tool.id) {
      case 'crm-sync':
        return await this.syncCRM(
          parameters.action,
          parameters.entityType,
          parameters.data,
          parameters.externalId
        );
      case 'database-operation':
        return await this.executeDatabaseOperation(
          parameters.operation,
          parameters.table,
