/**
 * Agent Orchestrator for GLM-4.5 Orchestrated AI Ecosystem
 *
 * This module provides the main orchestration system that coordinates
 * multiple AI agents with GLM-4.5 as the primary orchestrator.
 * All operations are now orchestrated through the GLM-4.5 system.
 */
import { DynamicLoadBalancer } from './dynamic-load-balancer';
import { glmOrchestrator, GLMOrchestratorConfig } from '../glm-orchestrator';

interface AgentTask {
  id: string;
  type: string;
  priority: 'high' | 'medium' | 'low';
  capabilities: string[];
  payload: any;
  timeout?: number;
  retries?: number;
  dependencies?: string[];
}

interface AgentResult {
  taskId: string;
  success: boolean;
  data?: any;
  error?: string;
  executionTime: number;
  agentId: string;
}

interface OrchestratorConfig {
  maxConcurrentTasks: number;
  taskTimeout: number;
  maxRetries: number;
  loadBalancer: {
    strategy: 'round-robin' | 'weighted' | 'least-connections' | 'response-time';
    healthCheckInterval: number;
  };
  enableFaultTolerance: boolean;
  enableMonitoring: boolean;
}

export class AgentOrchestrator {
  private loadBalancer: DynamicLoadBalancer;
  private config: OrchestratorConfig;
  private taskQueue: AgentTask[] = [];
  private runningTasks: Map<string, Promise<AgentResult>> = new Map();
  private completedTasks: Map<string, AgentResult> = new Map();
  private taskDependencies: Map<string, string[]> = new Map();
  private isRunning = false;
  private monitoringInterval?: NodeJS.Timeout;

  constructor(config: OrchestratorConfig) {
    this.config = config;
    this.loadBalancer = new DynamicLoadBalancer({
      strategy: config.loadBalancer.strategy,
      healthCheckInterval: config.loadBalancer.healthCheckInterval,
      maxRetries: config.maxRetries,
      timeout: config.taskTimeout,
    });
  }

  /**
   * Initialize the orchestrator with GLM-4.5 as primary orchestrator
   */
  async initialize(agents: any[]): Promise<void> {
    // First, initialize the GLM orchestrator
    await glmOrchestrator.initialize();

    // Register agents with the load balancer
    agents.forEach(agent => {
      this.loadBalancer.registerAgent({
        id: agent.id,
        name: agent.name,
        type: agent.type,
        capabilities: agent.capabilities,
        performance: {
          responseTime: 0,
          successRate: 1,
          throughput: 0,
          errorRate: 0,
        },
        resources: {
          cpu: 0,
          memory: 0,
          network: 0,
        },
        health: 'healthy',
      });
    });

    if (this.config.enableMonitoring) {
      this.startMonitoring();
    }
  }

  /**
   * Start the orchestrator
   */
  start(): void {
    this.isRunning = true;
    this.processTaskQueue();
  }

  /**
   * Stop the orchestrator
   */
  stop(): void {
    this.isRunning = false;
    if (this.monitoringInterval) {
      clearInterval(this.monitoringInterval);
    }
    this.loadBalancer.stopHealthChecks();
  }

  /**
   * Submit a task for execution
   */
  async submitTask(task: Omit<AgentTask, 'id'>): Promise<string> {
    const taskId = this.generateTaskId();
    const fullTask: AgentTask = {
      ...task,
      id: taskId,
      timeout: task.timeout || this.config.taskTimeout,
      retries: task.retries || this.config.maxRetries,
    };

    this.taskQueue.push(fullTask);

    if (task.dependencies) {
      this.taskDependencies.set(taskId, task.dependencies);
    }

    // Process queue immediately if not already running
    if (this.isRunning && this.runningTasks.size < this.config.maxConcurrentTasks) {
      this.processTaskQueue();
    }

    return taskId;
  }

  /**
   * Get task result by ID
   */
  async getTaskResult(taskId: string): Promise<AgentResult | null> {
    if (this.completedTasks.has(taskId)) {
      return this.completedTasks.get(taskId)!;
    }

    if (this.runningTasks.has(taskId)) {
      return await this.runningTasks.get(taskId)!;
    }

    return null;
  }

  /**
   * Get orchestrator status and statistics
   */
  getStatus() {
    return {
      isRunning: this.isRunning,
      queueLength: this.taskQueue.length,
      runningTasks: this.runningTasks.size,
      completedTasks: this.completedTasks.size,
      loadBalancerStats: this.loadBalancer.getStats(),
    };
  }

  /**
   * Process the task queue
   */
  private async processTaskQueue(): Promise<void> {
    if (!this.isRunning) return;

    while (this.taskQueue.length > 0 && this.runningTasks.size < this.config.maxConcurrentTasks) {
      const task = this.findNextExecutableTask();

      if (!task) break;

      // Remove task from queue
      const taskIndex = this.taskQueue.findIndex(t => t.id === task.id);
      if (taskIndex !== -1) {
        this.taskQueue.splice(taskIndex, 1);
      }

      // Execute task
      const taskPromise = this.executeTask(task);
      this.runningTasks.set(task.id, taskPromise);

      // Handle task completion
      taskPromise
        .then(result => {
          this.completedTasks.set(task.id, result);
          this.runningTasks.delete(task.id);
        })
        .catch(error => {
          this.completedTasks.set(task.id, {
            taskId: task.id,
            success: false,
            error: error.message,
            executionTime: 0,
            agentId: 'unknown',
          });
          this.runningTasks.delete(task.id);
        })
        .finally(() => {
          // Process next task
          if (this.isRunning) {
            this.processTaskQueue();
          }
        });
    }
  }

  /**
   * Find the next executable task considering dependencies
   */
  private findNextExecutableTask(): AgentTask | null {
    for (const task of this.taskQueue) {
      const dependencies = this.taskDependencies.get(task.id) || [];

      // Check if all dependencies are completed
      const allDependenciesCompleted = dependencies.every(
        depId => this.completedTasks.has(depId) && this.completedTasks.get(depId)?.success
      );

      if (allDependenciesCompleted) {
        return task;
      }
    }
    return null;
  }

  /**
   * Execute a single task
   */
  private async executeTask(task: AgentTask): Promise<AgentResult> {
    const startTime = Date.now();

    try {
      const result = await this.loadBalancer.executeTask(
        {
          capabilities: task.capabilities,
          priority: task.priority,
          payload: task.payload,
        },
        async agent => {
          // Execute the task using the selected agent
          return await this.executeWithAgent(task, agent);
        }
      );

      const executionTime = Date.now() - startTime;
      return {
        taskId: task.id,
        success: true,
        data: result,
        executionTime,
        agentId: 'selected-agent', // This would be the actual agent ID
      };
    } catch (error) {
      const executionTime = Date.now() - startTime;
      return {
        taskId: task.id,
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        executionTime,
        agentId: 'unknown',
      };
    }
  }

  /**
   * Execute task with a specific agent - now orchestrated through GLM-4.5
   */
  private async executeWithAgent(task: AgentTask, agent: any): Promise<any> {
    // Submit the task to the GLM orchestrator for execution
    const operationType = this.mapTaskTypeToOperationType(task.type);

    const operationId = await glmOrchestrator.submitOperation({
      type: operationType,
      priority: task.priority,
      payload: {
        task,
        agent,
        originalPayload: task.payload,
      },
      agentRequirements: task.capabilities,
      expectedOutcome: `Execute ${task.type} task using agent ${agent.name}`,
      timeout: task.timeout || this.config.taskTimeout,
    });

    // Wait for the GLM orchestrator to complete the operation
    const result = await glmOrchestrator.getOperationResult(operationId);

    if (!result || !result.success) {
      throw new Error(result ? `Operation failed: ${JSON.stringify(result)}` : 'Operation failed');
    }

    return result.result;
  }

  /**
   * Map task type to GLM orchestrator operation type
   */
  private mapTaskTypeToOperationType(
    taskType: string
  ): 'analysis' | 'optimization' | 'monitoring' | 'security' | 'prediction' {
    switch (taskType) {
      case 'text-generation':
      case 'data-analysis':
        return 'analysis';
      case 'image-generation':
        return 'optimization';
      case 'web-search':
        return 'monitoring';
      case 'security-scan':
        return 'security';
      case 'prediction':
        return 'prediction';
      default:
        return 'analysis';
    }
  }

  /**
   * Start monitoring system
   */
  private startMonitoring(): void {
    this.monitoringInterval = setInterval(() => {
      this.logSystemStatus();
    }, 30000); // Log every 30 seconds
  }

  /**
   * Log system status
   */
  private logSystemStatus(): void {
    const status = this.getStatus();
    console.log('Agent Orchestrator Status:', {
      ...status,
      timestamp: new Date().toISOString(),
    });
  }

  /**
   * Generate unique task ID
   */
  private generateTaskId(): string {
    return `task_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  /**
   * Clean up resources
   */
  destroy(): void {
    this.stop();
    this.loadBalancer.destroy();
    this.taskQueue = [];
    this.runningTasks.clear();
    this.completedTasks.clear();
    this.taskDependencies.clear();
    glmOrchestrator.destroy();
  }
}

/**
 * Dynamic Load Balancer for GLM-4.5 Orchestrated AI Ecosystem
 *
 * This module provides intelligent load balancing across AI agents with
 * dynamic selection based on performance, capabilities, and resource availability.
 */
import { ZAI } from 'z-ai-web-dev-sdk';

interface LoadBalancerConfig {
  strategy: 'round-robin' | 'weighted' | 'least-connections' | 'response-time';
  healthCheckInterval: number;
  maxRetries: number;
  timeout: number;
}

interface AgentMetrics {
  id: string;
  name: string;
  type: 'primary' | 'supporting' | 'specialized' | 'external';
  capabilities: string[];
  performance: {
    responseTime: number;
    successRate: number;
    throughput: number;
    errorRate: number;
  };
  resources: {
    cpu: number;
    memory: number;
    network: number;
  };
  health: 'healthy' | 'degraded' | 'unhealthy';
  lastUsed: Date;
  connections: number;
}

interface LoadBalancerStats {
  totalRequests: number;
  successfulRequests: number;
  failedRequests: number;
  averageResponseTime: number;
  agentUtilization: Record<string, number>;
}

export class DynamicLoadBalancer {
  private agents: Map<string, AgentMetrics> = new Map();
  private config: LoadBalancerConfig;
  private currentIndex = 0;
  private stats: LoadBalancerStats = {
    totalRequests: 0,
    successfulRequests: 0,
    failedRequests: 0,
    averageResponseTime: 0,
    agentUtilization: {},
  };
  private healthCheckInterval?: NodeJS.Timeout;

  constructor(config: LoadBalancerConfig) {
    this.config = config;
    this.startHealthChecks();
  }

  /**
   * Register a new agent with the load balancer
   */
  registerAgent(agent: Omit<AgentMetrics, 'lastUsed' | 'connections'>): void {
    const agentMetrics: AgentMetrics = {
      ...agent,
      lastUsed: new Date(),
      connections: 0,
    };
    this.agents.set(agent.id, agentMetrics);
    this.stats.agentUtilization[agent.id] = 0;
  }

  /**
   * Unregister an agent from the load balancer
   */
  unregisterAgent(agentId: string): void {
    this.agents.delete(agentId);
    delete this.stats.agentUtilization[agentId];
  }

  /**
   * Select the best agent for a given task based on load balancing strategy
   */
  async selectAgent(
    capabilities: string[],
    priority: 'high' | 'medium' | 'low' = 'medium'
  ): Promise<AgentMetrics | null> {
    const availableAgents = Array.from(this.agents.values()).filter(
      agent => agent.health === 'healthy' && this.hasRequiredCapabilities(agent, capabilities)
    );

    if (availableAgents.length === 0) {
      return null;
    }

    let selectedAgent: AgentMetrics;

    switch (this.config.strategy) {
      case 'round-robin':
        selectedAgent = this.roundRobinSelection(availableAgents);
        break;
      case 'weighted':
        selectedAgent = this.weightedSelection(availableAgents, priority);
        break;
      case 'least-connections':
        selectedAgent = this.leastConnectionsSelection(availableAgents);
        break;
      case 'response-time':
        selectedAgent = this.responseTimeSelection(availableAgents);
        break;
      default:
        selectedAgent = this.roundRobinSelection(availableAgents);
    }

    selectedAgent.connections++;
    selectedAgent.lastUsed = new Date();
    this.stats.totalRequests++;

    return selectedAgent;
  }

  /**
   * Execute a task using the load balancer
   */
  async executeTask<T>(
    task: {
      capabilities: string[];
      priority: 'high' | 'medium' | 'low';
      payload: any;
    },
    executor: (agent: AgentMetrics) => Promise<T>
  ): Promise<T> {
    const startTime = Date.now();
    let attempts = 0;

    while (attempts < this.config.maxRetries) {
      const agent = await this.selectAgent(task.capabilities, task.priority);

      if (!agent) {
        throw new Error('No available agents with required capabilities');
      }

      try {
        const result = await Promise.race([
          executor(agent),
          new Promise<never>((_, reject) =>
            setTimeout(() => reject(new Error('Agent timeout')), this.config.timeout)
          ),
        ]);

        const responseTime = Date.now() - startTime;
        this.updateAgentMetrics(agent, true, responseTime);
        this.stats.successfulRequests++;
        this.updateAverageResponseTime(responseTime);

        return result;
      } catch (error) {
        attempts++;
        this.updateAgentMetrics(agent, false, 0);

        if (attempts === this.config.maxRetries) {
          this.stats.failedRequests++;
          throw error;
        }
      }
    }

    throw new Error('Max retries exceeded');
  }

  /**
   * Get current load balancer statistics
   */
  getStats(): LoadBalancerStats {
    return { ...this.stats };
  }

  /**
   * Get agent status
   */
  getAgentStatus(agentId: string): AgentMetrics | undefined {
    return this.agents.get(agentId);
  }

  /**
   * Update agent performance metrics
   */
  updateAgentMetrics(agent: AgentMetrics, success: boolean, responseTime: number): void {
    agent.performance.responseTime = agent.performance.responseTime * 0.9 + responseTime * 0.1;
    agent.performance.successRate = agent.performance.successRate * 0.9 + (success ? 1 : 0) * 0.1;
    agent.performance.errorRate = agent.performance.errorRate * 0.9 + (success ? 0 : 1) * 0.1;
    agent.connections = Math.max(0, agent.connections - 1);

    this.stats.agentUtilization[agent.id] =
      (this.stats.agentUtilization[agent.id] || 0) * 0.9 + (agent.connections > 0 ? 1 : 0) * 0.1;
  }

  /**
   * Health check for all agents
   */
  private async healthCheck(): Promise<void> {
    for (const [agentId, agent] of this.agents) {
      try {
        // Simulate health check - in real implementation, this would ping the agent
        const isHealthy = await this.checkAgentHealth(agent);

        if (isHealthy) {
          agent.health = 'healthy';
        } else if (agent.health === 'healthy') {
          agent.health = 'degraded';
        } else {
          agent.health = 'unhealthy';
        }
      } catch (error) {
        agent.health = 'unhealthy';
      }
    }
  }

  /**
   * Check individual agent health
   */
  private async checkAgentHealth(agent: AgentMetrics): Promise<boolean> {
    // Simulate health check based on resource usage and error rates
    const resourceThreshold = 80; // 80% resource usage threshold
    const errorRateThreshold = 0.1; // 10% error rate threshold

    return (
      agent.resources.cpu < resourceThreshold &&
      agent.resources.memory < resourceThreshold &&
      agent.resources.network < resourceThreshold &&
      agent.performance.errorRate < errorRateThreshold
    );
  }

  /**
   * Start periodic health checks
   */
  private startHealthChecks(): void {
    this.healthCheckInterval = setInterval(
      () => this.healthCheck(),
      this.config.healthCheckInterval
    );
  }

  /**
   * Stop health checks
   */
  stopHealthChecks(): void {
    if (this.healthCheckInterval) {
      clearInterval(this.healthCheckInterval);
    }
  }

  /**
   * Round-robin selection strategy
   */
  private roundRobinSelection(agents: AgentMetrics[]): AgentMetrics {
    const agent = agents[this.currentIndex % agents.length];
    this.currentIndex++;
    return agent;
  }

  /**
   * Weighted selection strategy based on priority and performance
   */
  private weightedSelection(
    agents: AgentMetrics[],
    priority: 'high' | 'medium' | 'low'
  ): AgentMetrics {
    const weights = agents.map(agent => {
      let weight = 1;

      // Priority-based weighting
      if (priority === 'high') weight *= 2;
      else if (priority === 'low') weight *= 0.5;

      // Performance-based weighting
      weight *= 1 - agent.performance.errorRate;
      weight *= agent.performance.successRate;
      weight *= 1 / (1 + agent.performance.responseTime / 1000);

      // Resource-based weighting
      weight *= 1 - agent.resources.cpu / 100;
      weight *= 1 - agent.resources.memory / 100;

      // Connection-based weighting
      weight *= 1 / (1 + agent.connections);

      return weight;
    });

    const totalWeight = weights.reduce((sum, weight) => sum + weight, 0);
    let random = Math.random() * totalWeight;

    for (let i = 0; i < agents.length; i++) {
      random -= weights[i];
      if (random <= 0) {
        return agents[i];
      }
    }

    return agents[agents.length - 1];
  }

  /**
   * Least connections selection strategy
   */
  private leastConnectionsSelection(agents: AgentMetrics[]): AgentMetrics {
    return agents.reduce((min, agent) => (agent.connections < min.connections ? agent : min));
  }

  /**
   * Response time selection strategy
   */
  private responseTimeSelection(agents: AgentMetrics[]): AgentMetrics {
    return agents.reduce((min, agent) =>
      agent.performance.responseTime < min.performance.responseTime ? agent : min
    );
  }

  /**
   * Check if agent has required capabilities
   */
  private hasRequiredCapabilities(agent: AgentMetrics, requiredCapabilities: string[]): boolean {
    return requiredCapabilities.every(capability => agent.capabilities.includes(capability));
  }

  /**
   * Update average response time
   */
  private updateAverageResponseTime(responseTime: number): void {
    this.stats.averageResponseTime = this.stats.averageResponseTime * 0.9 + responseTime * 0.1;
  }

  /**
   * Clean up resources
   */
  destroy(): void {
    this.stopHealthChecks();
    this.agents.clear();
  }
}

/**
 * performance-optimization.ts
 * Utilities for progressive loading, lazy rendering, and performance optimization
 */

import React, { useEffect, useState, useCallback, useRef, useMemo } from 'react';

interface IntersectionObserverOptions {
  threshold?: number;
  rootMargin?: string;
  triggerOnce?: boolean;
}

interface LoadingStrategy {
  type: 'lazy' | 'eager' | 'progressive';
  priority?: 'high' | 'medium' | 'low';
  placeholder?: React.ReactNode;
  skeleton?: React.ReactNode;
}

interface PerformanceMetrics {
  loadTime: number;
  renderTime: number;
  memoryUsage?: number;
  componentSize?: number;
}

// Global performance tracking
const performanceMetrics = new Map<string, PerformanceMetrics[]>();

/**
 * Hook for intersection observer based lazy loading
 */
export function useIntersectionObserver(
  elementRef: React.RefObject<Element>,
  options: IntersectionObserverOptions = {}
) {
  const [isIntersecting, setIsIntersecting] = useState(false);
  const { threshold = 0.1, rootMargin = '0px', triggerOnce = true } = options;

  useEffect(() => {
    const element = elementRef.current;
    if (!element) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsIntersecting(true);
          if (triggerOnce) {
            observer.unobserve(element);
          }
        } else if (!triggerOnce) {
          setIsIntersecting(false);
        }
      },
      { threshold, rootMargin }
    );

    observer.observe(element);

    return () => {
      observer.unobserve(element);
    };
  }, [elementRef, threshold, rootMargin, triggerOnce]);

  return isIntersecting;
}

/**
 * Hook for lazy loading components
 */
export function useLazyComponent<T extends React.ComponentType<any>>(
  importFn: () => Promise<{ default: T }>,
  options: LoadingStrategy = { type: 'lazy' }
) {
  const [Component, setComponent] = useState<T | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  const loadComponent = useCallback(async () => {
    if (Component || isLoading) return;

    setIsLoading(true);
    const startTime = performance.now();

    try {
      const importedModule = await importFn();
      setComponent(() => importedModule.default);

      // Track performance metrics
      const loadTime = performance.now() - startTime;
      trackPerformance('lazy_component', { loadTime, renderTime: 0 });
    } catch (err) {
      setError(err as Error);
    } finally {
      setIsLoading(false);
    }
  }, [importFn, Component, isLoading]);

  useEffect(() => {
    if (options.type === 'eager') {
      loadComponent();
    }
  }, [loadComponent, options.type]);

  const LazyComponent = useMemo(() => {
    if (!Component) {
      return null;
    }

    const WrappedComponent = (props: Parameters<T>[0]) => {
      const renderStartTime = performance.now();

      useEffect(() => {
        const renderTime = performance.now() - renderStartTime;
        trackPerformance('lazy_component_render', { loadTime: 0, renderTime });
      }, []);

      return React.createElement(Component, props);
    };

    return WrappedComponent;
  }, [Component]);

  return {
    LazyComponent,
    isLoading,
    error,
    loadComponent,
  };
}

/**
 * Hook for progressive image loading
 */
export function useProgressiveImage(
  src: string,
  placeholderSrc?: string,
  options: LoadingStrategy = { type: 'lazy' }
) {
  const [currentSrc, setCurrentSrc] = useState(placeholderSrc || '');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);
  const imgRef = useRef<HTMLImageElement>(null);

  const isIntersecting = useIntersectionObserver(imgRef, {
    threshold: 0.1,
    triggerOnce: true,
  });

  useEffect(() => {
    if (!isIntersecting || options.type !== 'lazy') return;

    const img = new Image();
    const startTime = performance.now();

    img.onload = () => {
      setCurrentSrc(src);
      setIsLoading(false);

      // Track performance metrics
      const loadTime = performance.now() - startTime;
      trackPerformance('image_load', { loadTime, renderTime: 0 });
    };

    img.onerror = () => {
      setError(new Error('Failed to load image'));
      setIsLoading(false);
    };

    img.src = src;

    return () => {
      img.onload = null;
      img.onerror = null;
    };
  }, [src, isIntersecting, options.type]);

  // Eager loading
  useEffect(() => {
    if (options.type === 'eager' && !currentSrc) {
      const img = new Image();
      img.onload = () => setCurrentSrc(src);
      img.src = src;
    }
  }, [src, options.type, currentSrc]);

  return {
    src: currentSrc,
    isLoading,
    error,
    imgRef,
  };
}

/**
 * Hook for virtual scrolling (large lists)
 */
export function useVirtualScroll<T>(
  items: T[],
  itemHeight: number,
  containerHeight: number,
  overscan: number = 5
) {
  const [scrollTop, setScrollTop] = useState(0);

  const visibleRange = useMemo(() => {
    const startIndex = Math.max(0, Math.floor(scrollTop / itemHeight) - overscan);
    const endIndex = Math.min(
      items.length - 1,
      Math.ceil((scrollTop + containerHeight) / itemHeight) + overscan
    );

    return { startIndex, endIndex };
  }, [scrollTop, itemHeight, containerHeight, overscan, items.length]);

  const visibleItems = useMemo(() => {
    return items.slice(visibleRange.startIndex, visibleRange.endIndex + 1);
  }, [items, visibleRange]);

  const totalHeight = items.length * itemHeight;
  const offsetY = visibleRange.startIndex * itemHeight;

  const handleScroll = useCallback((event: React.UIEvent<HTMLDivElement>) => {
    setScrollTop(event.currentTarget.scrollTop);
  }, []);

  return {
    visibleItems,
    totalHeight,
    offsetY,
    handleScroll,
    scrollTop,
  };
}

/**
 * Hook for debounced values
 */
export function useDebounce<T>(value: T, delay: number): T {
  const [debouncedValue, setDebouncedValue] = useState<T>(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
}

/**
 * Hook for throttled functions
 */
export function useThrottle<T extends (...args: any[]) => any>(callback: T, delay: number): T {
  const lastCall = useRef<number>(0);

  return useCallback(
    ((...args: Parameters<T>) => {
      const now = Date.now();
      if (now - lastCall.current >= delay) {
        lastCall.current = now;
        return callback(...args);
      }
    }) as T,
    [callback, delay]
  );
}

/**
 * Performance monitoring utilities
 */
export function trackPerformance(componentName: string, metrics: Partial<PerformanceMetrics>) {
  if (!performanceMetrics.has(componentName)) {
    performanceMetrics.set(componentName, []);
  }

  const componentMetrics = performanceMetrics.get(componentName)!;
  componentMetrics.push({
    loadTime: metrics.loadTime || 0,
    renderTime: metrics.renderTime || 0,
    memoryUsage: metrics.memoryUsage,
    componentSize: metrics.componentSize,
  });

  // Keep only last 100 measurements
  if (componentMetrics.length > 100) {
    componentMetrics.shift();
  }

  // Log performance warnings
  if (metrics.loadTime && metrics.loadTime > 1000) {
    console.warn(`[Performance] ${componentName} slow load: ${metrics.loadTime}ms`);
  }
  if (metrics.renderTime && metrics.renderTime > 100) {
    console.warn(`[Performance] ${componentName} slow render: ${metrics.renderTime}ms`);
  }
}

/**
 * Get performance metrics for a component
 */
export function getPerformanceMetrics(componentName: string) {
  const metrics = performanceMetrics.get(componentName) || [];

  if (metrics.length === 0) return null;

  const avgLoadTime = metrics.reduce((sum, m) => sum + m.loadTime, 0) / metrics.length;
  const avgRenderTime = metrics.reduce((sum, m) => sum + m.renderTime, 0) / metrics.length;
  const maxLoadTime = Math.max(...metrics.map(m => m.loadTime));
  const maxRenderTime = Math.max(...metrics.map(m => m.renderTime));

  return {
    count: metrics.length,
    avgLoadTime,
    avgRenderTime,
    maxLoadTime,
    maxRenderTime,
    recentMetrics: metrics.slice(-10),
  };
}

/**
 * Memory usage monitoring
 */
export function useMemoryUsage() {
  const [memoryUsage, setMemoryUsage] = useState<{
    used: number;
    total: number;
    jsHeapSizeLimit: number;
  } | null>(null);

  useEffect(() => {
    const updateMemoryUsage = () => {
      if ('memory' in performance) {
        const memory = (performance as any).memory;
        setMemoryUsage({
          used: memory.usedJSHeapSize,
          total: memory.totalJSHeapSize,
          jsHeapSizeLimit: memory.jsHeapSizeLimit,
        });
      }
    };

    const interval = setInterval(updateMemoryUsage, 5000);
    updateMemoryUsage();

    return () => clearInterval(interval);
  }, []);

  return memoryUsage;
}

/**
 * Component for lazy loading with skeleton
 */
export function LazyLoadComponent({
  children,
  placeholder,
  skeleton,
  strategy = { type: 'lazy' },
  className = '',
}: {
  children: React.ReactNode;
  placeholder?: React.ReactNode;
  skeleton?: React.ReactNode;
  strategy?: LoadingStrategy;
  className?: string;
}) {
  const ref = useRef<HTMLDivElement>(null);
  const isIntersecting = useIntersectionObserver(ref, {
    threshold: 0.1,
    triggerOnce: true,
  });

  const shouldLoad = strategy.type === 'eager' || isIntersecting;

  return React.createElement(
    'div',
    { ref, className },
    shouldLoad
      ? children
      : React.createElement(
          'div',
          { className: 'lazy-loading-placeholder' },
          skeleton ||
            placeholder ||
            React.createElement(
              'div',
              { className: 'animate-pulse' },
              React.createElement('div', {
                className: 'h-4 bg-gray-200 rounded w-3/4 mb-2',
              }),
              React.createElement('div', {
                className: 'h-4 bg-gray-200 rounded w-1/2 mb-2',
              }),
              React.createElement('div', {
                className: 'h-4 bg-gray-200 rounded w-5/6',
              })
            )
        )
  );
}

/**
 * Progressive image component
 */
export function ProgressiveImage({
  src,
  alt,
  placeholderSrc,
  className = '',
  onLoad,
  onError,
  strategy = { type: 'lazy' },
}: {
  src: string;
  alt: string;
  placeholderSrc?: string;
  className?: string;
  onLoad?: () => void;
  onError?: () => void;
  strategy?: LoadingStrategy;
}) {
  const {
    src: currentSrc,
    isLoading,
    error,
    imgRef,
  } = useProgressiveImage(src, placeholderSrc, strategy);

  return (
    <div className={`progressive-image-container ${className}`}>
      <img
        ref={imgRef}
        src={currentSrc}
        alt={alt}
        className={`w-full h-full object-cover transition-opacity duration-300 ${
          isLoading ? 'opacity-50' : 'opacity-100'
        }`}
        onLoad={onLoad}
        onError={onError}
      />
      {isLoading && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-100">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-600"></div>
        </div>
      )}
      {error && (
        <div className="absolute inset-0 flex items-center justify-center bg-red-50">
          <div className="text-red-600 text-sm">Failed to load image</div>
        </div>
      )}
    </div>
  );
}

/**
 * Virtual list component for large datasets
 */
export function VirtualList<T>({
  items,
  itemHeight,
  containerHeight,
  renderItem,
  overscan = 5,
  className = '',
}: {
  items: T[];
  itemHeight: number;
  containerHeight: number;
  renderItem: (item: T, index: number) => React.ReactNode;
  overscan?: number;
  className?: string;
}) {
  const { visibleItems, totalHeight, offsetY, handleScroll } = useVirtualScroll(
    items,
    itemHeight,
    containerHeight,
    overscan
  );

  return (
    <div
      className={`virtual-list-container ${className}`}
      style={{ height: containerHeight, overflow: 'auto' }}
      onScroll={handleScroll}
    >
      <div style={{ height: totalHeight, position: 'relative' }}>
        <div style={{ position: 'absolute', top: offsetY, width: '100%' }}>
          {visibleItems.map((item, index) => {
            const originalIndex = items.indexOf(item);
            return (
              <div key={originalIndex} style={{ height: itemHeight }}>
                {renderItem(item, originalIndex)}
              </div>
            );
          })}
        </div>
      </div>
    </div>
