export interface ContextPromptTemplate {
  id: string;
  name: string;
  description: string;
  domain: 'healthcare' | 'legal' | 'education' | 'customer-service' | 'general';
  version: string;
  structure: PromptStructure;
  validationRules: ValidationRule[];
  outputFormat: OutputFormat;
  compliance: ComplianceRequirements;
}

export interface PromptStructure {
  sections: PromptSection[];
  requiredSections: string[];
  optionalSections: string[];
  order: string[];
  dependencies: { [key: string]: string[] };
}

export interface PromptSection {
  id: string;
  name: string;
  description: string;
  type: 'directive' | 'profile' | 'state' | 'input' | 'output' | 'safeguard' | 'context';
  required: boolean;
  template: string;
  validation: SectionValidation;
  variables: PromptVariable[];
}

export interface PromptVariable {
  name: string;
  type: 'string' | 'number' | 'boolean' | 'object' | 'array';
  description: string;
  required: boolean;
  defaultValue?: any;
  validation?: VariableValidation;
}

export interface VariableValidation {
  type: 'string' | 'number' | 'boolean' | 'object' | 'array';
  constraints?: any;
  pattern?: string;
  min?: number;
  max?: number;
  enum?: any[];
}

export interface SectionValidation {
  required: boolean;
  minLength?: number;
  maxLength?: number;
  pattern?: string;
  customValidation?: string;
}

export interface ValidationRule {
  id: string;
  name: string;
  description: string;
  type: 'syntax' | 'semantic' | 'compliance' | 'security';
  severity: 'error' | 'warning' | 'info';
  condition: string;
  message: string;
}

export interface OutputFormat {
  type: 'json' | 'xml' | 'text' | 'markdown' | 'custom';
  schema?: any;
  template?: string;
  validation: OutputValidation;
}

export interface OutputValidation {
  strict: boolean;
  requiredFields: string[];
  optionalFields: string[];
  typeChecking: boolean;
  customValidation?: string;
}

export interface ComplianceRequirements {
  hipaa?: boolean;
  gdpr?: boolean;
  soc2?: boolean;
  custom?: string[];
  dataRetention?: {
    enabled: boolean;
    duration: number; // in days
    encryption: boolean;
  };
  auditTrail?: {
    enabled: boolean;
    level: 'basic' | 'detailed' | 'comprehensive';
  };
}

export interface ContextPrompt {
  id: string;
  templateId: string;
  variables: { [key: string]: any };
  sections: { [key: string]: string };
  metadata: {
    createdAt: Date;
    updatedAt: Date;
    version: string;
    domain: string;
    agentConfig: MCPAgentConfig;
  };
  validation: {
    isValid: boolean;
    errors: ValidationError[];
    warnings: ValidationWarning[];
    score: number;
  };
}

export interface ValidationError {
  section: string;
  field: string;
  message: string;
  severity: 'error' | 'warning' | 'info';
  ruleId: string;
}

export interface ValidationWarning {
  section: string;
  field: string;
  message: string;
  ruleId: string;
}

export interface ContextPromptGenerationRequest {
  templateId: string;
  variables: { [key: string]: any };
  agentConfig: MCPAgentConfig;
  options?: {
    strictValidation?: boolean;
    includeMetadata?: boolean;
    complianceLevel?: 'basic' | 'standard' | 'strict';
  };
}

export interface ContextPromptGenerationResponse {
  success: boolean;
  prompt?: ContextPrompt;
  errors: string[];
  warnings: string[];
  metadata: {
    generationTime: number;
    validationScore: number;
    complianceScore: number;
  };
}

