/**
 * humanReview.ts
 * Minimal human-in-the-loop queue + assignment logic.
 * Replace in-memory queue with Redis/Bull and DB persistence in production.
 */
import { v4 as uuidv4 } from 'uuid';

import { ReviewItem } from '../types/index';

// In-memory queue for dev
const reviewQueue: ReviewItem[] = [];

// Simple reviewer pool example (in prod use DB + RBAC)
const REVIEWERS = [
  {
    id: 'rev-1',
    name: 'Alice',
    specialties: ['nudity', 'deepfake'],
    maxConcurrent: 5,
    currentLoad: 0,
  },
  {
    id: 'rev-2',
    name: 'Bob',
    specialties: ['violence', 'hate'],
    maxConcurrent: 5,
    currentLoad: 0,
  },
  {
    id: 'rev-3',
    name: 'Carol',
    specialties: ['nudity', 'violence', 'hate'],
    maxConcurrent: 3,
    currentLoad: 0,
  },
];

/**
 * Intelligent reviewer assignment based on specialties and workload
 */
function pickReviewer(priority: string, reasons: string[]): string {
  // Extract content categories from reasons
  const contentCategories = reasons
    .map(
      reason =>
        reason
          .toLowerCase()
          .match(/(nudity|deepfake|violence|hate|sexual|explicit|weapon|blood|discrimination)/)?.[0]
    )
    .filter(Boolean);

  // Find reviewers with matching specialties
  const availableReviewers = REVIEWERS.filter(reviewer => {
    // Check if reviewer has capacity
    if (reviewer.currentLoad >= reviewer.maxConcurrent) return false;

    // Check if reviewer has relevant specialties
    if (contentCategories.length === 0) return true;

    return contentCategories.some(category =>
      reviewer.specialties.some(specialty => specialty.toLowerCase().includes(category))
    );
  });

  if (availableReviewers.length === 0) {
    // Fallback to any available reviewer
    const anyAvailable = REVIEWERS.filter(r => r.currentLoad < r.maxConcurrent);
    if (anyAvailable.length === 0) {
      // All reviewers at capacity, return least loaded
      return REVIEWERS.reduce((min, reviewer) =>
        reviewer.currentLoad < min.currentLoad ? reviewer : min
      ).id;
    }
    return anyAvailable[Math.floor(Math.random() * anyAvailable.length)].id;
  }

  // Pick the reviewer with the most relevant specialties and lowest load
  const scoredReviewers = availableReviewers.map(reviewer => {
    let score = 0;

    // Score based on specialty matches
    for (const category of contentCategories) {
      if (reviewer.specialties.some(specialty => specialty.toLowerCase().includes(category))) {
        score += 10;
      }
    }

    // Score based on availability (lower load = higher score)
    score += (reviewer.maxConcurrent - reviewer.currentLoad) * 2;

    // Priority weighting
    const priorityWeight = { critical: 5, high: 3, medium: 2, low: 1 }[priority] || 1;
    score *= priorityWeight;

    return { reviewer, score };
  });

  // Return the highest-scoring reviewer
  scoredReviewers.sort((a, b) => b.score - a.score);
  const selectedReviewer = scoredReviewers[0].reviewer;

  // Update reviewer load
  selectedReviewer.currentLoad++;

  return selectedReviewer.id;
}

/**
 * Enqueue an item for human review
 */
export async function enqueueHumanReview(opts: {
  imageId: string;
  priority?: 'low' | 'medium' | 'high' | 'critical';
  reasons: string[];
  metadata?: Record<string, any>;
}): Promise<ReviewItem> {
  const reviewId = uuidv4();
  const assignedTo = pickReviewer(opts.priority || 'medium', opts.reasons);

  const item: ReviewItem = {
    reviewId,
    imageId: opts.imageId,
    priority: opts.priority || 'medium',
    assignedTo,
    reasons: opts.reasons,
    createdAt: new Date().toISOString(),
  };

  reviewQueue.push(item);

  console.log(
    `[humanReview] enqueued ${reviewId} for image ${opts.imageId}, assigned to ${assignedTo}`
  );

  // In production, this would trigger notifications, database writes, etc.
  return item;
}

/**
 * List all pending reviews
 */
export async function listPendingReviews(filters?: {
  priority?: 'low' | 'medium' | 'high' | 'critical';
  assignedTo?: string;
  limit?: number;
}): Promise<ReviewItem[]> {
  let filtered = [...reviewQueue];

  if (filters?.priority) {
    filtered = filtered.filter(item => item.priority === filters.priority);
  }

  if (filters?.assignedTo) {
    filtered = filtered.filter(item => item.assignedTo === filters.assignedTo);
  }

  if (filters?.limit) {
    filtered = filtered.slice(0, filters.limit);
  }

  // Sort by priority (critical first) then by creation time (oldest first)
  const priorityOrder = { critical: 4, high: 3, medium: 2, low: 1 };
  filtered.sort((a, b) => {
    const priorityDiff = priorityOrder[b.priority] - priorityOrder[a.priority];
    if (priorityDiff !== 0) return priorityDiff;
    return new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime();
  });

  return filtered;
}

/**
 * Remove a review from the queue (when completed)
 */
export async function popReview(
  reviewId: string,
  action?: string,
  notes?: string
): Promise<boolean> {
  const idx = reviewQueue.findIndex(r => r.reviewId === reviewId);
  if (idx === -1) return false;

  const review = reviewQueue[idx];

  // Update reviewer load
  const reviewer = REVIEWERS.find(r => r.id === review.assignedTo);
  if (reviewer && reviewer.currentLoad > 0) {
    reviewer.currentLoad--;
  }

  // Remove from queue
  reviewQueue.splice(idx, 1);

  console.log(`[humanReview] completed review ${reviewId} with action: ${action || 'unknown'}`);

  // In production, this would update database, send notifications, etc.
  return true;
}

/**
 * Get review queue statistics
 */
export async function getReviewStats(): Promise<{
  total: number;
  byPriority: Record<string, number>;
  byReviewer: Array<{
    reviewerId: string;
    reviewerName: string;
    currentLoad: number;
    maxConcurrent: number;
    utilization: number;
  }>;
  averageAge: number;
}> {
  const total = reviewQueue.length;

  // Count by priority
  const byPriority = reviewQueue.reduce(
    (acc, item) => {
      acc[item.priority] = (acc[item.priority] || 0) + 1;
      return acc;
    },
    {} as Record<string, number>
  );

  // Reviewer statistics
  const byReviewer = REVIEWERS.map(reviewer => ({
    reviewerId: reviewer.id,
    reviewerName: reviewer.name,
    currentLoad: reviewer.currentLoad,
    maxConcurrent: reviewer.maxConcurrent,
    utilization: Math.round((reviewer.currentLoad / reviewer.maxConcurrent) * 100),
  }));

  // Calculate average age of reviews in queue
  const now = Date.now();
  const totalAge = reviewQueue.reduce((sum, item) => {
    return sum + (now - new Date(item.createdAt).getTime());
  }, 0);
  const averageAge = total > 0 ? total / reviewQueue.length : 0;

  return {
    total,
    byPriority,
    byReviewer,
    averageAge: Math.round(averageAge / 1000 / 60), // Convert to minutes
  };
}

/**
 * Reassign a review to a different reviewer
 */
export async function reassignReview(reviewId: string, newReviewerId?: string): Promise<boolean> {
  const review = reviewQueue.find(r => r.reviewId === reviewId);
  if (!review) return false;

  // Release current reviewer
  const currentReviewer = REVIEWERS.find(r => r.id === review.assignedTo);
  if (currentReviewer && currentReviewer.currentLoad > 0) {
    currentReviewer.currentLoad--;
  }

  // Assign to new reviewer
  const newReviewerIdFinal = newReviewerId || pickReviewer(review.priority, review.reasons);
  review.assignedTo = newReviewerIdFinal;

  // Update new reviewer load
  const newReviewer = REVIEWERS.find(r => r.id === newReviewerIdFinal);
  if (newReviewer) {
    newReviewer.currentLoad++;
  }

  console.log(
    `[humanReview] reassigned ${reviewId} from ${currentReviewer?.name} to ${newReviewer?.name}`
  );

  return true;
}

/**
 * Escalate a review to higher priority
 */
export async function escalateReview(reviewId: string, reason: string): Promise<boolean> {
  const review = reviewQueue.find(r => r.reviewId === reviewId);
  if (!review) return false;

  const priorityEscalation = {
    low: 'medium',
    medium: 'high',
    high: 'critical',
    critical: 'critical', // Already at highest
  } as const;

  const newPriority = priorityEscalation[review.priority];
  if (newPriority !== review.priority) {
    review.priority = newPriority;
    review.reasons.push(`Escalated: ${reason}`);

    // Reassign to potentially more specialized reviewer
    await reassignReview(reviewId);

    console.log(`[humanReview] escalated ${reviewId} to ${newPriority}: ${reason}`);
  }

  return true;
}

/**
 * Clean up old reviews (for maintenance)
 */
export async function cleanupOldReviews(maxAgeHours: number = 24): Promise<number> {
  const cutoffTime = new Date(Date.now() - maxAgeHours * 60 * 60 * 1000);
  const initialLength = reviewQueue.length;

  // Remove reviews older than cutoff time
  for (let i = reviewQueue.length - 1; i >= 0; i--) {
    if (new Date(reviewQueue[i].createdAt) < cutoffTime) {
      const review = reviewQueue[i];

      // Release reviewer load
      const reviewer = REVIEWERS.find(r => r.id === review.assignedTo);
      if (reviewer && reviewer.currentLoad > 0) {
        reviewer.currentLoad--;
      }

      reviewQueue.splice(i, 1);
    }
  }

  const cleanedCount = initialLength - reviewQueue.length;
  if (cleanedCount > 0) {
    console.log(`[humanReview] cleaned up ${cleanedCount} old reviews`);
  }

  return cleanedCount;
}

/**
 * moderationPersistence.ts
 * Database persistence layer for moderation analysis results
 * Handles storing analysis results, failures, and review items
 */

import { AnalysisType, ReviewPriority, ReviewStatus } from '@prisma/client';

import { db } from '@/lib/db';
import { ModelResult, ConsensusResult } from '@/types';

export interface PersistAnalysisInput {
  type: AnalysisType;
  inputRef: string;
  labels: Array<{ label: string; score: number; region?: unknown }>;
  reasons?: string[];
  provenance: { model: string; version?: string };
  rawOutput?: unknown;
  reviewNeeded: boolean;
}

export interface FlagFailureInput {
  inputRef: string;
  error: string;
  details?: string;
  metadata?: Record<string, any>;
}

/**
 * Persist a successful analysis result to the database
 */
export async function persistAnalysisResult(input: PersistAnalysisInput) {
  try {
    const analysis = await db.moderationAnalysis.create({
      data: {
        inputRef: input.inputRef,
        type: input.type,
        labels: input.labels,
        reasons: input.reasons || [],
        provenance: input.provenance,
        rawOutput: input.rawOutput,
        reviewNeeded: input.reviewNeeded,
      },
    });

    // If review is needed, create a review item
    if (input.reviewNeeded) {
      await createReviewItem(input.inputRef, input.labels, input.reasons || []);
    }

    // Log the analysis event
    await logAuditEvent('analysis_completed', {
      inputRef: input.inputRef,
      type: input.type,
      reviewNeeded: input.reviewNeeded,
      analysisId: analysis.id,
    });

    return analysis;
  } catch (error) {
    console.error('[moderationPersistence] Failed to persist analysis:', error);
    throw new Error(`Failed to persist analysis: ${(error as Error).message}`);
  }
}

/**
 * Flag an analysis as failed and persist the failure for auditing
 */
export async function flagAnalysisAsFailed(inputRef: string, failure: FlagFailureInput) {
  try {
    const failedAnalysis = await db.moderationAnalysis.create({
      data: {
        inputRef,
        type: AnalysisType.CONSENSUS, // Use consensus as the fallback type
        labels: [{ label: 'analysis_failed', score: 1 }],
        reasons: [
          `Analysis failed: ${failure.error}`,
          ...(failure.details ? [failure.details] : []),
        ],
        provenance: { model: 'system', version: '1.0' },
        rawOutput: {
          error: failure.error,
          details: failure.details,
          metadata: failure.metadata,
          timestamp: new Date().toISOString(),
        },
        reviewNeeded: true, // Failed analyses always need review
      },
    });

    // Create a high-priority review item for failures
    await createReviewItem(
      inputRef,
      [{ label: 'analysis_failed', score: 1 }],
      [`Analysis failed: ${failure.error}`, ...(failure.details ? [failure.details] : [])],
      ReviewPriority.HIGH
    );

    // Log the failure event
    await logAuditEvent('analysis_failed', {
      inputRef,
      error: failure.error,
      details: failure.details,
      metadata: failure.metadata,
      analysisId: failedAnalysis.id,
    });

    return failedAnalysis;
  } catch (error) {
    console.error('[moderationPersistence] Failed to flag analysis as failed:', error);
    throw new Error(`Failed to flag analysis failure: ${(error as Error).message}`);
  }
}

/**
 * Create a review item for human moderation
 */
async function createReviewItem(
  imageId: string,
  labels: Array<{ label: string; score: number }>,
  reasons: string[],
  priority: ReviewPriority = ReviewPriority.MEDIUM
) {
  try {
    // Determine priority based on labels and scores
    const calculatedPriority = calculateReviewPriority(labels, reasons);

    const reviewItem = await db.reviewItem.create({
      data: {
        imageId,
        priority: calculatedPriority,
        reasons,
        metadata: {
          labels,
          severity: calculateSeverity(labels),
          triggeredAt: new Date().toISOString(),
        },
      },
    });

    // Log review item creation
    await logAuditEvent('review_item_created', {
      imageId,
      reviewItemId: reviewItem.id,
      priority: calculatedPriority,
      reasons,
    });

    return reviewItem;
  } catch (error) {
    console.error('[moderationPersistence] Failed to create review item:', error);
    throw new Error(`Failed to create review item: ${(error as Error).message}`);
  }
}

/**
 * Calculate review priority based on labels and scores
 */
function calculateReviewPriority(
  labels: Array<{ label: string; score: number }>,
  reasons: string[]
): ReviewPriority {
  // Check for critical labels
  const criticalLabels = ['child_exposed', 'sexual_nudity', 'deepfake_suspected', 'violence'];
  const hasCriticalLabel = labels.some(l => criticalLabels.includes(l.label) && l.score >= 0.6);

  if (hasCriticalLabel) {
    return ReviewPriority.CRITICAL;
  }

  // Check for high-confidence sensitive content
  const sensitiveLabels = ['suggestive', 'partial_nudity', 'hate_symbols'];
  const hasHighConfidenceSensitive = labels.some(
    l => sensitiveLabels.includes(l.label) && l.score >= 0.8
  );

  if (hasHighConfidenceSensitive) {
    return ReviewPriority.HIGH;
  }

  // Check for analysis failures
  const hasFailure = reasons.some(r => r.includes('failed') || r.includes('error'));
  if (hasFailure) {
    return ReviewPriority.HIGH;
  }

  // Default to medium priority for review-needed items
  return ReviewPriority.MEDIUM;
}

/**
 * Calculate severity score for review metadata
 */
function calculateSeverity(labels: Array<{ label: string; score: number }>): number {
  const severityWeights: Record<string, number> = {
    child_exposed: 1,
    sexual_nudity: 0.9,
    deepfake_suspected: 0.85,
    violence: 0.8,
    hate_symbols: 0.75,
    suggestive: 0.6,
    partial_nudity: 0.5,
    analysis_failed: 0.7,
  };

  let maxSeverity = 0;
  for (const label of labels) {
    const weight = severityWeights[label.label] || 0.3;
    const severity = label.score * weight;
    maxSeverity = Math.max(maxSeverity, severity);
  }

  return Math.round(maxSeverity * 100) / 100;
}

/**
 * Log an audit event for tracking and compliance
 */
async function logAuditEvent(event: string, payload: unknown, actor?: string) {
  try {
    await db.auditLog.create({
      data: {
        event,
        actor: actor || 'system',
        payload,
      },
    });
  } catch (error) {
    console.error('[moderationPersistence] Failed to log audit event:', error);
    // Don't throw here - audit logging failures shouldn't break the main flow
  }
}

/**
 * Get analysis history for a specific input reference
 */
export async function getAnalysisHistory(inputRef: string) {
  try {
    const analyses = await db.moderationAnalysis.findMany({
      where: { inputRef },
      orderBy: { createdAt: 'desc' },
    });

    const reviewItems = await db.reviewItem.findMany({
      where: { imageId: inputRef },
      orderBy: { createdAt: 'desc' },
    });

    return {
      analyses,
      reviewItems,
    };
  } catch (error) {
    console.error('[moderationPersistence] Failed to get analysis history:', error);
    throw new Error(`Failed to get analysis history: ${(error as Error).message}`);
  }
}

/**
 * Get pending review items with optional filtering
 */
export async function getPendingReviewItems(
  options: {
    priority?: ReviewPriority;
    assignedTo?: string;
    limit?: number;
    offset?: number;
  } = {}
) {
  try {
    const { priority, assignedTo, limit = 50, offset = 0 } = options;

    const where: unknown = { status: ReviewStatus.PENDING };
    if (priority) where.priority = priority;
    if (assignedTo) where.assignedTo = assignedTo;

    const items = await db.reviewItem.findMany({
      where,
      orderBy: [{ priority: 'desc' }, { createdAt: 'asc' }],
      take: limit,
      skip: offset,
    });

    return items;
  } catch (error) {
    console.error('[moderationPersistence] Failed to get pending review items:', error);
    throw new Error(`Failed to get pending review items: ${(error as Error).message}`);
  }
}

/**
 * Update a review item status and decision
 */
export async function updateReviewItem(
  reviewId: string,
  updates: {
    status?: ReviewStatus;
    assignedTo?: string;
    reviewDecision?: string;
    reviewNotes?: string;
    completedAt?: Date;
  },
  actor?: string
) {
  try {
    const updatedItem = await db.reviewItem.update({
      where: { id: reviewId },
      data: updates,
    });

    // Update the corresponding analysis if a decision was made
    if (updates.reviewDecision) {
      const analysis = await db.moderationAnalysis.findFirst({
        where: { inputRef: updatedItem.imageId },
        orderBy: { createdAt: 'desc' },
      });

      if (analysis) {
        await db.moderationAnalysis.update({
          where: { id: analysis.id },
          data: {
            reviewedAt: new Date(),
            reviewedBy: actor,
            reviewDecision: updates.reviewDecision,
            reviewNotes: updates.reviewNotes,
          },
        });
      }
    }

    // Log the review update
    await logAuditEvent('review_item_updated', {
      reviewId,
      updates,
      actor,
    });

    return updatedItem;
  } catch (error) {
    console.error('[moderationPersistence] Failed to update review item:', error);
    throw new Error(`Failed to update review item: ${(error as Error).message}`);
  }
}

