/**
 * zaiIntegration.ts
 * End-to-end orchestration:
 *  - calls GLM-4.5V (vision), GLM-4.5-AIR (advanced reasoning), and GLM-4.5 (text)
 *  - validates outputs via AJV
 *  - persists raw AI outputs to SQLite via Prisma
 *  - computes a sophisticated consensus and persists it
 *  - marks reviewNeeded when thresholds/critical labels found
 *  - handles failures gracefully with proper auditing
 *
 * Usage:
 *   const integr = new ZaiIntegration({ zaiApiKey: process.env.ZAI_API_KEY });
 *   const result = await integr.analyzeAndPersistImage(imageId, imageBuffer, uploadMetadata);
 */

import { AnalysisType } from '@prisma/client';
import pLimit from 'p-limit';

import { ZaiClient } from './zaiClient';
import { zaiVisionAnalyze } from './zaiVision';
import { zaiTextReasoning } from './zaiText';
import { zaiAirAnalyze } from './zaiAir';
import { assertValidModeration } from './validators';
import { persistAnalysisResult, flagAnalysisAsFailed } from './moderationPersistence';
import { computeConsensus } from './imageAnalysis';
import { AdaptiveConsensusEngine } from './adaptiveConsensus';

const DEFAULT_TIMEOUT_MS = Number(process.env.ZAI_CALL_TIMEOUT_MS || 30000);

export interface ZaiIntegrationConfig {
  zaiApiKey: string;
  visionModel?: string;
  textModel?: string;
  airModel?: string;
  maxConcurrency?: number;
  enableAIR?: boolean;
  enableAdaptiveConsensus?: boolean;
  adaptiveConfig?: unknown;
}

export class ZaiIntegration {
  client: ZaiClient;
  visionModel: string;
  textModel: string;
  airModel: string;
  enableAIR: boolean;
  enableAdaptiveConsensus: boolean;
  adaptiveEngine: AdaptiveConsensusEngine;
  limit: ReturnType<typeof pLimit>;

  constructor(cfg: Partial<ZaiIntegrationConfig>) {
    if (!cfg.zaiApiKey) {
      throw new Error('ZAI API key required for ZaiIntegration');
    }

    this.client = new ZaiClient({
      apiKey: cfg.zaiApiKey,
      apiUrl: process.env.ZAI_API_URL,
    });

    this.visionModel = cfg.visionModel || process.env.ZAI_VISION_MODEL || 'GLM-4.5V';
    this.textModel = cfg.textModel || process.env.ZAI_TEXT_MODEL || 'GLM-4.5';
    this.airModel = cfg.airModel || process.env.ZAI_AIR_MODEL || 'GLM-4.5-AIR';
    this.enableAIR = cfg.enableAIR ?? true;
    this.enableAdaptiveConsensus = cfg.enableAdaptiveConsensus ?? true;
    this.limit = pLimit(cfg.maxConcurrency ?? 2);

    // Initialize adaptive consensus engine
    this.adaptiveEngine = new AdaptiveConsensusEngine(cfg.adaptiveConfig);
  }

  /**
   * Primary method: analyze image buffer and persist all artifacts.
   * Returns a summary that includes persisted DB records (IDs) or failure info.
   */
  async analyzeAndPersistImage(
    imageId: string,
    imageBuffer: Buffer,
    uploadContext: Record<string, any>
  ) {
    console.log(`[zaiIntegration] Starting analysis for image ${imageId}`);

    // Run vision, AIR (if enabled), and text in parallel with limited concurrency
    const tasks = [this.limit(() => this.runVision(imageId, imageBuffer, uploadContext))];

    if (this.enableAIR) {
      tasks.push(this.limit(() => this.runAIR(imageId, imageBuffer, uploadContext)));
    }

    tasks.push(this.limit(() => this.runText(imageId, imageBuffer, uploadContext)));

    const results = await Promise.allSettled(tasks);
    const [visionResult, airResult, textResult] = results.map(r =>
      r.status === 'fulfilled' ? r.value : null
    );

    // If all models failed, mark overall failure
    const successfulResults = [visionResult, airResult, textResult].filter(Boolean);
    if (successfulResults.length === 0) {
      await flagAnalysisAsFailed(imageId, {
        error: 'all_models_failed',
        details: 'Vision, AIR, and Text analysis all failed',
      });
      return {
        success: false,
        reason: 'all_models_failed',
        error: 'All analysis models failed',
      };
    }

    // Compute consensus using the sophisticated consensus calculator
    let consensus;
    try {
      const modelResults = [];
      if (visionResult?.raw) modelResults.push(visionResult.rawModelMapped);
      if (airResult?.raw) modelResults.push(airResult.rawModelMapped);
      if (textResult?.raw) modelResults.push(textResult.rawModelMapped);

      // Use adaptive consensus if enabled, otherwise fall back to original
      if (this.enableAdaptiveConsensus && modelResults.length > 0) {
        consensus = this.adaptiveEngine.computeAdaptiveConsensus(modelResults);
      } else {
        consensus = computeConsensus(modelResults);
      }
    } catch (err) {
      console.warn('[zaiIntegration] consensus computation failed', (err as Error).message);
      consensus = {
        topLabel: 'unknown',
        score: 0,
        spread: 0,
        allLabels: [],
        provenance: { models: [], timestamp: new Date().toISOString() },
        recommendedAction: 'monitor',
        reasons: ['consensus_failure'],
      };
    }

    // Determine whether human review is needed per policy
    const reviewNeeded = this.determineReviewNeed(consensus, successfulResults.length);

    // Persist consensus as an analysis record
    try {
      const persisted = await persistAnalysisResult({
        type: AnalysisType.CONSENSUS,
        inputRef: imageId,
        labels: consensus.allLabels || [{ label: consensus.topLabel, score: consensus.score }],
        reasons: consensus.reasons || [],
        provenance: consensus.provenance || {
          model: 'consensus',
          version: '1',
        },
        rawOutput: consensus,
        reviewNeeded,
      });

      console.log(`[zaiIntegration] Analysis completed for ${imageId}`, {
        success: true,
        consensus: {
          topLabel: consensus.topLabel,
          score: consensus.score,
          action: consensus.recommendedAction,
        },
        reviewNeeded,
        persistedId: persisted.id,
      });

      return {
        success: true,
        persistedId: persisted.id,
        consensus,
        vision: visionResult?.persisted,
        air: airResult?.persisted,
        text: textResult?.persisted,
      };
    } catch (err) {
      console.error('[zaiIntegration] failed to persist consensus', (err as Error).message);
      await flagAnalysisAsFailed(imageId, {
        error: 'consensus_persist_failed',
        details: (err as Error).message,
      });
      return {
        success: false,
        reason: 'consensus_persist_failed',
        error: (err as Error).message,
      };
    }
  }

  // ------- Private helpers -------

  private async runVision(imageId: string, buffer: Buffer, ctx: Record<string, any>) {
    try {
      console.log(`[zaiIntegration][vision] Starting vision analysis for ${imageId}`);

      const modelResult = await zaiVisionAnalyze(this.client, buffer, {
        model: this.visionModel,
        temperature: 0,
        max_tokens: 1200,
        includeSaliencyBase64: false,
        allowLenientParse: false,
      });

      // Validate the result
      assertValidModeration(modelResult.rawOutput);

      // Persist raw vision analysis
      const persisted = await persistAnalysisResult({
        type: AnalysisType.VISION,
        inputRef: imageId,
        labels: modelResult.labels,
        reasons: (modelResult.rawOutput && modelResult.rawOutput.reasons) || [],
        provenance: {
          model: modelResult.modelName,
          version: modelResult.modelVersion,
        },
        rawOutput: modelResult.rawOutput,
        reviewNeeded: false, // Will be set by consensus later
      });

      console.log(`[zaiIntegration][vision] Completed for ${imageId}`);

      return {
        raw: modelResult.rawOutput,
        rawModelMapped: modelResult,
        persisted,
      };
    } catch (err) {
      console.error(`[zaiIntegration][vision] imageId=${imageId} failed:`, (err as Error).message);

      // Persist failure record
      try {
        await flagAnalysisAsFailed(imageId, {
          error: 'vision_failed',
          details: (err as Error).message,
        });
      } catch (e) {
        console.warn('[zaiIntegration] failed to flag vision failure', (e as Error).message);
      }

      return null;
    }
  }

  private async runAIR(imageId: string, buffer: Buffer, ctx: Record<string, any>) {
    if (!this.enableAIR) return null;

    try {
      console.log(`[zaiIntegration][air] Starting AIR analysis for ${imageId}`);

      const modelResult = await zaiAirAnalyze(
        this.client,
        buffer,
        {
          filename: ctx.filename || 'unknown',
          contentType: ctx.contentType || 'image/jpeg',
          size: buffer.length,
          uploaderId: ctx.uploaderId,
          metadata: ctx.metadata || {},
        },
        {
          model: this.airModel,
          temperature: 0,
          max_tokens: 1500,
          enableMultiStepReasoning: true,
          includeRelationshipAnalysis: true,
          analysisDepth: 'detailed',
          allowLenientParse: false,
        }
      );

      // Validate the result
      assertValidModeration(modelResult.rawOutput);

      // Persist raw AIR analysis
      const persisted = await persistAnalysisResult({
        type: AnalysisType.AIR,
        inputRef: imageId,
        labels: modelResult.labels,
        reasons: (modelResult.rawOutput && modelResult.rawOutput.reasons) || [],
        provenance: {
          model: modelResult.modelName,
          version: modelResult.modelVersion,
        },
        rawOutput: modelResult.rawOutput,
        reviewNeeded: false,
      });

      console.log(`[zaiIntegration][air] Completed for ${imageId}`);

      return {
        raw: modelResult.rawOutput,
        rawModelMapped: modelResult,
        persisted,
      };
    } catch (err) {
      console.error(`[zaiIntegration][air] imageId=${imageId} failed:`, (err as Error).message);

      // Persist failure record
      try {
        await flagAnalysisAsFailed(imageId, {
          error: 'air_failed',
          details: (err as Error).message,
        });
      } catch (e) {
        console.warn('[zaiIntegration] failed to flag AIR failure', (e as Error).message);
      }

      return null;
    }
  }

  private async runText(imageId: string, buffer: Buffer, ctx: Record<string, any>) {
    try {
      console.log(`[zaiIntegration][text] Starting text reasoning for ${imageId}`);

      // Construct context for text reasoning - include vision and AIR hints if available
      const ctxDesc = JSON.stringify({
        filename: ctx.filename || 'unknown',
        metadata: ctx.metadata || {},
        contentType: ctx.contentType,
        size: buffer.length,
        uploaderId: ctx.uploaderId,
        hint: 'Use vision descriptors and metadata to reason about content categories (sexual_nudity, child_exposed, deepfake, etc.)',
        analysisContext:
          'This is part of a multi-modal analysis pipeline. Consider visual context and advanced reasoning.',
      });

      const modelResult = await zaiTextReasoning(this.client, ctxDesc, {
        model: this.textModel,
        temperature: 0,
        max_tokens: 800,
        allowLenientParse: false,
      });

      // Validate the result
      assertValidModeration(modelResult.rawOutput);

      const persisted = await persistAnalysisResult({
        type: AnalysisType.TEXT,
        inputRef: imageId,
        labels: modelResult.labels,
        reasons: (modelResult.rawOutput && modelResult.rawOutput.reasons) || [],
        provenance: {
          model: modelResult.modelName,
          version: modelResult.modelVersion,
        },
        rawOutput: modelResult.rawOutput,
        reviewNeeded: false,
      });

      console.log(`[zaiIntegration][text] Completed for ${imageId}`);

      return {
        raw: modelResult.rawOutput,
        rawModelMapped: modelResult,
        persisted,
      };
    } catch (err) {
      console.error(`[zaiIntegration][text] imageId=${imageId} failed:`, (err as Error).message);

      // Persist failure record
      try {
        await flagAnalysisAsFailed(imageId, {
          error: 'text_failed',
          details: (err as Error).message,
        });
      } catch (e) {
        console.warn('[zaiIntegration] failed to flag text failure', (e as Error).message);
      }

      return null;
    }
  }

  /**
   * Determine if human review is needed based on consensus results
   */
  private determineReviewNeed(consensus: unknown, successfulModelsCount: number): boolean {
    if (!consensus || !consensus.allLabels) return true;

    // Critical labels that always require review
    const criticalLabels = ['child_exposed', 'deepfake_suspected', 'sexual_nudity', 'violence'];
    for (const l of consensus.allLabels) {
      if (criticalLabels.includes(l.label) && l.score >= 0.6) return true;
    }

    // Special rule: child detection + any sexual content = immediate review
    const hasChildDetection = consensus.allLabels.some(
      l => l.label === 'child_detected' && l.score >= 0.6
    );
    const hasSexualContent = consensus.allLabels.some(
      l => ['sexual_nudity', 'partial_nudity', 'suggestive'].includes(l.label) && l.score >= 0.1
    );

    if (hasChildDetection && hasSexualContent) return true;

    // Review if there's high model disagreement (spread)
    if (consensus.spread && consensus.spread > 0.25) return true;

    // Review if confidence is low but there are detections
    if (
      consensus.score < 0.6 &&
      consensus.allLabels.length > 0 &&
      consensus.allLabels[0].score > 0.3
    )
      return true;

    // Review if recommended action is quarantine or hold_for_review
    if (['quarantine', 'hold_for_review'].includes(consensus.recommendedAction)) return true;

    // Review if fewer than 2 models succeeded (need consensus)
    if (successfulModelsCount < 2) return true;

    return false;
  }

  /**
   * Get current model performance metrics from adaptive consensus engine
   */
  getModelPerformance() {
    if (this.enableAdaptiveConsensus) {
      return this.adaptiveEngine.getModelPerformance();
    }
    return [];
  }

  /**
   * Get current adaptive weights from consensus engine
   */
  getCurrentWeights() {
    if (this.enableAdaptiveConsensus) {
      return this.adaptiveEngine.getCurrentWeights();
    }
    return {
      'GLM-4.5V': 0.4,
      'GLM-4.5': 0.3,
      'GLM-4.5-AIR': 0.3,
    };
  }

  /**
   * Provide feedback to improve adaptive consensus (ground truth based)
   */
  provideFeedback(imageId: string, groundTruth: string, correctAction: string) {
    if (this.enableAdaptiveConsensus) {
      this.adaptiveEngine.provideFeedback(imageId, groundTruth, correctAction);
    }
  }

  /**
   * Reset adaptive learning
   */
  resetAdaptiveLearning() {
    if (this.enableAdaptiveConsensus) {
      this.adaptiveEngine.resetLearning();
    }
  }
}

// src/services/zaiText.ts
/**
 * zaiText.ts
 * Wrapper for GLM-4.5 text reasoning / agent orchestration.
 * Designed to accept a visual description or metadata and return structured JSON
 * with labels + reasoning and confidence. Deterministic (temperature 0).
 *
 * Returns a ModelResult compatible with ModelResult type.
 */
import Ajv from 'ajv';
import addFormats from 'ajv-formats';

import { ModelResult, ModelLabel } from '../types/index';

import { ZaiClient } from './zaiClient';

addFormats(Ajv);

const ajv = new Ajv({ strict: false });

export interface TextOptions {
  model?: string;
  temperature?: number;
  max_tokens?: number;
  allowLenientParse?: boolean;
}

/** JSON schema for text reasoning output */
const textSchema = {
  type: 'object',
  properties: {
    labels: {
      type: 'array',
      items: {
        type: 'object',
        properties: {
          label: { type: 'string' },
          score: { type: 'number' },
        },
        required: ['label', 'score'],
      },
    },
    reasons: { type: 'array', items: { type: 'string' } },
    provenance: {
      type: 'object',
      properties: { model: { type: 'string' }, version: { type: 'string' } },
      required: ['model'],
    },
  },
  required: ['labels', 'provenance'],
};

const validateText = ajv.compile(textSchema);

function buildTextPrompt(contextDescription: string) {
  const system = `
SYSTEM:
You are a content policy reasoning agent. You receive visual descriptors, OCR, EXIF, and metadata.
Produce ONLY valid JSON that matches the schema:
{ "labels": [{"label":"string","score":float}], "reasons":[ "short reason strings" ], "provenance":{ "model":"GLM-4.5", "version":"..." } }
Do not provide step-by-step internal reasoning or chain-of-thought. Output only JSON.
`;

  const user = `
USER:
Context: ${contextDescription}
Return the JSON as specified. Prioritize accuracy; use score 0.0-1.0.
`;

  return `${system}\n\n${user}`;
}

export async function zaiTextReasoning(
  client: ZaiClient,
  contextDescription: string,
  opts?: TextOptions
): Promise<ModelResult> {
  const options: TextOptions = {
    model: process.env.ZAI_TEXT_MODEL || 'GLM-4.5',
    temperature: 0,
    max_tokens: 800,
    allowLenientParse: false,
    ...opts,
  };

  const prompt = buildTextPrompt(contextDescription);

  const payload = {
    model: options.model,
    inputs: [
      {
        modality: 'text',
        content: prompt,
      },
    ],
    parameters: {
      temperature: options.temperature,
      max_tokens: options.max_tokens,
      output_format: 'json',
    },
  };

  const resp = await client.callZai(payload, { retries: 2 });

  const rawText =
    resp?.outputs?.[0]?.content ??
    resp?.result ??
    resp?.choices?.[0]?.message?.content ??
    JSON.stringify(resp);

  // parse and validate
  let parsed: unknown;
  try {
    parsed = JSON.parse(rawText);
  } catch {
    // attempt to extract JSON block
    const m = rawText.match(/\{[\s\S]*\}/);
    if (!m) {
      if (options.allowLenientParse) {
        parsed = {
          labels: [],
          reasons: [],
          provenance: { model: options.model },
        };
      } else {
        throw new Error('[zaiTextReasoning] Could not parse model output to JSON');
      }
    } else {
      parsed = JSON.parse(m[0]);
    }
  }

  const valid = validateText(parsed);
  if (!valid) {
    if (!options.allowLenientParse) {
      throw new Error(
        `[zaiTextReasoning] Schema validation failed: ${JSON.stringify(validateText.errors)}`
      );
    } else {
      console.warn('[zaiTextReasoning] schema failed but continue (lenient).', validateText.errors);
    }
  }

  const labels: ModelLabel[] = (parsed.labels || []).map((l: unknown) => ({
    label: String(l.label),
    score: Number(l.score),
  }));

  const result: ModelResult = {
    modelName: options.model!,
    modelVersion: parsed.provenance?.version || undefined,
    labels,
    rawOutput: parsed,
    latencyMs: typeof resp?.latencyMs === 'number' ? resp.latencyMs : undefined,
  };

  return result;
}

// src/services/zaiVision.ts
/**
 * zaiVision.ts
 * Wrapper that calls GLM-4.5V (multimodal vision model) to produce deterministic JSON
 * that matches the ModelResult type in src/types/index.ts.
 *
 * - Uses ZaiClient.callZai
 * - Validates output with AJV
 * - Error handling + retry fallback
 *
 * Example usage:
 *   const client = new ZaiClient({ apiKey: process.env.ZAI_API_KEY });
 *   const result = await zaiVisionAnalyze(client, imageBuffer, { temperature: 0.0 });
 */
import Ajv, { JSONSchemaType } from 'ajv';
import addFormats from 'ajv-formats';

import { ModelResult, ModelLabel } from '../types/index';

import { ZaiClient } from './zaiClient';

addFormats(Ajv);

export interface VisionOptions {
  model?: string; // default GLM-4.5V
  temperature?: number;
  max_tokens?: number;
  // extra flag to include saliency base64 in response; many providers prefer returning as image URL; default false
  includeSaliencyBase64?: boolean;
  // allow returning best-effort even if schema validation fails
  allowLenientParse?: boolean;
}

/** JSON schema we instruct the model to follow exactly */
const visionSchema = {
  type: 'object',
  properties: {
    labels: {
      type: 'array',
      items: {
        type: 'object',
        properties: {
          label: { type: 'string' },
          score: { type: 'number' },
          region: {
            type: 'object',
            nullable: true,
            properties: {
              x: { type: 'number' },
              y: { type: 'number' },
              width: { type: 'number' },
              height: { type: 'number' },
            },
            required: ['x', 'y', 'width', 'height'],
          },
        },
        required: ['label', 'score'],
      },
    },
    saliency_base64: { type: 'string', nullable: true },
    provenance: {
      type: 'object',
      properties: {
        model: { type: 'string' },
        version: { type: 'string' },
      },
      required: ['model'],
    },
  },
  required: ['labels', 'provenance'],
  additionalProperties: true,
} as const;

const ajv = new Ajv({ strict: false });
addFormats(ajv);
const validateVision = ajv.compile(visionSchema);

function safeJsonParse(s: string) {
  try {
    return JSON.parse(s);
  } catch {
    // attempt to extract first {...}
    const m = s.match(/\{[\s\S]*\}/);
    if (!m) throw new Error('Unable to parse JSON from model output');
    return JSON.parse(m[0]);
  }
}

/**
 * Build the deterministic system + user prompt for vision moderation.
 * Note: We instruct the model to *only* output valid JSON identical to the schema.
 */
function buildVisionPrompt(opts: VisionOptions) {
  const system = `
SYSTEM:
You are a vision moderation assistant. DO NOT provide free-form text.
Output MUST be valid JSON ONLY and adhere to the schema described below.
Schema: { "labels":[{"label":"string","score":float,"region":{"x":0..1,"y":0..1,"width":0..1,"height":0..1}}], "saliency_base64":<nullable string>, "provenance":{"model":"GLM-4.5V","version":"..."} }
Labels: include categories such as "sexual_nudity", "partial_nudity", "suggestive", "violence", "self_harm", "hate_symbols", "non-sexual_adult_content", "child_exposed", "deepfake".
Scores must be between 0 and 1 (0.0-1.0). Regions must be normalized (x,y,width,height) relative to image dimensions.
If you cannot detect any label with confidence, return labels: [].
Return BASE64 saliency map only if requested via includeSaliencyBase64.
  `.trim();

  const user = `
USER:
Analyze the attached image and return JSON only. Do not include commentary or chain-of-thought.
Return labels sorted by descending score.
`;

  return { system, user };
}

export async function zaiVisionAnalyze(
  client: ZaiClient,
  imageBuffer: Buffer,
  opts?: VisionOptions
): Promise<ModelResult> {
  const options: VisionOptions = {
    model: process.env.ZAI_VISION_MODEL || 'GLM-4.5V',
    temperature: 0,
    max_tokens: 1200,
    includeSaliencyBase64: false,
    allowLenientParse: false,
    ...opts,
  };

  // encode the image as base64 for the request; if your provider supports multipart or binary, adapt accordingly.
  const imageB64 = imageBuffer.toString('base64');
  const promptParts = buildVisionPrompt(options);

  const payload = {
    model: options.model,
    inputs: [
      {
        modality: 'image',
        content_base64: imageB64,
      },
      {
        modality: 'text',
        content: `${promptParts.system}\n\n${promptParts.user}`,
      },
    ],
    parameters: {
      temperature: options.temperature,
      max_tokens: options.max_tokens,
      // instruct the model to disable verbose content; provider-specific parameter names may vary
      output_format: 'json',
      // We ask the model to respond with the JSON body only
    },
  };

  // call ZAI
  const resp = await client.callZai(payload, { retries: 3 });

  // Model SDKs differ. We assume the API returns an object { outputs: [{ content: "..." }] } or similar.
  const rawText =
    resp?.outputs?.[0]?.content ??
    resp?.result ??
    resp?.choices?.[0]?.message?.content ??
    JSON.stringify(resp); // fallback to raw JSON for debugging

  let parsed: unknown;
  try {
    parsed = safeJsonParse(rawText);
  } catch (err) {
    if (options.allowLenientParse) {
      // best-effort: return empty labels
      parsed = { labels: [], provenance: { model: options.model } };
    } else {
      throw new Error(`[zaiVisionAnalyze] failed to parse model JSON: ${(err as Error).message}`);
    }
  }

  // Validate
  const valid = validateVision(parsed);
  if (!valid) {
    if (options.allowLenientParse) {
      // log and fallback
      console.warn(
        '[zaiVisionAnalyze] schema validation failed - falling back',
        validateVision.errors
      );
    } else {
      throw new Error(
        `[zaiVisionAnalyze] schema validation failed: ${JSON.stringify(validateVision.errors)}`
      );
    }
  }

  // Map to ModelResult type
  const labels: ModelLabel[] = (parsed.labels || []).map((l: unknown) => ({
    label: String(l.label),
    score: Number(l.score),
    region: l.region
      ? {
          x: Number(l.region.x),
          y: Number(l.region.y),
          width: Number(l.region.width),
          height: Number(l.region.height),
        }
      : undefined,
  }));

  const modelResult: ModelResult = {
    modelName: options.model!,
    modelVersion: parsed.provenance?.version || undefined,
    labels,
    rawOutput: parsed,
    latencyMs: typeof resp?.latencyMs === 'number' ? resp.latencyMs : undefined,
    saliencyUrl: undefined, // if parsed.saliency_base64 present, handle storage upload outside this function
  };

  // If we have a base64 saliency map, caller can upload it to storage and set saliencyUrl
  if (parsed.saliency_base64) {
    // TODO: upload parsed.saliency_base64 to object store and set modelResult.saliencyUrl
    modelResult.rawOutput._saliency_b64 = parsed.saliency_base64;
  }

  return modelResult;
}

/**
 * zaiWrapper.ts
 * Real Z.AI SDK integration for GLM-4.5V and GLM-4.5 with deterministic prompts and schema enforcement
 *
 * This service provides:
 * - GLM-4.5V for visual content analysis with structured output
 * - GLM-4.5 for text reasoning and consensus computation
 * - Deterministic prompts with JSON schema enforcement
 * - Error handling and retry logic
 * - Performance monitoring
 */

import ZAI from 'z-ai-web-dev-sdk';

import { ModelResult } from '../types/index';

// Z.AI Client singleton
let zaiClient: ZAI | null = null;

async function getZaiClient(): Promise<ZAI> {
  if (!zaiClient) {
    zaiClient = await ZAI.create();
  }
  return zaiClient;
}

// JSON Schema for GLM-4.5V visual analysis output
const VISION_ANALYSIS_SCHEMA = {
